css:
@namespace url(http://www.mozilla.org/keymaster/gatekeeper/there.is.only.xul);

#navigator-toolbox{
  -moz-box-shadow: none !important;
  border: 0 !important;
}

/* --- TABS --- */

/* --- all tabs and inactive tabs --- */

.tabbrowser-tab {
  margin-top: 2px !important;
  margin-right: 2px !important;
  margin-bottom: 2px !important;

  border: 1px solid #aaa !important;
  border-left: 1px solid #aaa !important;
  border-right: 1px solid #919191 !important;
  border-bottom: 1px solid #B4B4B4 !important;

  border-radius: 2px !important;
  /*border-bottom-left-radius: 0 !important;*/
  /*border-bottom-right-radius: 0 !important;*/

  /*border-bottom: 0 !important;*/
  
  background: -moz-linear-gradient(-90deg,rgba(223,220,217,.4),rgba(232,231,228,.5),rgba(255,255,255,.5)) !important;

  color: #eee !important;
  text-shadow: 0.05em 0.12em .07em #000 !important;

  padding: 2px 4px !important;
}

.tabbrowser-tab:hover, .tabbrowser-tab[selected="true"]:hover{
  /*background: -moz-linear-gradient(-90deg,#E5E4E3,#FFFFFF) !important;*/
  background: -moz-linear-gradient(-90deg, #1F8FD4,#2BCFE8) !important;
  border: 1px solid #1971A7 !important;
  border-top: 1px solid #42B9F2 !important;
  border-left: 1px solid #42B9F2 !important;
  color: #DCEFF1 !important;
  text-shadow: 0.05em 0.12em .07em #000 !important;
}

.tabbrowser-tab[selected="true"] {
  background: -moz-linear-gradient(-90deg,#D8ECC7,#B6E291) !important;
  border: 1px solid #ADCB93 !important;
  border-top: 1px solid #E0F9B7 !important;
  border-left: 1px solid #D1F98F !important;
  color: #fff !important;
  text-shadow: 0.05em 0.12em .07em #1F5B0F !important;
}

.tabbrowser-tab[pinned]{
  /*
  border: 1px solid white !important;
  border-left: 1px solid #F4F4F4 !important;
  border-right: 1px solid #919191 !important;
  border-bottom: 1px solid #B4B4B4 !important;
  */
  
  border: none !important;
  
  /*border-radius: 2px !important;*/
  
  /*
  background: -moz-linear-gradient(90deg,rgba(223,220,217,.25),rgba(232,231,228,.25),rgba(255,255,255,.25)) !important;
  */

  background: transparent !important;

  padding: 1px 0px 0px 0px !important;
  min-width: 16px !important;
  margin-top: 3px !important;
  min-height: 17px !important;

  text-shadow: none !important;

}

.tabbrowser-tab[pinned]:hover, .tabbrowser-tab[pinned][selected="true"]:hover {
  border: 1px solid #aaa !important;
  border-left: 1px solid #aaa !important;
  border-right: 1px solid #919191 !important;
  border-bottom: 1px solid #B4B4B4 !important;
  border-radius: 2px !important;
  
  background: -moz-linear-gradient(-90deg,rgba(223,220,217,.4),rgba(232,231,228,.5),rgba(255,255,255,.5)) !important;
}

.tabbrowser-tab[pinned][selected="true"] {
  background: -moz-linear-gradient(-90deg,#D8ECC7,#B6E291) !important;
  border: 1px solid #ADCB93 !important;
  border-top: 1px solid #E0F9B7 !important;
  border-left: 1px solid #D1F98F !important;
  color: #628840 !important;
}


/* --- nav bar ---*/
#nav-bar{
  opacity: 1 !important;
  background: -moz-linear-gradient(-90deg,#DFDCD9,#CCC6C2) !important;
  box-shadow: none !important;
  border-top: 1px solid rgba(255,255,255,0) !important;
}

/* --- bookmarks toolbar ---*/
#PersonalToolbar{
  opacity: 1 !important;
  background: #CCC6C2 !important;
  box-shadow: none !important;
  border: 0 !important;
}

/* --- add on bar ---*/
#addon-bar{
  padding: 0 !important;
}

/* --- APPBUTTON --- */

#appmenu-button {
  background: none !important;
  min-width: 54px !important;
  width: 64px !important;
  border: none !important;
  box-shadow: none !important;
}

#main-window[tabsontop="true"][sizemode="normal"] #appmenu-button-container{
  background-clip: padding-box !important;
  border: 1px solid rgba(255,255,255,0.5) !important;
  border-radius: 0 0 2.5px 2.5px !important;
  border-top: none !important;
  margin: 1px 0 10px 5px !important;
  padding: 0 !important;
  background-image: url("chrome://branding/content/icon48.png");
  background-repeat: no-repeat !important;
  height: 22px !important;
  box-shadow: none !important;
}

#main-window[tabsontop="true"][sizemode="normal"] #appmenu-button-container:hover{
  background-image: url("chrome://branding/content/icon48.png"), -moz-linear-gradient(90deg, #1F8FD4,#2BCFE8) !important;
  border: 1px solid #1971A7 !important;
  border-top: none !important;
}

#main-window[tabsontop="true"][sizemode="maximized"] #appmenu-button-container {
  background-clip: padding-box !important;
  /*border: 1px solid white !important;
  border-radius: 2px !important;
  border-left: 1px solid #FFFFFF !important;
  border-right: 1px solid #919191 !important;
  border-bottom: 0 !important;*/
  margin-top: 2px !important;
  margin-bottom: -4px !important;
  padding: 0 !important;
  /*background-image: url("chrome://branding/content/icon48.png"), -moz-linear-gradient(90deg,#DFDCD9,#E8E7E4) !important;*/
  background-image: url("chrome://branding/content/icon48.png")  !important;
  background-repeat: no-repeat !important;
  height: 23px !important;
  box-shadow: none !important;
}

#main-window[tabsontop="true"][sizemode="maximized"] #appmenu-button-container:hover {
  background-image: url("chrome://branding/content/icon48.png"), -moz-linear-gradient(-90deg, #1F8FD4,#2BCFE8) !important;
  border: 1px solid #1971A7 !important;
  border-top: 1px solid #42B9F2 !important;
  border-left: 1px solid #42B9F2 !important;
}

#main-window[privatebrowsingmode][tabsontop="true"][sizemode="maximized"] #appmenu-button-container {
    border: 1px solid rgba(0,0,0,.35) !important;
  border-bottom: none !important;
	background-image: url("chrome://branding/content/icon48.png"), -moz-linear-gradient(-90deg, rgba(106,161,228,1), rgba(68,68,202,1),rgba(68,68,202,1),rgba(68,68,202,1)) !important;
	background-repeat: no-repeat !important;
	background-clip: padding-box !important;
	box-shadow: none !important;
}

#main-window[privatebrowsingmode][tabsontop="true"][sizemode="maximized"] #appmenu-button-container:hover {
	background-image: url("chrome://branding/content/icon48.png"), -moz-linear-gradient(-90deg, rgba(128,154,188,1), rgba(47,0,140,1),rgba(47,0,140,1),rgba(47,0,140,1)) !important;
}

#appmenu-button .button-text {
	display: none !important;
}

#appmenu-button dropmarker {
	margin: 2px 0 0 -1px !important;
}
