css:
/* Part of Firefox CandyBar theme              */
/* By Pullipo - http://pullipo.deviantart.com/ */

@-moz-document url("chrome://browser/content/tabview.html") {

/*#################################################################################*/
/*#################################################################################*/
/*############ Don't edit beyond this part unless you know what to do #############*/
/*#################################################################################*/
/*#################################################################################*/


/* --- Base --- */
.body {font-family: Ubuntu !important}


/* --- Action bar --- */
#actions {
margin: 18px 18px !important;
padding: 5px 0 5px 3px !important;
border-radius: 3px !important;
box-shadow: 0 3px 5px rgba(0, 0, 0, .3) !important;
background-image: -moz-linear-gradient(top, #E6EDF5, #DAE5F0) !important;
-moz-transition: all 250ms ease-out 0ms !important}

#actions:-moz-window-inactive {opacity: 0 !important}

#exit-button {background: none}

/* --- Chromeless exit and search button --- */
/*#actions {box-shadow: none !important; background: none !important}*/

/* --- Search stuff --- */
#searchshade {border-radius: 3px !important; background: rgba(0, 0, 0, .75) !important; z-index: 1000 !important}
#search {margin: 23px 0 0 5px !important; -moz-transition: all 250ms ease-in 0ms !important}
#search:-moz-window-inactive {margin: -5px 0 0 5px !important; -moz-transition: all 250ms ease-out 0ms !important}
#searchbox {
font-size: 14px!important;
text-shadow: 0 1px 0px rgba(255, 255, 255, 1) !important;
box-shadow: 0px 5px 5px rgba(0,0,0,.3)!important;
color: black !important;
background-image: -moz-linear-gradient(top, #E0E9F2, #DAE5F0) !important;
border-radius: 4px !important}

/* --- Etched menu bar --- */
#actions {
z-index: 100 !important;
margin: 0px 1px !important;
box-shadow: none !important;
background: none !important}
#search, #search:-moz-window-inactive {margin: -5px 0 0 5px !important}
#searchbox {background-image: -moz-linear-gradient(top, #E6EDF5, #DAE5F0) !important}

/* --- App tabs --- */
.appTabTrayContainer {
/*display: none !important;*/
margin-right: 7px !important;
border: none !important}
.appTabIcon { padding-bottom: 7px !important;}


/* --- Overlay --- */
.overlay {
background: rgba(0, 0, 0, .75) !important;
border-radius: 3px !important;
box-shadow: 0 3px 5px rgba(0, 0, 0, .5) !important}


/* --- Group box --- */
.groupItem {
border-radius: 3px !important;
box-shadow: 0 -3px 0px rgba(0, 0, 0, .3) inset,
            0 0 1px rgba(0, 0, 0, .5),
            0 3px 5px rgba(0, 0, 0, .3) !important;
background-image: -moz-linear-gradient(top, #E6EDF5, #DAE5F0) !important}


/* --- Group title --- */
input.name {margin-left: 11px !important; font-weight: bold !important; text-shadow: 0 1px 0px rgba(255, 255, 255, 1) !important}
input.name:-moz-placeholder {opacity: 0 !important; -moz-transition: all 250ms ease-in 0ms !important}
.groupItem:hover input.name:-moz-placeholder {opacity: 1 !important; -moz-transition: all 250ms ease-in 0ms !important}


/* --- Tab --- */
.tab.focus{
box-shadow: 0 0 1px rgba(0, 0, 0, .5),
            0 0 5px rgba(0, 0, 0, .3) !important;
border-radius: 3px !important;
background-image: -moz-linear-gradient(top, #E6EDF5, #DAE5F0) !important}


/* --- Item's title --- */
.tab-title {
padding-bottom: 1px !important;
font-size: 9pt !important}


/* --- Close button --- */
.groupItem .close {opacity: 0 !important; -moz-transition: all 250ms ease-in 0ms !important}
.tab .close {opacity: 0 !important; -moz-transition: all 250ms ease-in 0ms !important}
.groupItem:hover .close {opacity: 1 !important}
.tab:hover .close {opacity: 1 !important}


/* --- New tab button --- */
.newTabButton {opacity: 0 !important; -moz-transition: all 250ms ease-in 0ms !important}
.groupItem:hover .newTabButton {opacity: .75 !important}


/* --- Group expander --- */
.stackExpander {
opacity: 0 !important; -moz-transition: all 250ms ease-in 0ms !important}
.groupItem:hover .stackExpander {
opacity: 1 !important}


/* --- Favicon --- */
.favicon {
opacity: 0 !important;
padding: 2px !important;
border-radius: 0 0 3px 0 !important;
-moz-transition: all 250ms ease-in 0ms !important}
.tab:hover .favicon {opacity: 1 !important; -moz-transition: all 250ms ease-out 0ms !important}


/* --- Resizer --- */
.groupItem .resizer {margin: 0 4px 6px 0 !important; opacity: 0 !important; -moz-transition: all 250ms ease-in 0ms !important; }
.groupItem:hover .resizer {opacity: 1 !important; -moz-transition: all 250ms ease-out 0ms !important}


/* --- Undo --- */
.undo {
border-radius: 3px !important;
color: #616364 !important;
border-radius: 4px !important;
box-shadow: 0 -2px 0px rgba(0, 0, 0, .2) inset,
            0 0 1px rgba(0, 0, 0, .1),
            0 2px 4px rgba(0, 0, 0, .3) !important;
background-image: -moz-linear-gradient(top, #E6EDF5, #DAE5F0) !important;
-moz-transition: all 250ms ease-out 0ms !important}

.undo:hover {
color: white !important;
box-shadow: 0 1px 0px rgba(255, 255, 255, .75) !important;
background: rgba(0, 0, 0, .75) !important}

}
