css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("www.facetsmovies.com") {


/* liquify layout */
#body-wrapper {width:auto!important;}
#nav .links {float:none!important; width:auto!important;}
#main-content, .shade {width:100%!important;}
div.shade, div.shade2 {
  background:none!important;
  float:none!important;
  margin:0!important;
}
div.shade2 {padding:0 200px; width:auto!important; float:none!important;}
#left-column {position:absolute; left:10px;}
#right-column {
  position:absolute; 
  right:10px; 
  top:150px;
  padding-bottom:65px;
}

/* center col liquify */
#content {
  padding: 0;
  float:none!important;
}
.red-panel {
  float:none!important;
  width:auto!important;
  clear:none!important;
}
.red-panel .content, .bl, .br, .pc {
  float:none!important;
  width:auto!important;
}

#footer {
  position:fixed; 
  bottom:0; 
  width:100%!important; 
  margin:0!important;
}
#footer div.c {
  float:none!important; 
  height:auto!important; 
  width:auto!important;}

/* remove decorative structure */
div.l, div.r {display:none;}
div.browseAZ {
  background:none!important;
  border:1px solid #ccc;
}
.table-header-bg {
  margin:0!important;
  background:none #ccc!important;
}
div.browseAZ, .table-header-bg
.plain-table, col, .grbroundbox,
tr, td, th, tbody {width:auto!important;}
.table-header-bg2 .plain-table, .plain-table {width:100%!important;}

/* header mods */
#header {margin-bottom:0!important;}
#header .logo img {margin-left:-15px;}
#header .search {background:none!important;}


body, #main-content {background:#FDF4A0!important;}
#footer {background:none #000!important; z-index:9;}

.table-header-bg2,
.table-header-bg,
table[width] {width:100%!important;}

}
