css:
@namespace url(http://www.w3.org/1999/xhtml); @-moz-document url-prefix('http://www.tumblr.com/'), url-prefix('https://www.tumblr.com/') 
 {
body {background: url('http://i798.photobucket.com/albums/yy266/nautiical/tumblr/c657f223.jpg') top left #2B2B2B no-repeat fixed !important;}
#left_column, #right_column {background-color: transparent !important;}

div.l-content.l-content.l-content {background-color:#A7A8AC !important; background: rgba(115,117,119,0.3) !important;}
*{text-shadow:none !important; color:#A5A6A8 !important;}
ol#posts li.notification{background-color: #A7A8AC !important; border-bottom: 2px solid #A7A8AC !important; color: #A7A8AC !important;opacity: .50;}
#right_column a#dashboard_switch_blog_menu_current:hover {background:#FDECF0;opacity: .50;}
#nav .nav_item.active{background-color:#FDECF0}
#left_column #posts .post .permalink{background-color:#A7A8AC;opacity: .50;}
#dashboard_switch_blog_menu_current,
#dashboard_switch_blog_arrow_and_divider,
#dashboard_switch_blog_arrow_current
{background-image:none !important}
.selection_nipple.white, .selection_nipple:not([class="selection_nipple white"]) { 
	background: none !important;
	width: 0px !important;
	height: 0px !important;
	margin-top: 0px !important;
	border-bottom: solid 11px white !important;
	border-right: 11px solid transparent !important;
	border-left: 11px solid transparent !important; }
.selection_nipple:not([class="selection_nipple white"]) {
	border-bottom: solid 11px #404141 !important; opacity: .5;}
.notification, .avatar_frame, .nipple{border:0px !important}
.notification_type_icon{display:none !important}
.selected .following,
.selected .followers,
.selected .settings,
.selected .posts,
.selected .messages,
.selected .drafts,
.selected .queue, 
.selected .likes {
	color: #A7A8AC !important; 
	background: #2B2B2B !important;}
a.explore_more_tags,
a.add_and_remove,
a.customize,
a.mass_editor
	{background-color:#2B2B2B !important;  
	background-image:none !important;
	border:0px !important}
div.gradient,
div.dashboard_nav_item, 
a.permalink
	{background-color:#2B2B2B !important;
	opacity:.9; 
	background-image:none !important;
	border:0px !important}
.content.is_media, 
.mask.top, 
.mask.bottom {
	background: none !important; }
.post.photo_post .photo, 
.post.photo_post .video_thumbnail {
	border-radius: 16px !important;}
.post.photo_post .photo, 
.post.photo_post .video_thumbnail 
	{-moz-box-shadow: 1px 1px 3px #000000 !important;}
#auto_pagination_loader
	{background: #A7A8AC !important;}
div.return_to_top_icon{	background: none !important;
	width: 0px !important;
	height: 0px !important;
	margin-top: 0px !important;
	border-bottom: solid 30px white !important;
	border-right: 15px solid transparent !important;
	border-left: 15px solid transparent !important;}
#left_column #posts .notification {
background-color:#A7A8AC !important;
color:#666 !important;
border-bottom-color: #eee !important}
#left_column #posts .post .post_avatar{-moz-border-radius: 38px !important; -webkit-border-radius: 60px !important; width: 64px !important; height: 64px !important; margin: 0px !important;}
#left_column #posts .post .sub_avatar_inner {
-moz-border-radius: 38px !important; -webkit-border-radius: 60px !important; width: 32px !important; height: 32px !important; margin: 0px !important;}
#new_post_notice_container{
color:#fff !important}
#new_post_notice_container a{
color:#fff !important}
.new_post_label_icon{background-image:none !important;}
#new_post{background: url('http://i798.photobucket.com/albums/yy266/nautiical/tumblr/e03d284f.png') center no-repeat; background-color:none !important;}
img[src*="http://assets.tumblr.com/images/new_post_icons_no_labels.png"]{
    width:0;
    height:0px;
    opacity: 0;
    display: block;}
.new_post_label{
    font-size:0px !important;
    padding-top:0px !important;
    opacity: 0;}
.permalink {background: none !important;
width: 16px !important;
height: 16px !important;
  background: #FDECF0) !important;
  border-bottom-left-radius: 10px !important;
  border-top-right-radius: 10px !important;
  margin-top: 3px !important;
  margin-right: 4px !important;}
#logo {height: 0 !important;
width: 0 !important;
padding-left: 207px !important;
padding-top: 43px !important;
background: url(http://i53.tinypic.com/219nrlt.gif) no-repeat !important;}
.sub_avatar{width:32px !important; height:32px !important; background-color:transparent !important}}
