css:
@namespace html url(http://www.w3.org/1999/xhtml); @-moz-document domain("ne2.ch"),domain("2ch.so"),domain("2-ch.ru") {

html, body {

background-color: #333;
color: #ccc;

}


h1{
font-family: 'Myriad Pro Light', Arial, sans-serif; 

font-weight: normal;
font-size:11px;
}

dl.menu dt {
background: #222;
border: solid 1px #000;
border-radius: 5px;
-moz-border-radius: 5px;
-webkit-border-radius: 5px;
margin-top: 1em;
padding-left: .5em;
cursor: pointer;
border-top-left-radius: 5px 5px;
border-top-right-radius: 5px 5px;
border-bottom-right-radius: 5px 5px;
border-bottom-left-radius: 5px 5px;
}
a {
color: #E14B4B !important;
}
span a {
color: #E14B4B !important;
}
.rmenu a {
white-space: nowrap;
color: #E14B4B  !important;
font-size: 14px !important;
font-family: sanf !important;
}
.rmenu a:hover {
white-space: nowrap;
color: #CCC  !important;
font-size: 12px;
}
a:hover, a:focus {
color: #888 !important;
}
.reply {

background: #292929;
border: solid 1px #222;
padding: 0;
margin: 0;
border-radius: 5px;
-moz-border-radius: 5px;
-webkit-border-radius: 5px;
border-top-left-radius: 5px 5px;
border-top-right-radius: 5px 5px;
border-bottom-right-radius: 5px 5px;
border-bottom-left-radius: 5px 5px;
min-width: 500px;
min-weight: 500px;
}
input[type="checkbox"] {
display:none;
margin: 3px 3px 3px 4px;
}
.filetitle {
font-size: 14px;
color: #777;
font-weight: normal;
}

element.style {
display: none;
width: 16px;
height: 16px;
margin: 0 7px 0 0;
background: #000;
}
.logo {
display:none
}
input, textarea, select {
background-color: #222;
border: 1px solid #444;
color: #ccc;
border-radius: 5px;
}
.theader, .passvalid {
background: #222;
text-align: center;
padding: 2px;
color: #555;
clear: both;
font-weight: normal;
margin-bottom: .5em;
border: solid 1px #000;
border-radius: 5px;
-moz-border-radius: 5px;
-webkit-border-radius: 5px;
border-top-left-radius: 5px 5px;
border-top-right-radius: 5px 5px;
border-bottom-right-radius: 5px 5px;
border-bottom-left-radius: 5px 5px;
}
.postblock {

}


#trgetback {
display: none;
}
.rules {
display: none;
}
.reflink div {
display: none;
}

.quote {
background: #212121;
border: solid 1px #CCC;
padding: 5px;
margin: 4px;
border-radius: 5px;
-moz-border-radius: 5px;
-webkit-border-radius: 5px;
border-radius: 5px 5px;
border-top-left-radius: 5px 5px;
border-top-right-radius: 5px 5px;
border-bottom-right-radius: 5px 5px;
border-bottom-left-radius: 5px 5px;
}

#postform table tbody tr td {
  color: #333
}

hr{
width: 100%;
border: 1px solid #343434;
}

.postername{
display: none;
}

label {
  font-size: 11px;
}

.thumbnailmsg {
display: none;
}

img[alt="increase"] { 
display: none; 
}

.filezise a {
  color: #666!important;
}

span a {
  color: #ccc !important;
}

.adminbar {

  font-size: 1.3em;
  font-family: Rockwell ;
}


.unkfunc {
  color: #F07074 ;
}

input[type="submit"] {
  padding: 3px 6px 3px 6px;
  color: #666 !important;
  float: right;
}

hr {
  margin: 0 !important;
}

input[type="password"] { 
#display: none;
  margin: 0 !important;
  width: 0px !important;
  height: 0px !important;
}

input[size="35"] { 
#display: block important!;
}
embed[src="http://www.youtube.com/v/0HVTa7lMrXc"] {
display: block important!;
}


pre code, pre .ruby .subst, pre .tag .title, pre .lisp .title {
color: #e4e4e4;
font-size: 16px;
}

center {
display: none;
}

.replytitle {
color: #555;
font-weight: normal;
}

}
