@namespace url(http://www.mozilla.org/keymaster/gatekeeper/there.is.only.xul);
/* ==UserStyle==
// @name         Effect for window-notifications
// @author       Roblesolido
// @description	 Firefox Browser Message.
// ==/UserStyle==*/

notification[type="info"] {
background-color: -moz-field !important;
}
notification .notification-inner {
padding: 1px 3px !important;
border: none !important;
border-bottom: 1px solid Threedshadow !important;
}

notification .messageCloseButton
{
-moz-user-focus: ignore !important;
-moz-margin-start: 2em !important;
list-style-image: url(data:image/png;base64,)!important;
-moz-image-region: rect(0px, 14px, 14px, 0px) !important;
}
notification .messageCloseButton:hover
{
-moz-image-region: rect(0px, 28px, 14px, 14px) !important;
}
notification .messageCloseButton:hover:active
{
-moz-image-region: rect(0px, 42px, 14px, 28px) !important;
}
/* ----- */
