css:
/* Name: Clockwork AHK forum theme */

/*  
  Clockwork AHK forum theme
  Ported and Modified by budRich
  version 1 (Fri November 15, 2013)
*/

/*  
  Zenburn AHK forum theme
  Ported and Modified by tidbit
  version 3 (Sat September 17, 2011)
*/

/*
  The original sunbbSilver Theme for phpBB version 2+
  Created by subBlue design
  http://www.subBlue.com
*/


/* General page style. The scroll bar colours only visible in IE5.5+  dddd*/

@namespace url(http://www.w3.org/1999/xhtml);
@charset "utf-8";
@-moz-document
url-prefix("http://ahkscript.org/boards/") 
{

/*
===========================
THE BELOW STYLE IS FOR THE FORUM.
SCROLL DOWN TO FOR THE DOCUMENTATION AND SITE.
===========================
*/


/* General page style. The scroll bar colours only visible in IE5.5+ */

body
{
   background-color: #1D1D1D !important;
   color: #EDEDE4 !important;
   scrollbar-face-color: #1D1D1D !important;
   scrollbar-highlight-color: #FFFFFF !important;
   scrollbar-shadow-color: #1D1D1D !important;
   scrollbar-arrow-color: #F30342 !important;
   scrollbar-track-color: #EFEFEF !important;
   scrollbar-darkshadow-color: #101010 !important;
}

#logodesc {display:none;}


/* General font families for common tags */

font, th, td, p { font-family: Segoe UI, Verdana, Arial, Helvetica, sans-serif }

a:link, a:active, a:visited { color : #F30342 !important }

a:hover
{
   text-decoration: underline !important;
   color : #F969A8 !important;
}

hr
{
   height: 0px !important;
   border: solid #1D1D1D 0px !important;
   border-top-width: 1px !important;
}

/* This is the border line & background colour round the entire page */

.bodyline
{
   background-color: #1D1D1D !important;
   border: 1px #101010 solid !important;
}

/* This is the outline round the main forum tables */

.forumline
{
   background-color: #101010 !important;
   border: 0px #101010 solid !important;
}

/* Main table cell colours and backgrounds */

tr.row1 { background-color: #1D1D1D !important }
tr.row2 { background-color: #1F1F1F !important }
tr.row3 { background-color: #1D1D1D !important }
td.row1 { background-color: #1D1D1D !important }
td.spacer { background-color: #1D1D1D !important;}
td.row2 { background-color: #1D1D1D !important;}
p.topicdetails { background-color: #1D1D1D !important;}
td.catdiv
{
   background-color: #1D1D1D !important;
   border: 0px !important;
   background-image: none !important;
}
.row3 { background-color: #454545 !important }

/*
  This is for the table cell above the Topics, Post & Last posts on the index.php page
  By default this is the fading out gradiated silver background.
  However, you could replace this with a bitmap specific for each forum
*/

/* cell pic 2 */
td.rowpic
{
   background-color: #1f1a16 !important;
   background-repeat: repeat-y !important;
}


/* cell pic 3 */
th
{
   display: none !important;
   color: #EDEDE4 !important;
   font-size: 12px !important;
   font-weight : bold !important;
   background-color: #555555 !important;
   height: 25px !important;
}

/* cell pic 1 */
td.cat, td.catHead, td.catSides, td.catLeft, td.catRight, td.catBottom
{
   background-color: #1D1D1D !important;
   background-image: none !important;
   border: #372E28 !important;
   border-style: solid !important;
   height: 28px !important;
}

/*
  Setting additional nice inner borders for the main table cells.
  The names indicate which sides the border will be on.
  Don't worry if you don't understand this, just ignore it :-)
*/

td.catHead, td.catBottom
{
   height: 29px !important;
   border-width: 0px 0px 0px 0px !important;

}

th.thHead, th.thSides, th.thTop, th.thLeft, th.thRight, th.thBottom, th.thCornerL, th.thCornerR
{
   font-weight: bold !important;
   border: #101010 !important;
   border-style: solid !important;
   height: 28px !important;
}

td.row3Right, td.spaceRow
{
   background-color: #1D1D1D !important;
   border: #101010 !important;
   border-style: solid !important;
}

th.thHead, td.catHead
{
   font-size: 12px !important;
   border-width: 0px 1px 0px 1px !important;
}

th.thSides, td.catSides, td.spaceRow { border-width: 0px 1px 0px 1px !important }
th.thRight, td.catRight, td.row3Right { border-width: 0px 1px 0px 0px !important }
th.thLeft, td.catLeft { border-width: 0px 0px 0px 1px !important }
th.thBottom, td.catBottom { border-width: 0px 1px 1px 1px !important }
th.thTop { border-width: 1px 0px 0px 0px !important }
th.thCornerL { border-width: 1px 0px 0px 1px !important }
th.thCornerR { border-width: 1px 1px 0px 0px !important }

/* The largest text used in the index page title and toptic title etc. */

.maintitle, h1
{
   font-weight: bold !important;
   font-size: 55px !important;
   font-family: "Trebuchet MS",Verdana, Arial, Helvetica, sans-serif !important;
   text-decoration: none !important;
   line-height : 120% !important;
   color : #996666 !important;
}

h2
{
   font-weight: bold !important;
   font-size: 22px !important;
   font-family: "Trebuchet MS",Verdana, Arial, Helvetica, sans-serif !important;
   text-decoration: none !important;
   line-height : 120% !important;
   color : #996666 !important;
}

/* General text */

.gen { font-size : 12px }

.genmed { font-size : 11px }

.gensmall
{
   font-size : 10px;
   color: #CCCCCC !important;
}

.gen, .genmed { color : #EDEDE4 !important }

a.gen, a.genmed, a.gensmall
{
   color: #7A98CC !important;
   text-decoration: none;
}

a.gen:hover, a.genmed:hover, a.gensmall:hover
{
   color: #60C6DC !important;
   text-decoration: underline;
}

/* The register, login, search etc links at the top of the page */

.mainmenu
{
   font-size : 11px !important;
   color : #EDEDE4;
}

a.mainmenu
{
   text-decoration: none !important;
   color : #F30342 !important;
}

a.mainmenu:hover
{
   text-decoration: underline !important;
   color : #60C6DC !important;
}

/* Forum category titles */

.cattitle
{
   font-weight: bold !important;
   font-size: 12px  !important;
   letter-spacing: 1px !important;
   color : #F30342;
}

a.cattitle
{
   text-decoration: none !important;
   color : #F30342 !important;
}

a.cattitle:hover { text-decoration: underline !important }

/* Forum title: Text and link to the forums used in: index.php */

.forumlink
{
   font-weight: bold !important;
   font-size: 12px !important;
   color : #7A98CC !important;
}

a.forumlink
{
   text-decoration: none !important;
   color : #F30342 !important;
}

a.forumlink:hover
{
   text-decoration: underline !important;
   color : #ffa754 !important;
}

/* Used for the navigation text, (Page 1,2,3 etc) and the navigation bar when in a forum */

.nav
{
   font-weight: bold !important;
   font-size: 11px !important;
   color : #EDEDE4 !important;
}

a.nav
{
   text-decoration: none !important;
   color : #B8CC93 !important;
}

a.nav:hover
{
   text-decoration: underline !important;
   color : #6EC6DC !important;
}

/* titles for the topics: could specify viewed link colour too */

.topictitle
{
   font-weight: bold !important;
   font-size: 11px !important;
   color : #F09EAD !important;
}

a.topictitle:link
{
   text-decoration: none !important;
   color : #F30342 !important;
}

a.topictitle:visited
{
   text-decoration: none !important;
   color : #F969A8 !important;
}

a.topictitle:hover
{
   text-decoration: underline !important;
   color : #6EC6DC !important;
}

/* Name of poster in viewmsg.php and viewtopic.php and other places */

.name, .postauthor
{
   font-size : 11px !important;
   color : #C2BE9E !important;
}

/* Location, number of posts, post date etc */

.postdetails
{
   font-size : 10px !important;
   color : #CCC !important;
}

/* The content of the posts (body of text) */

.postbody
{
   font-size : 14px !important;
   line-height: 18px !important;
   color: #edede4 !important;
}

a.postlink:link
{
   text-decoration: none !important;
   color : #F30342;
}

a.postlink:visited
{
   text-decoration: none !important;
   color : #F30342 !important;
}

a.postlink:hover
{
   text-decoration: underline !important;
   color : #60C6DC;
}


/* Copyright and bottom info */

.copyright
{
   font-size: 10px !important;
   font-family: Verdana, Arial, Helvetica, sans-serif !important;
   color: #444444 !important;
   letter-spacing: -1px !important;
}

a.copyright
{
   color: #444444 !important;
   text-decoration: none !important;
}

a.copyright:hover
{
   color: #EDEDE4 !important;
   text-decoration: underline !important;
}

.forumrules {display: none !important;}

/* Form elements */
textarea
{ 
background-color: #1D1D1D !important;
border-color: #EDEDE4 !important;
color: #CCCCCC !important;
font: normal 11px Verdana, Arial, Helvetica, sans-serif !important;
}


/* The text input fields background colour */

input.post, message, select
{
   background-color : #444444 !important;
   color: #FFFFFF !important;
}

input { text-indent : 2px !important }

/* The buttons used for bbCode styling in message post */

input.button
{
   background-color : #616161 !important;
   color : #edede4 !important;
   font-size: 11px !important;
   font-family: Verdana, Arial, Helvetica, sans-serif !important;
}

/* The main submit button option */
input.btnmain
{
   background-color : #CCCCCC !important;
   font-weight : bold !important;
}

/* None-bold submit button */
input.btnlite
{
   background-color : #999999 !important;
   font-weight : normal !important;
}

input.btnfile { background-color : #AFAFAF !important; }
textarea.post { background-color : #AFAFAF !important; }

/* This is the line in the posting page which shows the rollover
  help line. This is actually a text box, but if set to be the same
  colour as the background no one will know  !important;)
*/

.helpline
{
   background-color: #545454 !important;
   color : #B8CC93 !important;
   border-style: none !important;
}
}
