@namespace url(http://www.mozilla.org/keymaster/gatekeeper/there.is.only.xul);
/* ==UserStyle==
// @name         Tabs with system appearance
// @author       Roblesolido
// @compatible   Firefox 4.0+
// @description	 This restores in Firefox the appearance of tabs of system.
// ==/UserStyle==*/

.tabbrowser-tabs
{
margin: 3px 0 0px 0 !important;
line-height: 29px !important;
}

.tabbrowser-tabs .tabbrowser-tab
{
-moz-appearance: tab !important;
-moz-box-sizing: border-box !important;
padding: 0 4px 0 4px !important;
vertical-align: bottom !important;
}

.tabbrowser-tabs .tabbrowser-tab
{/*25px*/
height: 24px !important;
min-height: 24px !important;
margin: 4px 0 1px 0 !important;
}

.tabbrowser-tabs .tabbrowser-tab:not([selected="true"]):hover
{
height: 26px !important;
min-height: 26px !important;
margin: 1px 0 1px 0 !important;
}

.tabbrowser-tabs .tabbrowser-tab[selected="true"]
{/*28px*/
height: 29px !important;
min-height: 29px !important;
margin: 0 0 0px 0 !important;
}

.tabbrowser-tabs .tabbrowser-arrowscrollbox .tabs-newtab-button
{
height: 24px !important;
min-height: 24px !important;
