css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("tesnexus.com") {

#content, #main-top, #main-bottom { background: none !important; }

#hotfiles-container { position: absolute !important; 
width: 630px !important; 
right: 320px; 
top: 253px; 
margin-bottom: 0px !important; 
height: 75px !important; 
}

.hotfiles-text { width: 580px !important; 
font-size: 10pt !important; margin-right: 310px !important;}

.hotfiles-box { top: 0; right: 0; background-color: #242424; opacity: .75; width: 640px !important; margin-right: 290px !important;}


.news-list { background-color: #292929 !important; }
.files-tab-description { background-color: #292929 !important; }

.bb-content { font-size: 13pt !important;
              font-family: serif;
              padding-left: 20px !important;
              padding-right: 20px !important;
              padding-bottom: 25px !important;
}

.bb-content ul, .bb-content ol { font-size: 13pt !important; }

.bb-content h2 {background: none !important;
              background-color: #cf964a !important; }

.quote { font-size: 13pt !important; }

#left-col { position: absolute !important;
            left: 10px;
            top: 255px;
            right: 330px;
            float: none !important;
            width: auto !important;
}

h2 {letter-spacing: normal !important;
    text-shadow: none !important;
    font-family: serif;
    font-size: 15pt !important;
   }

h2 a { color: black !important; 
   }


#right-col { position: absolute;
             top: 155px;
             right: 0px;
           }

.news-list h2 { background: none !important;
              background-color: #cf964a !important;
              }

.news-list .date-comments { background-color: black !important; }



.files-tab-header { background: none !important;
              background-color: #cf964a !important; }

.files-tab-header h2 { color: black !important;  }



.bottom-corner { background: none !important; }

.news-filters { background-color: #232323 !important; 
                width: 665px; }

.comment-list div.right { float: none !important; width: 92% !important; margin-left: 60px !important;  }
.comment-list .comment-posted { left: 100px !important; }

html, body { background-image: none !important; }

.file-image-scroller { margin-top: 65px !important; }

#content .header h1 {  letter-spacing: normal !important;
                       text-shadow: none !important;
                       font-family: serif;
                       font-size: 20pt !important;
                    }

.header { position: absolute;
          left: 20px !important;
          width: 100% !important;

        }

#content .header .buttons { float: none !important;
position: absolute;
right: 360px;
}

.image-type { background-image: none !important; 
background-color: transparent !important; 
}

.files-tab-images .inner   { color: black !important; 
background-image: none !important; 
background-color: transparent !important; 
}

.inner strong  {    text-shadow: none !important; }

.inner em { background-image: none !important;
background-color: white !important; 
margin-left: 5px; padding-right: 5px;
border-radius: 18px;
height: 32px;
}
#footer {display: none;}

}
