css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document
url-prefix(http://www.wikipedia),
url-prefix(http://en.wikipedia),
url-prefix(http://de.wikipedia),
url-prefix(http://fr.wikipedia),
url-prefix(http://it.wikipedia),
url-prefix(http://meta.wikimedia),
url-prefix(http://en.wiktionary),
url-prefix(http://fr.wiktionary),
url-prefix(http://de.wiktionary),
url-prefix(http://it.wiktionary)


{
.articleFeedback,
#column-one,
#footer,
.editsection,
#siteNotice,
#contentSub,
.toc,
.ambox,
.noprint {
   display: none !important;
}

h1, h2, h3, h4, h5, h6
{
    border-bottom: 1px dashed #B2B2B2 !important;
    font-family: Constantia, Gotham, GothamRounded-Medium, GothamRnd-Medium  !important;
    
   
   
   
}

h1
{
    font-size: 36px !important;
    margin-top: 0.625em !important;
    margin-bottom: 0.3em !important;
    padding-bottom: 0 !important;
}
h2
{
    font-size: 24px !important;   
    line-height: 0.9375 !important;
    margin-top: 0.9375em !important;
    margin-bottom: 0.9375em !important;
}
h3
{
    font-size: 21px !important;   
    line-height: 1.071 !important;
    margin-top: 1.071em !important;
    margin-bottom: 1.071em !important;
}
h4
{
    font-size: 18px !important;   
    line-height: 1.25 !important;
    margin-top: 1.25em !important;
    margin-bottom: 1.25em !important;
}
h5
{
    font-size: 16px !important;   
    line-height: 1.071 !important;
    margin-top: 1.071em !important;
    margin-bottom: 1.071em !important;
}
h6
{
    font-size: 15px !important;   
    line-height: 1.406 !important;
    margin-top: 1.406em !important;
    margin-bottom: 1.406em !important;
}


.firstHeading
{
    font-size: 36px !important;
    font-family: Sentinel Semibold, Forza Black, Gotham, GothamRounded-Bold, GothamRnd-Bold,  !important;
    font-style: italic;
   
   
   
     
   
}

#p-cactions, #p-logo, #p-tb, #p-lang
{
    display: none !important;
}
body
{
    background: #ECE9D8 !important;
    min-width: 1000px;
    max-width: 1500px;
    margin: 15px auto !important;
    min-height: 100%;
    border-top: 0px #98968C solid !important;
       
   
   
   
}

p
{
    margin-bottom: 1.406em !important;
    line-height: 1.5 !important;
}

.dablink
{
    padding-left: 0em !important;
    padding-top: 0em !important;
    opacity: 1;
    font-family: Constantia, WhitneyHTF-MediumCondensed !important;   
    font-size: 14px;
}

.dablink:before
{
   
content: "> ";
    color: #9E2110 !important;
    font-family: Forza Black, MercuryNumericG4-SemiItalic, Mercury-NumericG4SemiboldItalic !important;
}

#content
{
    background-image: none !important;
    background-color: #ffffff !important;
    padding: 0px 38px 38px 38px !important;
    
    font-family: Constantia, WhitneyHTF-Medium !important;
    font-size: 16px !important;
    font-weight: 500;
    line-height: 1.5em !important;
    width: 33em !important;
    position: absolute !important;
    left: 11.5% !important;
border: 1px solid #696;
padding: 60px 0;


-moz-border-radius: 0px !important;
border-radius: 0px;
-webkit-box-shadow: #000 0px 2px 3px ;
-moz-box-shadow: #000 0px 2px 3px !important;
box-shadow: #666 0px 2px 3px;

background: -moz-linear-gradient(#EEFF99, #66EE33);
   
   
   


   
   
   
   
}

.portlet, #footer, #bodyContent > H3:first-child + DIV + DIV + DIV + TABLE, #bodyContent > H3:first-child + DIV + DIV + DIV + TABLE + TABLE, #anon-banner, #siteSub, #siteNotice, span.editsection,
span#coordinates,
span.editsection,
span.toctoggle,
#togglelink,
#contentSub,
.metadata,

div.topicon
{
    display: none !important;
}

.metadata.plainlinks.ambox.ambox-content
{
    margin-top: 10px !important;
    margin-bottom: 10px !important;
}

table.ambox th.ambox-text, table.ambox td.ambox-text
{
    padding-left: 1em !important;
    padding-right: 1em !important;
    width: 100%;
    opacity: .75;
}

.searchresult
{
    width: 95% !important;
}
.infobox
{
    margin-left: 1.5em !important;
    margin-top: 1.0em !important;
   
}
.thumbinner
{
    background-color: #FFF !important;
    margin-left: 0em !important;
    margin-top: 1.0em !important;
    border: none !important;
   

}
.thumbcaption
{
    background-color: #FFF !important;
    margin-top: 1.0em !important;
    border-top: 1px dashed #B2B2B2 !important;
   
   
}   
#toc #toctitle, .toc #toctitle, #toc .toctitle, .toc .toctitle
{
    display: none !important;
   
   
}

img.thumbimage, img
{
    opacity:1 !important;
}

img.thumbimage:hover, img:hover
{
    opacity:0.8 !important;
}

div.thumbcaption:before
{
    content: ">";
    color: #9E2110 !important;
    font-family: Forza-Black !important;
       
}

div.thumbcaption
{
   
    text-align: left !important;
    font-family: Constantia !important;
    font-size: 12px !important;
    font-style: italic;
    
   
   
   
   
   
}

div.rellink
{
    text-align: left !important;
    font-family: Constantia !important;
}

div.rellink:before
{
    content: "> ";
    color: #9E2110 !important;
    font-family: Forza-Black, MercuryNumericG4-SemiItalic !important;
}

big
{
text-transform: UPPERCASE;   
font-size: 20px !important;

}
table.plainlinks
{
margin-top: 25px !important;
max-width: 530px !important;



}
div.infobox
{
margin-top: 5px !important;
max-width: 530px !important;

}
a:link { font-weight: bolder; color: #2C4484 !important; text-decoration: none !important;   }

a:hover, a:active { color: #687680 !important ; text-decoration: none !important;  }





#toc
{
background-color: #F8F7EF !important;
    border-bottom: 1px dashed #B2B2B2 !important;
    border-right: 0px !important;
    border-top: 1px dashed #B2B2B2 !important;
    border-left:0px !important;
}
.infobox
{
background-color: #EDEBD5 !important;

}
table.navbox
{
max-width: 533px !important;

}

p {
margin-bottom:0 !important;

}

p + p {
text-indent: 1.406em !important;
margin-top: 0 !important;
}


}
