css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("chelsealive.pl") {

body{
	background:#FFF;
	font-family:Tahoma, Arial, Times, sans-serif !important;
	font-size:12px;
	font-weight:normal;
	color:#404040;
	text-align:left;
	}

#text{
    padding: 5px 24px 15px 24px;
    text-align: left;
    font-family: Tahoma !important;
    font-size: 14px;
    letter-spacing: 0.5px;
}

#team-squad-1, #team-squad-2{
    float: left;
    width: 158px;
    font-family: Tahoma !important;
    font-size: 11px;
    background-color: #fff;
    border-left: 1px solid #bbc1c6;
    border-right: 1px solid #bbc1c6;
    position: absolute;
    top: 70px;
}

#profile_container fieldset legend {
	font-size: 12px;
	color: #000;
	font-family: Tahoma !important;
	margin: 0 25px;
	padding: 0 5px;
	font-weight: bold
}





#user_panel h4 {
	padding-bottom: 10px;
	font-weight: normal;
	color: #3f6dbe;
	font-size: 15px;
	font-family:Tahoma, Arial, Helvetica, sans-serif !important;
}

#news-dnia h4{
	font-family:Tahoma, Arial, Helvetica, sans-serif !important;
	font-size:15px;
	color:#3f6dbe;
	margin:6px 20px 0px 9px;
	float:left;
	}

.comment-nick { 
	color: #3c7adf;
	font-family: Tahoma !important;
	font-size: 14px;
	font-style: italic;
	font-weight: 700;
	margin-bottom: 10px;
}

#text h2, #player-info-text h2{
    color: #3f69ad;
	font-family: Tahoma, "Arial", "Helvetica", sans-serif !important;
	font-size: 15px;
	padding-bottom: 5px;
	font-weight: bold;
	font-style: normal;
}

#text h1, #player-info-text h1{
    color: #3f6dbe;
    font-size: 15px;
    font-weight: bold;
    font-family: Tahoma, "Arial", "Helvetica", sans-serif !important;
    padding-bottom: 5px;
    letter-spacing: 0;
}

#profile_container h2 {
	color: #3f6dbe;
	font-size: 15px;
	font-family: Tahoma, Arial, Helvetica, sans-serif !important;
	padding: 0 0 20px;
	font-weight: bold
	
}

}
