css:
@-moz-document domain(tumblr.com) {
body 
	{background: url('http://www.coldplay.com/downloads/wp01_03.jpg') center right #3e3e40 repeat fixed !important;}  
*  
	{text-shadow:none !important;}
	


#left_column, #right_column {background-color: transparent !important;}

div.l-content.l-content.l-content {background-color:#CACACC !important;	
	opacity:.9;}
	


#auto_pagination_loader
	{background:rgba(187,103,85, 0.00)!important;}
div.return_to_top_icon{	background: none !important;
	width: 0px !important;
	height: 0px !important;
	margin-top: 0px !important;
	opacity: .5 !important;
	border-bottom: solid 30px white !important;
	border-right: 15px solid transparent !important;
	border-left: 15px solid transparent !important; }	
	
	


.selection_nipple.white, .selection_nipple:not([class="selection_nipple white"]) { 
	background: none !important;
	width: 0px !important;
	height: 0px !important;
	margin-top: 0px !important;
	border-bottom: solid 11px white !important;
	border-right: 11px solid transparent !important;
	border-left: 11px solid transparent !important; }
.selection_nipple:not([class="selection_nipple white"]) {
	border-bottom: solid 11px #CACACC !important;}	
	

	
.selected .following,
.selected .followers,
.selected .settings,
.selected .posts,
.selected .messages,
.selected .drafts,
.selected .queue, 
.selected .likes {
	color: rgba(255,255,255, 0.50) !important; 
	background: #DFDFDF !important;}

	
	

/* Code repaired by admin. See http://forum.userstyles.org/discussion/37591/attempting-to-mass-fix-broken-tumblr-dash-icon-styles */
.new_post {
	background: #ffffff url('http://i1024.photobucket.com/albums/y302/BlahRandom/OhTumblr/travelandexploretextposticons394-sk.png') center center no-repeat !important;
}
.new_post_label_icon {
	background-image: none !important;
}


	
	
a.explore_more_tags,
a.add_and_remove,
a.customize,
a.mass_editor
	{background-color:#CACACC !important;  
	background-image:none !important;
	border:0px !important}

div.hide_overflow
	{font-size:.9em;}
a.tag,
a.explore_more_tags,
a.add_and_remove
	{height:8px;}
a.tag
	{position:relative;top:-4px;}
.selected  .tag
	{height:14px;background:#DFDFDF !important; 
	position:relative;top:0px;
	color:rgba(255,255,255, 0.50) !important;}
ul
	{-moz-box-shadow: 1px 1px 3px #DFDFDF !important;}
	


div.gradient,
div.dashboard_nav_item, 
a.permalink
	{background-color:#CACACC !important;
	opacity:.9; 
	background-image:none !important;
	border:0px !important}
.content.is_media, 
.mask.top, 
.mask.bottom {
	background: none !important; }
.post.photo_post .photo, 
.post.photo_post .video_thumbnail {
	border-radius: 16px !important;}
.post.photo_post .photo, 
.post.photo_post .video_thumbnail 
	{-moz-box-shadow: 1px 1px 3px #000000 !important;}



blockquote
	{margin-left:-5px !important;}



.login.sign_up,
A[href="/forgot_password"]{display: none !important;}	



.mceContentBody {
  background: none !important; }
  


ol#posts li.notification
	{background-color: #3e3e40 !important;
	border-bottom: 2px solid #333333 !important;
	color: #000000 !important;}
	
#right_column a#dashboard_switch_blog_menu_current:hover 
	{background:#3e3e40;}
	
#nav .nav_item.active
	{background-color:#3e3e40;}
	
#right_column
	{color:#333333;}
	
#left_column #posts .post .permalink
	{background-color:#3e3e40;}

#nav .nav_item.active, 
#nav .nav_item .nav_menu, 
#nav .nav_item .nav_item_nipple .nav_item_nipple_pixel
	{background-color:#3e3e40 !important;
	background-image:none !important;
	border:0px !important}

#dashboard_switch_blog_menu_current,
#dashboard_switch_blog_arrow_and_divider,
#dashboard_switch_blog_arrow_current
	{background-image:none !important}

.notification_type_icon
	{display:none !important}

#left_column #posts .notification 
	{background-color:#3e3e40 !important;
	color:#666 !important;
	border-bottom-color: #eee !important}

#new_post_notice_container
	{color:#fff !important}

#new_post_notice_container a
	{color:#fff !important}
ol#posts .post.new_post .new_post_label 
	{display:none !important;}


#logo {height: 0 !important;







	width: 0 !important;







	padding-left: 350px !important;







	padding-top: 44px !important;







	background: url(http://30.media.tumblr.com/tumblr_lpnt6z2B6p1r14o5so1_r1_400.png) no-repeat !important;}
}
