/*----- DEFAULT TEXT, BORDER & BACKGROUND COLORS -----*/
body, html, div, h1, h2, h3, h4, h5, h6, table, tbody, thead, tr, td, th, ul, li, p, i, dd, dt, strong, code, iframe, embed, section, blockquote, label, form, pre, dl, img {color: #FFFFFF !important; text-shadow: none !important; background-color: transparent !important; border-color: #444 !important; -moz-border-top-colors: #444 !important; -moz-border-bottom-colors: #444 !important; -moz-border-left-colors: #444 !important; -moz-border-right-colors: #444 !important}
a:not([style*="color"]), span:not([style*="color"]) {color: #F1FA04 !important; text-shadow: none !important; background-color: transparent !important; border-color: #444 !important; -moz-border-top-colors: #444 !important; -moz-border-bottom-colors: #444 !important; -moz-border-left-colors: #444 !important; -moz-border-right-colors: #444 !important}
*[style*="color"]:not([style="abcxyz"]), *[style*="color"]:not([style="abcxyz"]) * {text-shadow: 0 0 3px #000 !important}

table {background-color: rgba(40,30,30,.6) !important; border-radius: 6px !important}
div[style] > form {background-color: rgba(10,20,20,.8) !important; border-radius: 6px !important}

a:not([style*="abcxyz"]), a:not([style*="abcxyz"]) * {color: #6DF9F4 !important; text-decoration: none !important}
a:hover, a:visited:hover, a:hover *, span[onclick]:hover, div[onclick]:hover, [role="link"]:hover, [role="link"]:hover *, [role="button"]:hover, [role="button"]:hover *, [role="menuitem"]:hover, [role="menuitem"]:hover *, .link:hover, .link:hover *, .sn-link:hover, .gac_b:hover *, .google-ac-b *, .yt-suggest-selected *, .yt-uix-button-menu-item:hover, .acl div.sug .sel *, .acl div.sug .sel, .ac_active, .ac_active *, .yui-ac-bd li:hover {
  color: #F0F0F0 !important; text-shadow: 0 0 5px rgba(255,255,200,0.9) !important}
a:visited {color: #0FFA2A !important}
a.highlight, a.highlight *, a.active, a.active *, .selected, .selected *, .gtab-selected, .gtabi-mc, [href="#"] {color: #F8A3F8 !important; font-weight: bold !important}

h1, h2, h3, h4, h5, h6, h1 *, h2 *, h3 *, strong {color: #2FDD00 !important}
[class*="error"], [class*="alert"], code, span[onclick], div[onclick] {color: #FF8AFB !important}

::-moz-selection {background-color: #377 !important; color: #F8A3F8 !important}
:focus {outline: none !important}

/*----- MENU & CO BACKGROUND-COLORS -----*/
iframe, embed, header, nav, footer, label [onclick], div[style*="position:"][style*="left:"][style*="visible"], div[style*="z-index:"][style*="left:"][style*="visible"], div[style*="-moz-user-select"], div[role="menu"], div[role="dialog"], span[class*="script"] div, [id*="menu"], [class*="dropdown"], [class*="popup"], [class="title"], ul[style*="display:"], a[id*="ghosteryfirefox"], #translator-popup, .vbmenu_popup, #vspb, #search_controls, .menu, .tooltip, .olp-mn, .gh-iovr, #categoriesdropdown, #user_link_menucontent, .overlay-stream, .dd, .ac_res, .yui-ac-bd, #overhead-collect #lub, .bluestip, .oh-menu, .popup2, .bt-mid, .gac_m, .google-ac-m, .gbm, .yt-suggest-table, .yt-uix-button-menu, .acl div.sug, .yt-uix-tooltip, .yt-uix-tooltip-tip > div > div, #currentStore, #BobMovie, #nav-main ul ul {
  background-color: rgba(5,5,5,.8) !important;
  border-radius: 5px;
  box-shadow: 1px 1px 5px #000 !important}
[id*="overlay"], blockquote {
  background-color: rgba(35,35,35,.9) !important;
  border-radius: 5px}
pre, dl {background-color: rgba(5,5,5,.5) !important}

/*----- OTHER COLOR CHANGES -----*/
.modbox *:empty, div[style*="border: 1px"], div[style="display: block;"] {background-color: rgba(0,0,0,.5) !important}
[class*="dialog-bg"], .Kj-JD-Jh {background-color: rgba(35,35,35,.7) !important; opacity: 1 !important}
i.gr2, i.gr3, .gr-top {background-color: #252020 !important}
.goog-imageless-button-top-shadow {border-color: transparent !important}
.cc-in, .gr-body, .smbutton > span, #watch-info, #watch-next-list, #watch-channel-discoverbox, .object-lead, .listing .item {background: -moz-linear-gradient(top, rgba(200,200,200,.1), rgba(0,0,0,.3)) !important; background: -webkit-gradient(linear, left top, left bottom, from(rgba(200,200,200,.1)), to(rgba(0,0,0,.3))) !important}



/*----- DEFAULT BUTTONS, SEARCHBOXES & CO -----*/
input, select, button, a[href="javascript:;"]:not([t]):not([onclick]) {
  -moz-appearance: none !important;
  background: #000 !important;
  color: #9E79FC !important;
  border: 2px solid #333 !important;
  border-radius: 4px !important}
input:hover, input[type="button"]:hover, select:hover, button:hover {
  border: 2px solid #555 !important;
  -moz-border-top-colors: #555 !important;
  -moz-border-bottom-colors: #555 !important;
  -moz-border-left-colors: #555 !important;
  -moz-border-right-colors: #555 !important}
input:focus, select:focus {
  box-shadow: 0 0 5px #9E79FC !important}
input[type="checkbox"], input[type="checkbox"]:focus {
  border: 2px solid #222 !important;
  background-color: #F0F0F0 !important;
  color: #9E79FC !important;
  border-radius: 4px !important}
input[type="radio"], input[type="radio"]:focus {
  border: 2px solid #222 !important;
  background-color: #F0F0F0 !important;
  color: #9E79FC!important}
select input[type="button"] {border: none !important}

button:active, input[type="submit"]:active {
  background: #353978 !important}

input *, input, select {color: #F0F0F0 !important}
input *:hover * {color: #F0F0F0 !important; text-shadow: 0 0 2px #FFF !important}

textarea {-moz-appearance: none !important; background: rgba(0,0,0,.3) !important; -moz-border-radius: 3px !important; border: 1px solid #000 !important; -moz-box-shadow: inset 0 0 8px #000 !important}
textarea:focus {background: rgba(0,0,0,.5) !important}
textarea, textarea * {color: #F0F0F0 !important; text-shadow: 0 0 1px gray !important}
textarea:focus, textarea:focus > * {text-shadow: none !important; -moz-box-shadow: none !important}

option {
	-moz-appearance: none !important; background: none !important; color: #555 !important}
option:not([disabled]):hover, option:focus, option:checked {
	background: -moz-linear-gradient(center top, #AAA, #555) !important; color: #000 !important}




/*----- IMAGE CHANGES -----*/
*:not(:empty):not(html):not(span):not(option):not([style="display: block;"]):not([onclick*="open"]):not([onclick*="s_objectID"]):not(#globalsearch):not(IMG):not([class*="stars"]):not([id*="stars"]):not([id="rating"]):not([class="rating"]):not([class*="SPRITE"]):not([id*="SPRITE"]):not([class*="item"]):not([id*="item"]):not([class*="thumb"]):not([class*="icon"]):not([id*="lbImage"]):not([class*="cc-in"]):not([class*="gr-body"]):not([id*="watch"]):not(.sp),
.r3_hm, .gmbutton2 b, .gtab-i, .ph, .bstab-iLft, .csb, #pagination div, [style*="sprite2.png"], #mw-head-base, #mw-page-base {
  background-image: none !important}
.olp-mn * {background: #353978 !important; border: none !important}

img {opacity: .7 !important}
img:hover, a:hover img {opacity: 1 !important}

/*----- GOOGLE & YOUTUBE LOGOS -----*/
#lga #logo, #hp-logo img, img[src*="video_logo_lg.gif"], div[title="Firefox Start"] {
  margin-top: 4% !important;
  width: 0 !important;
  height: 103px !important;
  padding: 0 281px 0 0 !important;
  background: url("http://img193.imageshack.us/img193/8379/indexiin.png") !important}
h1 a[title*="Google"] img, a img[src="/images/logo_sm.gif"] {
  width: 0 !important;
  padding: 0 150px 0 0 !important;
  top: 0 !important;
  opacity: 1 !important;
  background: url("http://h-2.abload.de/img/indexsmall5z5t.png") !important}
h1 a[title*="Google"] {color: transparent !important}

.a9 {background: url("http://i46.tinypic.com/30n9aq8.jpg") no-repeat !important}

img[alt^="YouTube"] {
  width: 0 !important;
  padding-right: 100px !important;
  opacity: 1 !important;
  background: url("http://www.abload.de/img/youtube_neu7us1.png") no-repeat !important}
}

/*----- ABOUT PAGES BACKGROUND -----*/
@-moz-document url-prefix(about) {
* {background-color: transparent}
body > * {background-color: rgba(0,0,0,.5) !important} }

/*----- SHOW INSTALLS ON USERSTYLES.ORG -----*/
@-moz-document domain(userstyles.org) {
li[total-install-count]:after {
  font-size: .70em !important;
  content: " (Installs: " attr(total-install-count) ", " attr(weekly-install-count) " wk)"} }

/*----- RIGHT-CLICK MENUS & TOOLTIP STYLE -----*/
#contentAreaContextMenu menuitem[disabled="true"], 
#contentAreaContextMenu menuitem[disabled="true"] menuseparator {
  display: none !important}

popup {
  -moz-appearance: -moz-win-glass !important;
  background: rgba(0,0,0,.8) !important;
  border: 1px solid !important;
  -moz-border-left-colors: #555 !important;
  -moz-border-right-colors: #555 !important;
  -moz-border-top-colors: #555 !important;
  -moz-border-bottom-colors: #555 !important;
  -moz-border-radius: 8px !important;
  -moz-box-shadow: inset 0 0 10px !important;
  padding: 2px 0 !important}
popup * {
  color: #EEF66D !important;
  background: none !important}
popup menuitem:hover * {
  color: #13E52C !important;
  text-shadow: 0 0 1px #EFEFEF !important;}
popup menuitem:last-child:hover * {
  color: #F59641 !important;
  text-shadow: none !important;}
popup menuseparator {
  -moz-appearance: none !important;
  margin: 2px 0 2px -3px !important;
  padding: 0 !important;
  border-top: none !important;
  border-color: transparent !important;
  -moz-box-shadow: 0 0 3px #666 !important}
popup menuitem {
  -moz-appearance: -moz-win-glass !important;
  font-size: 13px !important;
  font-weight: bold !important}

tooltip {
  -moz-appearance: none !important;
  color: #000000 !important;
  background-color: #FFFFFF !important;
  border: 1px solid #6F6F72 !important;
  -moz-border-radius: 3px !important}

/*----- FIX WHITE NEW TAB FLASH -----*/
tabbrowser tabpanels {background-color: #151515 !important}
