css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("www.pytal.de") {
body {
font-family: Trebuchet MS !important;
}
.header {
height: 75px !important;
}
h1 {
color: #00BFFF !important;
}
a.black {
color: #FFF !important;
font-weight: normal !important;
}
div.ib {
margin-left: -1px !important;
overflow: hidden !important;
background: #fff !important;
border: 1px solid #00BFFF !important;
-moz-opacity: 0.75 !important;
opacity: 0.75 !important;
height: 24px !important;
}
div.ib:hover {
opacity: 1.0 !important;
-moz-opacity: 1.0 !important;
height: 28px !important;
margin-top: -4px !important;
}
div.ab:hover {
height: 30px !important;
margin-top: -4px !important;
}
.ib a, .ab a {
margin-left: -10px !important;
color: gray !important;
font-weight: normal !important;
}
.ib img, .ab img {
display: none !important;
}
div.ab {
overflow: hidden !important;
border: 1px solid #00BFFF !important;
background: #00BFFF !important;
}
div.cbox_title {
background: transparent none !important;
color: gray !important;
border-bottom: 3px solid #00BFFF !important;
}
div.cbox {
border: 0px !important;
background: none !important;
}
.navigation {
background: #00BFFF !important;
}
.mainmenu {
background: white none !important;
border: 1px solid #00BFFF !important;
border-left: 0px none !important;
margin: -3px 0px 0px 0px !important;
color: #333 !important;
font-weight: normal !important;
}
.mainmenu:hover {
background: #00BFFF !important;
color: #fff !important;
}
.submenu {
border-right: 1px solid #00BFFF !important;
border-bottom: 0px none !important;
margin-bottom: -1px !important;
}
.submenu:hover {
background: #fff none !important;
color: gray !important;
border-left: 8px solid #00BFFF !important;
width: 165px !important;
margin-left: 0px !important;
}
.box_frame {
padding-bottom: 1px !important;
border-bottom: 1px solid #00BFFF !important;
}
div.boa_thread[style="background-color: rgb(221, 221, 221);"]{
background: #00BFFF !important;
border: 0px none !important;
color: #fff !important;
}
div.boa_thread[style="background-color: rgb(221, 221, 221);"] a{
color: #fff !important;
}
div.boa_thread, div.boa_thread a {
border: 0px none !important;
color: gray !important;
}
div[style="position: absolute; left: 0px; width: 300px; height: 150px; background-color: rgb(221, 221, 221);"] {
border: 1px solid #00BFFF !important;
background: #fff !important;
}
div[style="position: absolute; top: 0px; right: 0px; width: 300px; height: 150px; background-color: rgb(204, 204, 204);"], div[style="position: absolute; top: 0px; right: 0px; width: 300px; height: 150px; background-color: rgb(204, 204, 204);"], div[style="position: absolute; top: 0px; right: 0px; width: 300px; height: 150px; background-color: rgb(221, 221, 221);"], div[style="position: absolute; left: 0px; width: 300px; height: 150px; background-color: rgb(221, 221, 221);"] {
border: 1px solid #00BFFF !important;
background: transparent !important;
padding-bottom: 20px !important;
margin-bottom: 20px !important;
}
div[style="padding: 3px; position: absolute; top: 0px; left: 0px; width: 294px; height: 14px; text-align: left; vertical-align: middle; background-color: rgb(128, 128, 128); color: rgb(255, 255, 255); font-weight: bold;"]{
background: transparent !important;
color: gray !important;
}
div [style="position: absolute; top: 25px; left: 5px; width: 290px;"]{
background: transparent none !important;
}
.footer {
background: #00BFFF !important;
}
}
