css:
@-moz-document domain("foro.noticias3d.com")

{

/*■■■■■■■■■■■■ BODY ■■■■■■■■■■■■*/


* {
background-color: #2B2B2B !important;
}

body {
background-color: #2B2B2B !important;

}
body * {
background-color: #2B2B2B !important;

}

#header, #searchform * {
background-color: #262626 !important;
border-color: #3e3e3e !important;
}

#content, #navigation, #nav-browse, #nav-upload, #nav-community, #nav-about {
background-color: #242424 !important;
border-color: #3e3e3e !important;
}

.cke_skin_kama *, .cke_skin_kama a:hover, .cke_skin_kama a:link, .cke_skin_kama a:visited, .cke_skin_kama a:active, .cke_skin_kama *, .cke_skin_kama a:hover, .cke_skin_kama a:link, .cke_skin_kama a:visited, .cke_skin_kama a:active {
background-color: #3a3a3a !important;
}

.texteditor.forum .cke_skin_kama textarea.cke_source, .texteditor.forum textarea {
background-color: #292828!important;
}

.navtabs ul, .navbar_search input, .navtabs li a.navtab {
border-color: #999999 !important;
}

#qr_submit.button, #qr_preview.button, #vB_Editor_QE_p_save, #vB_Editor_QE_p_adv, #vB_Editor_QE_p_abort, a.textcontrol, .navpopupmenu.popupmenu.nohovermenu a.textcontrol, .navpopupmenu a.textcontrol, .navpopupmenu a.popupctrl, .navpopupmenu.popupmenu.nohovermenu a.textcontrol, .navpopupmenu.popupmenu.nohovermenu a.popupctrl, input.button {
background: #292828!important;
border-color: #D3D3D3 !important;
color: #D3D3D3 !important;
}

#qr_submit.button:hover, #qr_preview.button:hover, #vB_Editor_QE_p_save:hover, #vB_Editor_QE_p_adv:hover, #vB_Editor_QE_p_abort:hover, a.textcontrol:hover, .navpopupmenu.popupmenu.nohovermenu a.textcontrol:hover, .navpopupmenu a.textcontrol:hover, .navpopupmenu a.popupctrl:hover, .navpopupmenu.popupmenu.nohovermenu a.textcontrol:hover, .navpopupmenu.popupmenu.nohovermenu a.popupctrl:hover, input.button:hover, span.ctrlcontainer:hover {
color: #D3D3D3 !important;
}

span.ctrlcontainer {
color: #d3d3d3 !important;
}

.pagination span.selected a, .formcontrols, .blockhead {
border-color: #999999 !important;
}

.bbcode_quote div.quote_container, div.message, td {
color: #1874bb !important;
}

.bbcode_container div.bbcode_quote {
border-color: #1874bb !important;
}

#location, #location * {
background-color: #3e3e3e !important;
}

.maintable *, .maintable {
background-color: #3e3e3e !important;
border-color: #262626 !important;
}

#footer {
background-color: #262626 !important;
border-color: #d3d3d3 !important;
}

.section * {
background-color: #262626 !important;
border-color: #3e3e3e !important;
}

.rss {
background: none !important;
border: none !important;
}

/*■■■■■■■■■■■■ TEXTO ■■■■■■■■■■■■*/


* {
color:#bbbbbb !important; 

}

a:link {
color:#1874bb !important;
}

a:visited {
color:#1874bb !important;
}

a:hover {
color:#1E90FF !important;
text-decoration:underline !important;
}

a:active {
color:#1E90FF !important;
}

/*■■■■■■■■■■■■ IMGS ■■■■■■■■■■■■*/

img {
max-width: 1500px !important;

}
}
