css:
@namespace url(http://www.w3.org/1999/xhtml);
@-moz-document domain("irccloud.com") {

/* Hide Logo */
h1#title span.logo {
  display: none;
}

/* Set Status & Sidebar Background */
#statusBar,
#sidebar {
  background: #656565 url(data:image/gif;base64,) repeat !important;
}

/* Hide Beta Tag */
h1#title span.beta {
  display: none; 
}

/* Buffer Color */
#buffers li.server .active a {
  color: #656565 !important;
}

/* Unread Buffer Color */
#buffers li.unread h2.buffer a.buffer,
#buffers li.server .unread a.buffer {
  color: #f69300 !important; 
}
#buffers li.server .active.unread a.buffer {
  border-left-color: #f69300 !important; 
}

/* Active Buffer Color */
#buffers li.server .active.selected a.buffer, 
#buffers li.server .active.selected a.buffer:hover {
  background-color: #f69300 !important;
  border-color: #f69300 !important;
  color: #ffffff !important;
}

/* Buffer Hover */
#buffers li.server a.buffer:hover {
  background-color: #dfdfdf !important;
}

/* Archives Restyle */
#buffers p.archiveToggle a, 
#buffers p.archiveToggle a:hover {
  color: #656565 !important;
}

/* Server Buffers */
#buffers li.server h2 a.buffer, 
p#joinServer a { 
  background: #7192A8 url(data:image/gif;base64,) repeat-x !important;
  color: #ffffff !important;
  font-weight: bold;
  border-bottom: none !important;
  padding-left: 7px !important;
}
#buffers li.server h2 a.buffer:hover, 
p#joinServer a:hover {
  text-decoration: underline;
  color: #ffffff !important;
}

/* Statusbar & Footer Text */
#statusActions b#loggedInAs {
  color: #ffffff !important;
}
#statusBar a, 
#footer a, 
#footer p {
  color: #ffffff !important;
  text-decoration: none;
}
#statusBar a:hover, 
#footer a:hover {
  color: #ffffff !important;
  text-decoration: underline;
}
#statusActions {
  color: #656565 !important;
}

/* Invites Bubble */
#invites span.numInvites {
  background-color: #f69300 !important;
  border-color: #f69300 !important;
}

/* Nickname Button */
span#nickname a {
  background-color: #f69300 !important;
  font-weight: bold; 
}
span#nickname a:hover {
  background-color: #f69300 !important;
  text-decoration: underline;
}

/* Scrollbar */
::-webkit-scrollbar-thumb {
  background-color: #f69300 !important;
}

/* Buffer Titlebar */
.bufferHead {
  background: #C8C8C8 url(data:image/gif;base64,) repeat !important;
  border-bottom: 1px solid #656565 !important;
  border-top-right-radius : 0px !important;
}
#bufferView {
  border: 1px solid #656565 !important;
  border-top-right-radius : 0px !important;
}

/* Topic Links */
.topic a {
  color: #f69300 !important;
  text-decoration: none;
}
.topic a:hover {
  color: #f69300 !important;
  text-decoration: underline;
}

/* Context Menu */
.status p.buttons button.active span {
  background-color: #f69300 !important;
  border-color: #f69300 !important;
  background-image: none !important;
}
#channelContextMenu,
.contextMenu {
  border: 1px solid #f69300 !important;
  background-color: #f2f7fc !important;
}
.contextMenu ul.actions li a:hover, 
#channelContextMenu.active ul.actions li a:hover, 
#channelContextMenu ul.actions li a.delete:hover, 
#channelContextMenu.mod ul.actions li a.topic:hover {
  background-color: #dfdfdf !important;
}
#channelContextMenu .modeLine { 
  border-top: 1px solid #656565 !important;
}

}
