/*
 * Greedy Height Hattrick (1280px)
 * 
 * created by dadnal, based on template from _eNeRGy_ (7397517)
 * untested for non-supporters, sorry!
 * 
 * clock is set to invisible, remove last 3 lines to change that
 */ 


@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("hattrick.org") {

#hattrick,#hattrickNoSupporter,#page{
width:1220px!important;
}
#menu{
width:1180px!important;
padding:0px!important;
}
.subMenu{
width:200px!important;
}
.main{
width:760px!important;
}

.mainFull {
width: 1000px !important;
}

.mainWide{
width:890px!important;
}
#sidebar{
width:200px!important;
}
.subMenuConf{
width:365px!important;
}
.mainConf{
width:805px!important;
}

#top {
background: white !important;
}

#header {
background: white !important;
height: 66px !important;
}

#bottom {
display: none;
}

.scContainer {
width: 300px !important;
}

#ticker{
left: 0px !important;
padding-left:20px!important;
width: 900px !important;
padding-bottom: 3px;
border-bottom: 1px solid #cccccc;
}

#ticker div {
  float: left;
  max-width: 280px;
  overflow: hidden;
  margin-right: 5px !important;
}

#teamLinks {
  display: none;
  clear: both;
}

#logo {
  display: none !important;
}

#online {
  margin-top: 50px !important;
}

#menu a.logout {
  float: left !important;
}

.threadItem td div.url {
  width: 250px !important;
}

.threadInfo {
  font-family: fixed !important;
  line-height: 1.2em !important;
}

}

.liveTabsContainer {
  margin-left: 50px !important;
  padding: 5px;
  width: 210px !important;
}

.rtsLI {
  margin-top: 12px !important;
}

#time {
  display:none;
}
