css:
@-moz-document url-prefix('http://www.tumblr.com/'), url-prefix('https://www.tumblr.com/')   {


#dashboard_index, #dashboard_edit_post, #dashboard_submissions, 

#dashboard_drafts, #dashboard_post_queue, #dashboard_followers, 

#dashboard_members{



background-image:url('http://img713.imageshack.us/img713/5329/bg1xe.jpg') 

!important;



background-repeat:no-repeat !important;



background-attachment: fixed;



background-color:#004a81 !important;



background-position:top right !important


}


*{font-size: 11px; font-family: Georgia, Times New Roman, Serif !

important; font-style:italic !important}


#dashboard_index div.l-content, #dashboard_submissions div.l-content, 

#dashboard_drafts div.l-content, #dashboard_post_queue div.l-content, 

#dashboard_members #left_column, #right_column {background-color: transparent !important;}

div.l-content.l-content.l-content {



background: rgba(0,50,88,0.7) !important;

}


#dashboard_edit_post{



background-color: #004a81 !important


}


#right_column .dashboard_nav_item {



border-bottom:0px solid #eee !important;



background-image:none  !important



}



#right_column .dashboard_nav_item .dashboard_nav_title,



                    #right_column .dashboard_nav_item .dashboard_nav_title 

a,



                    #right_column .dashboard_nav_item a,



                    #right_column .dashboard_nav_item 

ul.dashboard_subpages,



                    #right_column .dashboard_nav_item 

ul.dashboard_subpages li a {

-moz-border-radius: 10px !important;



-webkit-border-radius: 10px !important;



                        color:#96a59a !important;


}



#right_column .dashboard_nav_item ul.dashboard_subpages li 

a.tracked_tag_control {



                        color:#888 !important ;



                    }



#right_column .dashboard_nav_item #dashboard_controls_radar_media_fold {



                        background: none !important;



                    }


#left_column #posts .post .post_avatar{-moz-border-radius: 38px !

important; -webkit-border-radius: 60px !important; width: 64px !important; 

height: 64px !important; margin: 0px !important;}



#left_column #posts .post .sub_avatar_inner {



-moz-border-radius: 38px !important; -webkit-border-radius: 60px !

important; width: 32px !important; height: 32px !important; margin: 0px !

important;



}




#left_column #posts .notification .avatar {



                        border-color:transparent !important;



                        background-color:#004a81 !important;

                    }



#left_column #posts .notification .avatar:hover {opacity:1 !important;}



.post{opacity:1 !important; border-bottom:0px !important; color:#555555}



.post:hover{opacity:1 !important}



.permalink{background-image:none !important; height:15px !important; 

width:15px !important; margin-top:4px !important; margin-right:5px !

important;-webkit-border-radius:5px 5px 5px 5px !important;-moz-border-

radius:5px 5px 5px 5px  !important}





.active{background-color:#f9ccc9 !important; color:#96a59a !important;}



.nav_item_nipple{display:none !important}



.nav_menu{background-color:#e7bcc6 !important; border-bottom:0px !

important; color:#96a59a !important;}



#nav .nav_item.active,



#nav .nav_item .nav_menu,



#nav .nav_item .nav_item_nipple .nav_item_nipple_pixel {



background-color:#fddcd5 !important;



background-image:none !important;



border:0px !important;



color:#96a59a !important;

font-size: 11px;


}



#nav .nav_item .nav_menu {



border-bottom:0px !important

font-size: 14px;

}


#nav .nav_item .nav_menu a {



color:#e7bcc6 !important;



border-top-color:#322115 !important;



}



#nav .nav_item .nav_menu a:hover {



color:#fbfbfb !important;}



.dashboard_switch_blog_menu_header{



background-color:#17130d !important;



color:#777 !important;



}



.dashboard_switch_blog_menu_item{



background-color:#ffffff !important;



color:#777 !important;



border:0px !important



}



.dashboard_switch_blog_menu_footer{



background-color:#ffffff !important;



color:#777 !important;



}



.dashboard_switch_blog_menu_wrapper{



background-color:#ffffff !important;



}



#new_post_notice_container{



color:#fff !important



}



#new_post_notice_container a{



color:#fff !important



}



#dashboard_switch_blog_menu_current,



#dashboard_switch_blog_arrow_and_divider,



#dashboard_switch_blog_arrow_current



{background-image:none !important}



#footer{



background: #004a81 !important;



background-image:none !important



}



ol#posts li.post.new_post {



background: #ffffff url('http://img684.imageshack.us/img684/8731/newposticonsnolabels.png') center center no-repeat;



}


img[src*="http://assets.tumblr.com/images/new_post_icons_no_labels.png"]

{

    width:0;

    height:70px;

    padding-right:250px;

    opacity: 0;

}



.new_post_label{

font-size:14px !important;

padding-top:5px !important;

font-family:Georgia, Times New Roman, sans-serif !important;

font-style:italic !important;

text-transform: none !important;

color:#Ffffff !important;

letter-spacing:1px !important

}



#left_column #posts .notification {

                        background-color:#003055 !important;

                        color:#014a81 !important;

                        border-bottom-color: #003863 !important

                    }



#left_column #posts .notification .block {

                            color:#014a81 !important;

                        }



#left_column #posts .notification.alt {

                        background-color: #003055 !important;

border-bottom-color: #003863 !important

                    }



#left_column #posts .notification.last_notification,


                    #left_column #posts .notification.single_notification 

{

                        border-bottom-color:#003863 !important;

                    }



#left_column #posts .notification a {

                        color:#014a81 !important;

                        border-bottom:0px !important;

                    }



#left_column #posts .notification blockquote {

                        border-left-color:#eee !important;

                    }



#left_column #posts .notification .avatar {

                        border-color:transparent !important;

                        background-color:#fbfbfb !important;

                    }



#left_column #posts .notification .avatar:hover{

                    }



.notification_type_icon{display:none !important}

#left_column #posts .notification a,

#left_column #posts .notification a:hover,

#left_column #posts .notification

 {font-size: 10px;}



h1,h2,h3,h4,h5,h6{color:#014a81 !important}



}
