css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("youtube.com") {

#watch-sidebar, #watch-related, #watch-related-container{
display:none!important;
}

#video-sidebar ul:nth-child(5)
, #video-sidebar ul:nth-child(7)
, #video-sidebar h3:nth-child(6)
, #video-sidebar h3:nth-child(4)
, #video-sidebar h3:nth-child(11)
,#video-sidebar ul
,#video-sidebar h2
,#video-sidebar p

{
display:none;
}

.guide .guide-section:nth-child(3), .guide .guide-section:nth-child(4){
display:none;
}

#builder-category-items li:nth-child(8), #builder-category-items li:nth-child(12), #builder-category-items li:nth-child(18), #builder-category-items li:nth-child(17)
, #builder-category-items li:nth-child(18), #builder-category-items li:nth-child(16)
, #builder-category-items li:nth-child(23)
, #builder-category-items li:nth-child(15)
, #builder-category-items li:nth-child(11)
, #builder-category-items li:nth-child(18)
, #builder-category-items li:nth-child(6)
, #builder-category-items li:nth-child(18)
, #builder-category-items li:nth-child(4)
, #builder-category-items li:nth-child(13)
, #builder-category-items li:nth-child(7)
, #builder-category-items li:nth-child(14)
, #builder-category-items li:nth-child(10)
, #builder-category-items li:nth-child(9)

, #masthead-nav a:nth-child(1)
, #masthead-nav a:nth-child(3)
, .masthead-link-separator

,#masthead-subnav
,#browse-side-column ol li:nth-child(1)
,#browse-side-column ol li:nth-child(4)
,#browse-side-column ol li:nth-child(5)
,#browse-side-column ol li:nth-child(6)
,#browse-side-column ol li:nth-child(8)
,#browse-side-column ol li:nth-child(9)
,#browse-side-column ol li:nth-child(10)
,#browse-side-column ol li:nth-child(7)
{
display:none;
}

#masthead-nav a:nth-child(5){
font-weight:bold!important;
}


}

@-moz-document domain("dailymotion.com") {

#related, .right_ads_column, .dmpi_video_itemrelated .dmpi_video_preview, .dmpi_video_relatedlist, .dmpi_masscast, .dm_page_html_home .dmco_image, #csl_right img, .csl_img_wrapper img, .dm_page_html_channels img, .dmco_menu.header_main_menu, #tab_featured_content img{
display:none!important;
}

.explicit{
color:red!important;
background-color:#000!important;
opacity:1!important;
}

.dmco_image, .dmco_image:hover, .featured_video a.preview_link{
opacity:1;
}








#content .dmpi_box.dmpi_subbox.videofeatured_box.column

, #footer_centered ul:nth-child(1) li:nth-child(6)
, .dmpi_factory_jukebox img

{
display:none!important;
}






}



@-moz-document url("http://www.dailymotion.com/fr") {

.box_content{
display:none!important;
}

}
