css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document url-prefix("http://social.bioware.com/forum") {


/* My edits */
body#page_body {
background: #ededed !important;
}
#topbar {background:none !important;}
#uberwrapper{background:none !important;}
#topmenu, .top_menu_side {background:none !important;}
div.top_menu_link_container, div.top_menu_link_container_end{border:none !important;}
#main_logo{padding-left:25px !important;}
#topbar{width:400px !important; height:auto !important; float:left !important;}
#topmenu{width:600px !important; float:right !important; margin-top: 32px !important;}

.SEPPosts td textarea, td form textarea { 
height:250px !important; 
color: #000 !important;
}

#userinfo_block, .badge, #sidebar, div .SEPForumInstanceHeader, .forum_rss_link, .topicItem .subline, .category .subline {
display : none !important;
}

#content {
padding:0px !important;
text-align:left  !important;
margin-top:0px !important;
margin-left:7px !important;
width:970px !important;
}

div.item_list.wide, div.browse_fields, .content_block, table.SEPForumCategoriesOverview .parentCategoryMiddleLeft, table.SEPPosts {
	background-color: none !important; /*header*/
	background-image: none !important;
	background-repeat: no-repeat !important;
}

div, td {
font-family:tahoma,"Trebuchet MS",arial,serif !important;
font-size:8pt !important;

}

div .roundbox_head > span, div .roundbox_head, div .roundbox_foot > span, div .roundbox_foot, div .content_headline > span, .parentCategoryTopMiddle, .parentCategoryTopRight, .parentCategoryTopLeft, .parentCategoryBottomMiddle, .parentCategoryBottomLeft, .parentCategoryBottomRight {
	background-color: none !important; /*header*/
	background-image: none !important;
}

div .SEPForumTextOutput {
font-family:tahoma,"Trebuchet MS",arial,serif !important;
font-size:10pt !important;

}

.title {
font-weight:normal!important;
color:#f47373;
font-size:9pt !important;
font-family: arial, serif !important;
}

t-able .clearTable .SEPForumAvatar {
display : none !important ;
}

.SEPForumPagination, .SEPForumPagination td {
	padding: 0px !important;
	margin-top: 0px !important;
	margin-right: 0px !important;
	margin-bottom: 3px !important;
	margin-left: 0px !important;
}
.forum_mainactions {
	padding: 0px !important;
	margin-top: 6px !important;
	margin-right: 0px !important;
	margin-bottom: 0px !important;
	margin-left: 0px !important;
}

.forum_breadcrumb{
border-bottom: none !important;
font-size: 9pt !important;
}

.forum_mainactions .button {
/*padding-top : 0 !important;*/
}

.small_photo {
	height: 30px !important;
	width: 30px !important;
margin-top: 1px !important;
}

div.small_photo{
background-image: none !important;
background: #000 !important;
width:32px !important;
height:32px !important;
padding:0px !important;
text-align:center;}


.topicItem td {
padding-top : 4px !important;
padding-bottom : 4px !important;
}


/*main table elements*/
.background1 td {background:#f5f5f5 !important;}
 .background2 td {background:#ededed !important;}
table.SEPForumTopicsOverview .background1 td {background:#f5f5f5 !important;}
table.SEPForumTopicsOverview .background2 td {background:#ededed !important;}
.topicItem {border:1px solid #fff important;}
table.SEPForumCategoriesOverview {
background: #f5f5f5 !important; border 1px #d3d3d3 !important;
}

/*puts the post topic and edit buttons on the top of the page*/
.action_bar {float: right !important;
position: absolute !important;
top: 18px !important;
right: 105px !important; margin:0 !important;}


/*puts the post topic and edit buttons on the top of the page*/
div.content .forum_mainactions{
position: absolute !important;
top: 18px !important;
right: 80px !important;
margin: 0 !important;
width:350px !important;}

div.content div.action_bar  .forum_mainactions{
position: static !important;
top: 18px !important;
 margin:0 !important;}
.warning{display:block !important; float:none !important;}



/*grey header for an individual post*/
.content_headline, .content_headline > span {
padding: 0 0 0 10px !important;
font-size: 1.2em !important;
height: 32px !important;
line-height: 32px !important;
font-weight: bold !important;
background: url(http://na.llnet.bioware.cdn.ea.com/u/f/eagames/bioware/social/images/slices/top_nav_bar_mid.png) !important;
}
.parentCategory{background: url(http://na.llnet.bioware.cdn.ea.com/u/f/eagames/bioware/social/images/slices/top_nav_bar_mid.png) !important;}

table.SEPForumCategoriesOverview .parentCategoryTopRight, table.SEPForumCategoriesOverview .parentCategoryTopLeft{background:none !important;}

/*links*/
.top_menu_item {color:#D09448 !important;}
.top_menu_item:hover {color:#7b889b !important;}
/*.topicTitle{color:#62738f !important;}*/

/*Posts*/
table.SEPPosts .background1{background-color:#f5f5f5 !important;}

table.SEPPosts td.post .postInfo {
border-bottom: 1px solid #d3d3d3 !important;
border-top:none !important;
border-width: 1px 0; background-color: transparent !important;}
div, td {
color: #656565 !important;
}
table.SEPPosts {
border: 1px solid #d3d3d3 !important;}
table.SEPPosts td.userInfo {background: none !important; border-bottom: 1px solid #d3d3d3 !important;}

table.SEPPosts td.post {
border-left: 1px dotted lightGrey !important;}

.post, table.SEPPosts td.post {border-bottom:1px solid #d3d3d3 !important;}
/*no signature*/
.forum_post_signature {display:none;}

.roundbox_head span, .roundbox_foot span, .roundbox_head, .roundbox_foot {background-color:#d3d3d3 !important; background-image:none !important;}
#bottom_of_footer {background-image:none !important;}
#ea_footer{background-color:transparent !important; border-top:1px solid #d3d3d3 !important; border-right:none !important; border-bottom:none !important; border-left:none !important;}

table.SEPForumCategoriesOverview .parentCategoryTopMiddle .subline {display:none !important;}

table.SEPForumCategoriesOverview .parentCategoryMiddleLeft {
background: transparent !important;
border-left: 1px solid #d3d3d3 !important;}
 table.SEPForumCategoriesOverview .parentCategoryMiddleRight{
background: transparent !important;
border-right: 1px solid #d3d3d3 !important;}

table.SEPForumCategoriesOverview .background2 .parentCategoryMiddleLeft, table.SEPForumCategoriesOverview .background2 .parentCategoryMiddleRight {background-color:#ededed !important;}

table.SEPForumTopicsOverview .topicItem td, table.SEPForumTopicsOverview .topicItem th, table.SEPForumCategoriesOverview .parentCategoryCaptions td, table.SEPForumCategoriesOverview .parentCategoryCaptions th{border-bottom:1px solid #d3d3d3 !important;}
/*footer*/
table.SEPForumCategoriesOverview .parentCategoryBottomLeft, table.SEPForumCategoriesOverview .parentCategoryBottomMiddle, table.SEPForumCategoriesOverview .parentCategoryBottomRight{background: #d3d3d3 !important; background-image: none! Important;}

table.SEPForumTopicsOverview .topicItem td table.cleartable td {border-bottom:none !important;}
table.SEPForumCategoriesOverview .parentCategoryCaptions .lastReply{background:none !important;}
.forum_category_spacer{background:#ededed !important;}

/*text*/

.SEPForumTextOutput {
width: 770px !important;
line-height: 18px !important;}

/*moderators*/
.sep_forumGlobalCatModerator{display: none !important;}
.moderators {color:#f5f5f5; font-size:1pt !important; border-bottom:none !important; height:1px !important;}

/*line*/
 .border {border-bottom:1px solid #d3d3d3 !important;}
table.SEPForumCategoriesOverview .parentCategoryMiddleRight, table.SEPForumCategoriesOverview .parentCategoryMiddleLeft {border-bottom:1px solid #d3d3d3;}

.action_bar {
float: right !important;
position: absolute !important;
top: 18px !important;
right: 75px !important;
margin: 0 !important;
width:350px !important;
}

/*header drop down*/
.menumatic a, #subMenusContainer a, .menumatic_submenu_cont a {
text-decoration: none !important;
display: block !important;
padding: 0px 0px !important;
border-bottom: 1px solid lightGrey !important;
border-top: 1px solid lightGrey !important;
background-color: whiteSmoke !important;}
.menumatic a:hover, #subMenusContainer a:hover, .menumatic_submenu_cont a:hover, .menumatic a:focus, #subMenusContainer a:focus, .menumatic_submenu_cont a:focus, a.mainMenuParentBtnFocused, .menumatic li a:hover, .menumatic li a:focus{background-color:#607391 !important; color:#ffffff !important;}
a.title {color:#61758e !important;}

.parentCategoryCaptions td:nth-child(3){width:65% !important;}
 .SEPForumAvatarThumb {
padding: 2px !important;
background: #61758E !important;
border: 1px solid lightGrey !important;
margin-bottom:5px !important;}

table.SEPPosts td.post .postInfo{border-bottom:1px solid #d3d3d3 !important; border-top:none !important;}

.title_word2 {
color: #AAA !important;
}
.title a:link, .title a:visited{color: #61758E !important;}
.parentCategory .title a:first-of-type{color:#fff !important;}

.moderators {display: none;}
forum_breadcrumb{
text-align: left; width:50% !important; float:left !important; position: relative !important; display:block !important; margin:15px 0px 15px 0px !important;}
.SEPForumPagination{
position: absolute !important;
width:50% !important;
top: 75px !important;
right: 20px !important;}


.userInfo a:link{color: #61758E !important;}

/*v0.3*/
}
