css:
@-moz-document domain('taringa.net'){
	#logo {
width: 210px;
height: 50px;
display: block;
float: left;
background: url(http://k05.kn3.net/680FA74F0.png) no-repeat top left transparent;
margin-top: 14px;
margin-right: 30px;
background-position: -5px -1px;
}
#logo:hover {
background-position: -5px -53px;
}
#header {
height: 80px;
background: url(http://k08.kn3.net/AFAC53357.png) repeat-x top center #371515 /*url(bgheader.gif) repeat-x top center */;
border-bottom: 1px solid #371515;
}
#search input, #search-bottom input {
width: 183px;
height: 15px;
margin: 0;
padding: 10px;
background: transparent url(http://k17.kn3.net/D57BAF78C.png) no-repeat top left;
border: none;
font-weight: normal;
color: #333;
font-family: Helvetica,Arial;
float: left;
font-size: 14px;
}
#search .btn_search, #search-bottom .btn_search {
display: block;
width: 32px;
height: 35px;
margin: 0;
padding: 0;
background: transparent url(http://k04.kn3.net/9450DC2BB.png) no-repeat top left;
float: right;
cursor: pointer;
}
#nav {
background: #431919 url(http://k08.kn3.net/5C4252504.png) repeat-x top left;
}
.navbar {
border-bottom: 1px solid #340E0E;
border-top: 1px solid #4E0A0A;
border-left: 1px solid #4E0A0A;
border-right: 1px solid #4E0A0A;
background: url(http://k09.kn3.net/0993C4A4C.png) repeat-x top left;
height: 33px;
position: absolute;
right: 245px;
top: 23px;
}
.navbar .navitem:hover {
cursor: pointer;
background: url(http://k11.kn3.net/4D7B1ECE1.png) repeat-x top right!important;
}
.navbar .navitem {
background: url(http://k02.kn3.net/01064EEA9.png) no-repeat top right;
}
.navbar .navitem.active, .navbar .navitem.active:hover {
cursor: pointer;
background: url(http://k13.kn3.net/CEDCE2391.png) repeat-x top right!important;
}
.navitem.useritem.hover div {
background: url(http://k13.kn3.net/CEDCE2391.png) repeat-x top right!important;
}
.navitem.useritem ul li:hover {
cursor: pointer;
background: url(http://k01.kn3.net/01C29524C.png) top repeat-x #5b2121!important;
}
.navitem.useritem ul li {
border: 1px solid #3B0707!important;
text-align:center;
border-top: none!important;
background: url(http://k01.kn3.net/01C29524C.png) top repeat-x #391515!important;
}
.navitem.useritem.hover div {
border-bottom: 1px solid #340E0E;
}
#nav ul li a {
background: #500000;
}
#nav ul li:hover a {
background: #722727;
}
#nav ul li.active a {
background: white;
color: black;
}
#show-paises-lista {
background: #500000;
}
#show-paises-lista:hover {
background: #722727;
}
/* SCRIPT CREADO POR MIGUEL ACUNA (MIGUEL_EX) */
}
