css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("247sports.com")

{

.boarditm_lst {
border-bottom: 1px solid #18453B !important; 
border-top: 2px solid #18453B !important; 
border-right: 3px solid #18453B !important; 
border-left: 3px solid #18453B !important;}  /* Adds a small solid line between each thread on the main page. */

[class="left m_rt10 m_bm10"], .signature > .fancybox {display: none !important;}   /*Removes pictures from signatures.  Leaves text.  */

.board_lst > li:nth-of-type(2n){ background-color: lightgrey;}  /* every other box is gray */

.message p {
font-family:Verdana,Arial,sans-serif !important; 
font-size:12px !important; 
line-height:120% !important;}  /* Changes font type in user message. */

.comment_lst > li {

border-bottom: 1px solid #18453B !important; 
border-top: 2px solid #18453B !important; 
border-right: 3px solid #18453B !important; 
border-left: 3px solid #18453B !important;} /* Adds a small solid line between each response in threads. */


/* block parts of the board that I find annoying */

.avatar_lst {display: none !important;}     /* Removes avatar picture */

.usrnav_blk { border-bottom: none !important;}  /* removes dotted border from bottom of nav bar */

.mergetoc_blk {display: none !important;}  /* Removes big box at top  "30 day free offer" */

.pg_ftr {display: none !important;}   /* Removes page footer  aka links at bottom*/

.brd_ftr {display: none !important;}  /* Removes board footer aka board legend at bottom*/

.sidebar {display: none !important;}  /* Removes entire sidebar */

.stars_itm {display: none !important;} /* Removes stars */

.ad728_wpr, .ad728_blk, .ad300_blk {display: none !important;}   /* removes some ad stuff */

.signup {display: none !important;}  /* removes 30 day offer from header next to user name */

.trial_blk {display: none !important;} /* Removes "join the conversation" box */

.social_blk, .social_lst {display: none !important;} /* Removes the social stuff in the nav bar */

/* BELOW :   not currently active, but didn't want to delete */
/* .usernav_blk {position: relative !important;}   makes top toolbar move with page; not stay on top at all times */

/*.pg_bdy {background: whitesmoke none !important;}   Background color */
}
