css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document url-prefix("http://gigazine.net/") {
  
  div#section > div.sbn,
  div#section > div#EndFooter,
  div#UFad
  {display: none !important;}
  
  body,
  header,
  aside#subtop
  {background: ghostwhite !important;}
  
  div#main{
    width: 90% !important;
    max-width: 1200px !important;
    margin: 10px auto !important;
  }
    
  div.thumb span{font-size: 1.0em !important;}
  div.date time,
  div.date span{
    font-size: 0.7em !important;
  }
  
  html *{
    font-family:'メイリオ','Hiragino Kaku Gothic Pro','ヒラギノ角ゴ Pro W3','ＭＳ Ｐゴシック','sans-serif' !important;
  }
  
}


@-moz-document url-prefix("http://gigazine.net/news/") {
  
  
  div#article > div.sbn,
  div#article > div#EndFooter,
  aside#latest,
  aside#sub
  {display: none !important;}
  
  
  h1.title{
    padding: 10px 10px !important;
  }
  
  a:hover{text-decoration: underline;}
  a:not([href^="http://gigazine.net"]){color:red !important;}
  
}
