css:
@namespace url(http://www.w3.org/1999/xhtml);





@-moz-document url-prefix('http://www.tumblr.com/'), url-prefix('https://www.tumblr.com/')   {









/* -- Left and Right column container -- */

#dashboard_index #container, #dashboard_edit_post #container, #dashboard_submissions #container, #dashboard_drafts #container, #dashboard_post_queue #container, #dashboard_followers #container, #dashboard_members #container, #dashboard_likes #container, #dashboard_following #container, #main_tag #container, #main_tag_beta #container {

width: 865px !important;

margin-top: auto !important;

margin-right: 0px !important;

margin-bottom: auto !important;

padding-top: 0pt;

padding-bottom: 0pt;

}





#dashboard_index, #dashboard_edit_post, #dashboard_submissions, #dashboard_drafts, #dashboard_post_queue, #dashboard_followers, #dashboard_members, #dashboard_likes, #dashboard_following, #main_tag, #main_tag_beta {

background-image:url('http://i56.tinypic.com/3145cpg.jpg') !important;

background-color: #020202 !important;

background-repeat: no-repeat !important;

background-attachment: fixed !important;

background-position: top left !important;

}





/* -- BG Opacity -- */

#dashboard_index div.l-content, #dashboard_submissions div.l-content, #dashboard_drafts div.l-content, #dashboard_post_queue div.l-content, #dashboard_members div.l-content, #dashboard_likes div.l-content, #main_tag div.l-content, #main_tag_beta #left_column, #right_column {background-color: transparent !important;}

div.l-content.l-content.l-content {

background: rgba(10,10,10,0.0) !important;

}





.tag_page_header {

width: 865px !important;

height: 55px;

}





#content_top, #content_bottom  {

width: 865px !important;

height: 20px !important;

background: transparent !important;

display: none;

}





#edit_post_controls .advanced_option .inline_input_label {

display: none;

}



#edit_post_controls .advanced_option select, #edit_post_controls .advanced_option input, #edit_post_controls .advanced_option textarea {

color: #444444;

padding-top: 5px;

padding-right: 5px !important;

padding-bottom: 5px;

padding-left: 5px;

margin-top: 10px !important;

}





#edit_post_controls .advanced_option {

padding-top: 15px !important;

padding-right: 0pt;

padding-bottom: 15px;

padding-left: 0pt;

}





#tag_editor {

width: 180px !important;

height: 70px;

}





ol#posts li.post.new_post {

background: #0B0015 url('http://i55.tinypic.com/2mzi1k9.png') top center no-repeat !important;

}





/* -- 'Text' 'Photo' 'Quote' etc Text -- */

.new_post_label {

font-size: 14px !important;

padding-left: 1px !important;

font-family: Futura, sans-serif !important;

font-style: bold;

text-transform: uppercase !important;

color: #660066!important;

text-shadow: 0px 1px 1px #EFD49A;

letter-spacing: 1px !important

}









/* -- Entire sidebar -- */

#right_column {

float: right !important;

width: 180px !important;

color: #fefefe !important;

}







#right_column .dashboard_nav_item {

border-bottom: 0px solid #eee !important;

background-image: none  !important;

}









/* -- Sidebar text colour -- */

#right_column .dashboard_nav_item .dashboard_nav_title, #right_column .dashboard_nav_item .dashboard_nav_title a, #right_column .dashboard_nav_item a, #right_column .dashboard_nav_item ul.dashboard_subpages, #right_column .dashboard_nav_item ul.dashboard_subpages li a {

color: #fefefe !important;

}









/* -- Name of Blog -- */

#right_column a#dashboard_switch_blog_menu_current {

position: relative;

display: block;

height: 25px !important;

width: 190px;

padding-top: 5px;

padding-right: 5px;

padding-bottom: 3px;

padding-left: 10px;

margin-top: 5px;

margin-right: auto;

margin-bottom: 0pt;

margin-left: auto;

font-size: 13px !important;

color: #921169 !important;

}







/* -- Tracked tag X -- */

#right_column .dashboard_nav_item ul.dashboard_subpages li a.tracked_tag_control {

color: #921169 !important;

}







/* -- Radar Pic box -- */

#right_column .dashboard_nav_item .dashboard_controls_radar_media {

overflow-x: hidden;

overflow-y: hidden;

width: 180px !important;

}



/* -- Radar Pic box inner -- */

#right_column .dashboard_nav_item .dashboard_controls_radar_media.photo, #right_column .dashboard_nav_item .dashboard_controls_radar_media.photoset, #right_column .dashboard_nav_item .dashboard_controls_radar_media.video {

background-color:  #921169 !important;

background-image: none;

background-repeat: repeat;

background-attachment: scroll;

background-position: 0% 0%;

padding-top: 5px;

margin-right: 10px !important;

padding-bottom: 5px;

padding-left: 5px;

}



/* -- Radar Pic -- */

#right_column .dashboard_nav_item .dashboard_controls_radar_media .post img {

max-width: 180px !important;

}







#right_column .dashboard_nav_item #dashboard_controls_radar_media_fold {

background: none !important;

}







#left_column {

float: left;

width: 620px !important;

}







#left_column #posts .notification .avatar {

border-color: transparent !important;

background-color: #ececec !important;

opacity:0.4 !important;

}







.answer_container {

background-color: #9E7BF!important;

margin-left: 20px;

margin-right: 20px;

padding-top: 15px;

padding-right: 15px;

padding-bottom: 15px;

padding-left: 15px;

color: #444444 !important;

font-family: Arial,Helvetica;

font-style: normal;

font-variant: normal;

font-weight: bold;

font-size: 13px;

}











#posts .post .post_question {

background-color: #E6A9EC !important;

color: #444444 !important;

}







#left_column #posts .notification .avatar:hover {

opacity:0.6 !important;

}









/* -- Post text -- */

.post {

opacity:1 !important;

border-bottom:0px !important;

color: #222222 !important

}







.post:hover {

opacity:1 !important

}











.permalink {

background-color: transparent !important;

}







/* -- 'Following' BG -- */

.active {

background-color:  #1A032F !important;

}



#right_column .dashboard_nav_item a {

color: #fefefe !important;

}





.nav_item_nipple {

display: none !important

}







.nav_menu {

background-color: #b93f27 !important;

border-bottom: 0px !important

}









/* -- 'Dashboard' BG -- */

#nav .nav_item.active, #nav .nav_item, #nav .nav_item .nav_menu, #nav .nav_item .nav_item_nipple .nav_item_nipple_pixel {

background-color: #1A032F !important;

background-image: none !important;

color: #CA89A1 !important;

border: 0px !important

}







#nav .nav_item .nav_menu {

border-bottom: 0px !important

}







#nav .nav_item .nav_menu a {

color: #a77ca4 !important;

border-top-color: #ac9c82 !important;

}







#nav .nav_item .nav_menu a:hover {

color: #fbfbfb !important;

}









.dashboard_switch_blog_menu_header {

background-color: #ececec !important;

color: #666 !important;

}











.dashboard_switch_blog_menu_item {

background-color: #ececec !important;

color: #666 !important;

border: 0px !important

}









.dashboard_switch_blog_menu_footer {

background-color: #ececec !important;

color: #666666 !important;

}







.dashboard_switch_blog_menu_wrapper {

background-color: #ececec !important;

}









#new_post_notice_container {

color: #ffffff !important

}







#new_post_notice_container a {

color: #ffffff !important

}









#dashboard_switch_blog_menu_current, #dashboard_switch_blog_arrow_and_divider, #dashboard_switch_blog_arrow_current {

background-image: none !important

}







/* -- Top links 'Back to first page' 'Page 2' etc -- */

#dashboard_controls {

font-size: 16px !important;

line-height: 1 !important;

height: 35px !important;

color: #ffffff !important;

margin-bottom: 2px !important;

}



#dashboard_controls a, #dashboard_controls .control {

color: #fefefe !important;

text-decoration: none !important;

}



#dashboard_controls a:hover {

color: #995B8D !important;

}







#footer {

background: rgba(10,10,10,0.0) !important;

color: #e6ffff !important;

background-image: none !important;

}



#footer a {

color: #e6ffff !important;

}





/* -- 'Previous' 'Next' links -- */

#pagination a {

color: #995B8D !important;

text-decoration:none;

background-color: #ffffff !important;

padding:5px 8px;

margin-left:10px;

white-space:nowrap

}



/* -- 'Previous' 'Next' links -- */

#pagination a:hover {

color: #111111 !important

}





img[src*="http://assets.tumblr.com/images/new_post_icons_no_labels.png"] {

width:0;

height:70px;

padding-right:250px;

opacity: 0;

}











/* -- 'replied to' 'reblogged' etc BG -- */

#left_column #posts .notification {

background-color:#995B8D !important;

color: #444444 !important;

border-bottom-color: #fefefe !important

}



/* -- Alternate 'replied to' 'reblogged' etc BG -- */

#left_column #posts .notification.alt {

background-color: #995B8D !important;

border-bottom-color: #fefefe !important

}



/* -- 'replied to' 'reblogged' etc LINKS -- */

#left_column #posts .notification a {

color: #222222 !important;

border-bottom:0px !important;

}



/* -- 'replied to' 'reblogged' etc Added reply text -- */

#left_column #posts .notification blockquote {

border-left-color: #995B8D !important;

color: #333333 !important;

}





#left_column #posts .notification .block {

color: #ac9c82 !important;

}







#left_column #posts .notification.last_notification, #left_column #posts .notification.single_notification {

border-bottom-color: #98a0b2 !important;

}







#left_column #posts .notification .avatar {

border-color:transparent !important;

background-color: #fbfbfb !important;

opacity:0.5 !important;

}





#left_column #posts .notification .avatar:hover {

opacity:1 !important;

}





#posts .notification .notification_type_icon {

position: absolute;

right: 11px;

top: 10px;



}



#posts .notification .notification_type_icon.answer_icon {

width: 10px;

height: 16px;

}



.notification_type_icon {

}



#posts .notification .notification_type_icon.like_icon {

display: none !important;

}





#posts .notification a {



}



#posts .notification .report, #posts .notification a.block {



}



#posts .notification:hover a.block {

display: none !important;

}



h1,h2,h3,h4,h5,h6 {

margin-top: 5px !important;

color: #444444 !important

}





/* -- 'Post date' & 'Content source' text -- */

#edit_post_controls .advanced_option .inline_input_label {

margin-top: 10px;

}



#edit_post_controls .advanced_option .source_url_questionmark_gradient, #edit_post_controls .source_url_questionmark {

margin-top: 9px;

}





#right_column a#dashboard_controls_cumulative_tumblelog_unread_count .unread_count_arrow {

    background-image: url("http://img24.imageshack.us/img24/6262/dashboardmasterspriteco.png") !important;

}

#right_column .dashboard_nav_item .icon {

background-image: url("http://img24.imageshack.us/img24/6262/dashboardmasterspriteco.png") !important;

}



#nav .nav_item div .nav_item_menu_arrow {

background-image: url("http://img24.imageshack.us/img24/6262/dashboardmasterspriteco.png") !important;

}



#right_column a#dashboard_controls_cumulative_tumblelog_unread_count .unread_count_arrow {

background-image: url("http://img24.imageshack.us/img24/6262/dashboardmasterspriteco.png") !important;

}







#posts .notification .notification_type_icon {

background-image: url("http://img24.imageshack.us/img24/6262/dashboardmasterspriteco.png") !important;

}

}
