css:
@namespace url(http://www.w3.org/1999/xhtml);
@-moz-document url-prefix("https://www.google.com/calendar/"), url-prefix("https://www.google.co.jp/calendar/") {

/*-- 全体・ヘッダー --*/
#calcontent{ /* 全体カレンダー周り空白 */
margin: 8px 0px 0px 0px !important;
}

#vr-header{ /* Google 検索 表示　※これを削除すると検索窓が表示される*/
display:none !important;
}

#mainnav{ /*  検索の行 ヘッダ余白 */
height:22px !important;
}

.applogo{ /*検索の行 文字位置*/
line-height: 20px;
}

#mainlogo{ /* カレンダーロゴ */
font-size: 13px !important;
font-weight: bold !important;
margin: 0 0 0 -10px !important;
color: #ffffff !important;
letter-spacing: 0.2em !important;
}

#topLeftNavigation{ /* 今日,＞,＜ボタン位置 */
left: 171px !important; 
}

.navbutton {/* ＞,＜ボタン文字位置 */
margin-top: 2px !important; /*5*/
}

/* -- サイドバー --*/

#nav{ /* 左側サイドバー */
margin: 0 25px 20px 20px !important; 
width: 130px !important; 
}

#sidebar{ /* 作成ボタン周り */
padding: 10px 0px 5px 0px !important; 
}

.goog-imageless-button, .navbuttonouter{ 
height:21px !important; 
line-height:21px !important; 
}

.calHeader, #dp_0_cur { /* カレンダータイトル */
line-height:28px !important; 
}

.calListRow{ /* カレンダーリスト */
height:18px !important; 
}

.sn-link-container{ /*カレンダーAdd setting*/
padding: 5px 0 0 0 !important;
}


/* -- カレンダー部分 --*/

#mainbody{ /* メインカレンダー表示部分 */
padding-top: 3px !important;
margin-left: 172px !important; 
}

.mv-dayname{ /* カレンダー曜日文字位置 */
padding: 0 0 0 8px !important; 
font-size: 11px !important; 
}

.mv-event-container{ /* カレンダー表示部分 枠 */
top: 15px !important; 
}

.st-dtitle{ /* カレンダー数字 */
line-height: 15px !important; 
padding: 3px 5px 2px 4px !important; 
}

.st-c .te, .st-c .rb-n { /*予定部分*/
height: 15px !important; 
line-height: 15px !important; 
padding: 0 3px 0 7px !important; 
color: #333333 !important;
}

.st-c { /*予定部分 文字*/
padding: 0 !important; 
font-size: 10px !important;
}

.st-ad-ml, .st-ad-mr{ /* 複数日予定 */
top:5px !important; 
}

.st-ad-ml{ /* 複数日予定 左矢印*/
margin-left: -5px !important;
}

/*-- TODOリスト部分 --*/

#gadgetcell{/*TODOリスト右余白 スクロールあるとき*/
margin-right: 5px !important;/*44*/
}

.eui-t #gadgetcell {/*TODOリスト右余白 スクロールあるとき*/
margin-right: 0px !important;/*16*/
}

.eui-s #gadgetcell {/*TODOリスト右余白 みじかいとき*/
margin-right: 0px !important;/*28*/
}

#rhstogglecell.rhstogglecell-open {
right: 180px  !important; /*206px*/
}

.eui-t #rhstogglecell.rhstogglecell-open {
right: 176px  !important; /*178*/
}

.eui-s #rhstogglecell.rhstogglecell-open {
right: 180px  !important; /*190*/
}

#gridcontainer {/*カレンダー右余白*/
margin-right: 5px !important;/*44*/
}

.eui-t #gridcontainer {/*カレンダー右余白*/
margin-right: 5px !important;/*16*/
}

.eui-s #gridcontainer {/*カレンダー右余白*/
margin-right: 5px !important;/*28*/
}



.sng-title-bar {/*リスト*/
padding: 2px 3px 2px 0px !important;/*7px 8px 7px 5px*/
font-size: 12px !important; /*14*/
}

.k {
font-size: 8px !important; /*10*/
}

.y, .z {
font-size: 8pt !important; /*10*/
}


}
