@namespace url(http://www.mozilla.org/keymaster/gatekeeper/there.is.only.xul);

/* Made by seaweed !Ponyo.2wFE
   Do whatever you want with it! 
*/
#identity-box { display:none; }
searchbar#searchbar textbox, textbox#urlbar{
 background-color: 	transparent!important;

    opacity: 0.7 !important; 
     color: #ff52cb !important;
    }


#webdeveloper-toolbar{

     background-color: transparent!important;
     border-style: none!important;
     color:#FFFFFF!important;
    }
	

#PersonalToolbar{

	 background-color: transparent!important;
	 color: #81F0F6 !important;
	}

#TabsToolbar, #browser-bottombox, #status-bar, #status-bar > *, #nav-bar, #PersonalToolbar, #addon-bar,#personal-bookmarks {
 
	 background: transparent !important;
	 color: #ffffff !important;
	-moz-appearance: none !important;
	 border: #000000 !important;
	}
	
.tabs-newtab-button {
background: none !important;
border: none !important;
}

	
.chromeclass-directories *, #bookmarksPanel * {
         color: #ccc !important;  
}
.tabbrowser-tab {

	 background: transparent !important;
	 color: #ffffff !important;
	 text-shadow: #000000 0px 0px 2px !important;
height: 25px !important;

	 border: transparent 1px solid !important;
-moz-border-radius: 4px !important;
margin-bottom: 0px !important;


	}
	
.tabbrowser-tab:not([pinned="true"]){

	 margin-left: 1px !important;
	 margin-right: 1px !important;
         color: #ffffff !important;
	}

.tabbrowser-tab[selected="true"]{
	 background: transparent !important;
	 color: #ffffff !important;
         text-shadow: #000000 0px 0px 2px !important;
	-moz-box-shadow: inset 0px 0px 30px 0px #81F0F6 !important;
-moz-border-radius: 3px !important;
	}
	.tabbrowser-tab:hover:not[selected="true"]{
	background: transparent !important;
	
	text-shadow: #555444 0px 0px 2px !important;
	-moz-box-shadow: inset 0px 0px 40px 0px rgba(255,255,255,.5) !important; 
	}

 #nav-bar {
 
	 background: transparent !important;

	}


	

@namespace url(http://www.mozilla.org/keymaster/gatekeeper/there.is.only.xul);

#urlbar {
margin-top: none !important;
}

#urlbar,
.searchbar-textbox {

  font-size: 1.05em !important;

}






#navigator-toolbox[tabsontop=false] #urlbar:hover,
#navigator-toolbox[tabsontop=false] .searchbar-textbox:hover,
#urlbar:-moz-lwtheme:hover,
.searchbar-textbox:-moz-lwtheme:hover {
  background-color: rgba(255,255,255,.898);
}

#navigator-toolbox[tabsontop=false] #urlbar[focused="true"],
#navigator-toolbox[tabsontop=false] .searchbar-textbox[focused="true"],
#urlbar[focused="true"]:-moz-lwtheme,
.searchbar-textbox[focused="true"]:-moz-lwtheme {
  background-color: white;
}

#navigator-toolbox[iconsize="small"][mode="icons"] #urlbar,
#navigator-toolbox[iconsize="small"][mode="icons"] .searchbar-textbox,
#addon-bar #urlbar,
#addon-bar .searchbar-textbox {
  height: 25px !important;
}

#urlbar-container {
  -moz-box-orient: horizontal;
  -moz-box-align: stretch;
}

.urlbar-textbox-container {
  -moz-box-align: stretch;
}

#urlbar-icons {
  height: 18px;
  -moz-box-align: center;
}

.urlbar-icon {
  padding: 0 1px !important;
}

.urlbar-icon:-moz-system-metric(touch-enabled) {
  -moz-margin-end: 1px !important;
  padding: 0 3px !important;
}

#urlbar-search-splitter {
  min-width: 4px;
  -moz-margin-start: -3px;
  border-style: none !important;
  background:  transparent !important;
}

#urlbar-search-splitter + #urlbar-container > #urlbar ,
#urlbar-search-splitter + #search-container > #searchbar > .searchbar-textbox {
  -moz-margin-start: 0;
}

#urlbar-display-box {
  margin-top: -2px;
  margin-bottom: -2px;
  -moz-border-end: 1px solid #AAA;
  -moz-margin-end: 3px;
}
