css:
@-moz-document domain('boards.4chan.org') 
{
body
{
background: #0E0E0E url(data:image/gif;base64,R0lGODlhGAAMAJEAABkZGRoaGg4ODg8PDyH5BAAAAAAALAAAAAAYAAwAAAJG3DBipxtrXjozJiUQlCqIbzWU0ZFQ84iSAw0e2q4GAgrvHC+luul25RK5erye0QLRsETKViK10MB0zVWJqhuSAKBnDIMsAAA7) repeat 0 0!important;
color:#FFFFFF!important;
}
.reply,.postblock,.pages
{
background-color:rgba(255,255,255,0.1)!important;
border:1px solid rgba(255,255,255,0.1)!important;
border-radius: 2px;
color:#FFFFFF!important;
}
.replyhl
{
background-color:rgba(255,255,255,0.2)!important;
border:1px solid rgba(255,255,255,0.2)!important;
border-radius: 2px;
color:#FFFFFF!important;
}
img#recaptcha_reload
{
height: 0 !important;
width: 0 !important;
padding-left: 25px !important;
padding-top: 18px !important;
background: url(data:image/png;base64,) no-repeat !important;
position:relative!important;
right:40px!important
}
img#recaptcha_switch_audio
{
height: 0 !important;
width: 0 !important;
padding-left: 25px !important;
padding-top: 15px !important;
background: url(data:image/png;base64,) no-repeat !important;
position:relative!important;
right:40px!important
}
img#recaptcha_switch_img
{
height: 0 !important;
width: 0 !important;
padding-left: 25px !important;
padding-top: 15px !important;
background: url(data:image/png;base64,) no-repeat !important;
position:relative!important;
right:40px!important
}
img#recaptcha_whatsthis
{
height: 0 !important;
width: 0 !important;
padding-left: 25px !important;
padding-top: 16px !important;
background: url(data:image/png;base64,) no-repeat !important;
position:relative!important;
right:40px!important
}
textarea,input[type]
{
background-color:rgba(255,255,255,0.1)!important;
border:1px solid rgba(255,255,255,0.1)!important;
border-radius: 2px!important;
color:#FFFFFF!important;
-moz-appearance:none;
}
#com_submit
{
margin-bottom:0px!important;
width:48px;
height:23px;
padding-bottom:1px;
position:relative!important;
right:1px;
top:0px
}
input[type="button"]
{
margin-left:1px!important;
}
b,font[size],td,span.omittedposts,#navtop,#navbot
{
color:#FFFFFF!important;
}
a
{
color:#FFFFFF!important;
-moz-transition: text-shadow 0.5s;
-webkit-transition: text-shadow 0.5s;
text-decoration:none!important
}
a:hover
{
text-shadow:0px 0px 1px #FFFFFF;
}
.replytitle,.commentpostername,.postertrip
{
background:none!important;
}
hr,[bgcolor="#e04000"],img[style="border:1px solid black;"],img#recaptcha_tagline,img#recaptcha_logo,.rules
{
display:none!important;
}
}
