css:
@-moz-document domain(tumblr.com) {
#logo {height: 0 !important;
	width: 0 !important;
	padding-left: 240px !important;
	padding-top: 70px !important;
	background: url(http://img835.imageshack.us/img835/5735/bellelogo.png) no-repeat !important;}

ol#posts li.notification {

  background-color: #F8CECF !important;

  border-top: 1px solid #6E667E !important;

  border-left: 1px solid #6E667E !important;

  border-bottom: 1px solid #6E667E !important;

  border-right: 1px solid #6E667E !important;

  color: #B1A4CF !important;

  text-decoration: none;

}

.answer_container {

  background-color: #FFE9EA !important;

}


body {

background: #6E667E url('http://img402.imageshack.us/img402/6813/bellbg.png');
background-position: center;
background-attachment:fixed;
background-repeat:repeat;


}

#posts .notification a:not(.avatar_frame) {

color: rgba(213, 118, 118, 1);

font-weight: bold;

text-decoration: none;

border-bottom: 0px solid #ffffff;

-webkit-background-clip: padding-box;

}



#posts .notification blockquote {

  border-left: solid 3px #6E667E;

}



#posts .notification .avatar_frame {

background-color: #ffffff;

border: 1px solid #ffffff;

position: absolute;

top: -1px;

left: -55px;

overflow: hidden;

display: inline-block;

}












.post.is_mine.with_avatar.new_post {

  color: #ffffff;

  font-family:courier, monospace;

  background: rgba(0,0,0,1) !important; 

}




img[src*="http://assets.tumblr.com/images/new_post_icons_no_labels.png"]{

opacity:0.00 !important;  

}


#new_post{

  background: #FFFFFF url(http://img3.imageshack.us/img3/1434/posticonsh.png) no-repeat center !important;

}


#posts .post.new_post .new_post_label {

-webkit-font-smoothing: antialiased;

opacity: 1;

font-size: 14px;

font-weight: normal;

color: #E78383;

text-decoration: none;

text-transform: uppercase;

text-align: center;

width: 74px;

position: absolute;

}



#left_column, #right_column {background-color: transparent !important;}

div.l-content.l-content.l-content {

  background: rgba(124, 109, 155, .9) !important; 

}







.selection_nipple:not([class="selection_nipple white"]) {

border-bottom: solid 11px rgba(00,00,00, 0.50)  !important;

}



#content_top {

  width: 0px !important;

  height: 0px !important;




}

.selection_nipple.white, .selection_nipple:not([class="selection_nipple white"]) {

background: none !important;

width: 0px !important;

height: 0px !important;

margin-top: 0px !important;

border-bottom: solid 11px rgba(00,00,00, 0.50) !important;

border-right: 11px solid transparent !important;

border-left: 11px solid transparent !important;

}




#content_bottom {

  width: 0px !important;

  height: 0px !important;









}





#left_column, #right_column {background-color: transparent !important;}

div.l-content.l-content.l-content {

  padding-top: 25px !important; 

  padding-bottom: 25px !important; 

   -moz-border-radius: 20px !important; 

}





LABEL {

  color: rgba(0,0,0, 1.0) !important; 

}




#footer {

  display: none !important; 

}



.radar {

  display: none !important; 

}


.selected .following,

.selected .followers,

.selected .settings,

.selected .posts,

.selected .messages,

.selected .drafts,

.selected .queue,

.selected .likes  {

  color: rgba(0, 0, 0, 0.50) !important; 

  background: rgba(0, 0, 0, 0.0) !important; 

}





#new_post .arrow {

  opacity: 0.50 !important;

  margin-left: -1px !important; 

}

}
