@-moz-document url-prefix(http://www.google), 



url-prefix(http://google), 



url-prefix(http://images.google), 



url-prefix(http://blogsearch.google){







body {



background-image: url('http://i51.tinypic.com/a1hn6h.png') !important;



background-attachment: fixed !important; 



color: #FFF !important;



font-family: Tahoma, Arial, Helvetica, sans-serif !important;



margin: 1% 1.5% !important;



}







/* defaults for all links */



a:link { color: #DF0101!important; }



a:visited { color: #DF0101 !important; }



a:hover{ color: #ffffff !important; }







/* change color of lots of text */



font, div, td { color: #fff !important; }







/* make sure web forms (and google suggest) don't get their colors changed */



input *, textarea *, .aAutoComplete { background-color: -moz-Field !important; color: -moz-fieldtext !important; }







/* Box that pops up when you click the "more >>" link */



span#more { background: #FF0000 !important; }







/* superscripted "New!" text */



sup font[color="red"] { color: #0f0 !important; }







/* barre du haut 



--------------------*/







/*  HEADER BAR - REMOVE UNDERLINES */



#gbar a, #gbar a:active, #gbar a:visited, #gbar .gb3 > a > u, span[id*="1fb"], a[id*="1fb"], #undefined b {



text-decoration: none !important;



}







/* HEADER BAR - "GMAIL" TITLE */



#gbar .gb1 b {



color: #017bff !important;



font-size: 12px !important;



margin: 0 0 0 0 !important;



}







/* HEADER BAR - MORE DROP-DOWN MENU */



#gbi{



background: #f1f1f1 !important;



border: 1px solid BLACK !important;



position: absolute !important;



top: 20px !important;



width: 100px !important;



z-index: 1000 !important;



}



    



/* HEADER BAR - MORE DROP-DOWN MENU LINK WRAPPER */



.gb2 {



width: 100px !important;



z-index: 1001;



}







.gb2 div{



margin: 0 !important;



}



		



/* HEADER BAR - MORE DROP-DOWN MENU LINKS */



.gb2 a{



color: BLACK !important;



display: block !important;



font-size: 11px !important;



color: BLACK !important;



padding: .2em .5em !important;



text-decoration: none !important;



}



    



/* HEADER BAR - MORE DROP-DOWN MENU LINKS MOUSE-OVER */



#gbar .gb2 a:hover{



background: #B52501 !important;



color: WHITE !important;



display: block !important;



}







/* HEADER BAR - MENU LINKS (RIGHT) */



.nQ6QTe .l73JSe {



color: WHITE !important;



display: inline !important;



font-size: 11px !important;font-weight: normal !important;



height: 26px !important;



margin: 0 0 0 0 !important;



padding: 0 2px !important;



text-decoration: none !important;



}







/* HEADER BAR - MENU LINKS (RIGHT) - DIVIDER */



.nQ6QTe {	color: #FE6702 !important;



height: 26px !important;



font-size: 11px !important;



line-height: 26px !important;



margin: 0 !important;



padding: 0 12px 0 0 !important;



}







/* HEADER BAR - MAIL ADDRESS */



.nQ6QTe b {



color: #c0c0c0 !important;



font-weight: bold !important;



}







/* HEADER BAR - ADD HOVER EFFECT - RIGHT MENU ITEMS */



.nQ6QTe span:hover.l73JSe,



.nQ6QTe a:hover.l73JSe {



border-bottom: 1px dotted #FE6702 !important;



color: #DDDDDD !important;



}







/* HEADER BAR - "SIGN OUT" LINK */



.nQ6QTe > nobr > a:last-child {



font-weight: bold !important;



color: WHITE !important;



}











/* HEADER BAR - REMOVE BLUE LINE AT TOP OF PAGE */



.gbh {



display: none !important;



}







}







