css:
@namespace url(http://www.mozilla.org/keymaster/gatekeeper/there.is.only.xul);

#identity-icon-labels {
  margin-top: -2px !important;
  margin-bottom: -2px !important;
}

#main-window:not([stylish-url^="chrome://"]) #page-proxy-favicon {
  -moz-image-region: rect(0, 16px, 16px, 0) !important;
  opacity: 1 !important;
}

#main-window:not([stylish-url^="chrome://"]) #identity-box:hover #page-proxy-favicon {
  opacity: .75 !important;
}

#urlbar[pageproxystate="valid"] > #identity-box.verifiedIdentity {
  background: rgba(161,218,161,.25) !important;
  -moz-border-end: 1px solid rgba(0,0,0,.1) !important;
  color: hsl(92,100%,25%) !important;
}

/* image */

#identity-box.unknownIdentity #page-proxy-favicon {
  list-style-image: url(data:image/png;base64,) !important;
}

#identity-box.verifiedIdentity #page-proxy-favicon {
  list-style-image: url(data:image/png;base64,) !important;
}

#identity-box.verifiedDomain #page-proxy-favicon {
  list-style-image: url(data:image/png;base64,) !important;
}

#identity-box.mixedContent #page-proxy-favicon {
  list-style-image: url(data:image/png;base64,) !important;
}

#main-window[stylish-url^="about:"] #page-proxy-favicon {
  list-style-image: url('chrome://branding/content/icon16.png') !important;
}

#main-window[stylish-url^="unmht:///"] #page-proxy-favicon {
  list-style-image: url("chrome://unmht/content/icon_small.png") !important;
}

#main-window[stylish-url^="file:///"] #page-proxy-favicon {
  list-style-image: url(data:image/png;base64,) !important;
}

#main-window[stylish-url^="ftp://"] #page-proxy-favicon {
  list-style-image: url(data:image/png;base64,) !important;
}

#main-window #urlbar[pageproxystate="invalid"] #page-proxy-favicon {
  list-style-image: url(data:image/png;base64,) !important;
}
