@-moz-document domain("twitter.com") {
* {

font-family: "lucida grande", tahoma, arial, sans-serif !important;
font-size: 12px;}

.global-nav .search-input {font-size: 12px;}

.typeahead-category-title {
font-size: 12px;}
.global-nav .current-user .fullname {
