    font-size: 12px;
}
.global-nav {
    border-bottom: 0px solid rgba(0, 0, 0, 0.07);
    height: 46px;
    position: relative;
    width: 100%;
}.top-timeline-tweetbox .tweet-user {
    border-radius: 0px;
    position: relative;
}.home-tweet-box, .top-timeline-tweetbox .timeline-tweet-box {
    border-color: #E2E2E2 !important;
}
.top-timeline-tweetbox .timeline-tweet-box {
    border-radius: 0px 0px 0 0;
    border: 1px solid #e1e8ed;
}
.stream, .stream-container, .stream-items {
    position: relative;
    margin-left: 0;
    list-style: none;
    margin-top: 8px;
}
.TweetTextSize {
    font-size: 12px;

}
.btn-link.translate-button {

    font-size: 12px;

}
.global-nav .search-input {
    font-size: 12px;
}
span.text.geo-status {
    font-size: 12px;
}
.tweet-counter {
    background-color: transparent;
    color: #8899a6;
    display: inline-block;
    width: 35px;
    border: 0;
    padding: 0 3px;
    position: relative;
    vertical-align: top;
    top: 7px;
    text-align: right;
    font-size: 11px;
    text-shadow: 0 1px 1px rgba(255,255,255,.75);
}.dropdown-menu li>a, .dropdown-menu .dropdown-link {
    clear: both;
    color: #66757f;
    cursor: pointer;
    display: block;
    font-size: 12px;
    font-weight: normal;
    float: none;
    line-height: 18px;
    margin: 0;
    padding: 5px 20px;
    position: relative;
    text-shadow: none;
    white-space: nowrap;
}.modal-header .modal-title {
    width: 75%;
    margin: 0 auto;
    overflow: hidden;
    font-size: 12px;
    font-weight: normal;
    line-height: 22px;
    color: #8899a6;
    text-align: center;
    text-overflow: ellipsis;
    white-space: nowrap;
}


.ProfileNav-value {
    font-size: 18px;
    padding-top: 6px;
    font-weight: 500;
    transition: color .15s ease-in-out;
}

.ProfileHeading-toggleItem {
    display: inline-block;
    color: #ccd6dd;
    font-size: 12px;
    font-weight: 300;
    line-height: 1;
    padding: 15px 15px 12px;
}
.TweetTextSize.TweetTextSize--26px {
    font-size: 12px;
    line-height: 32px;
    font-weight: 300;
    letter-spacing: .01em;
}
.TweetTextSize.TweetTextSize--16px {
    font-size: 12px;
    line-height: 22px;
    font-weight: 400;
}
.WhoToFollow-title {
    color: #66757f;
    display: inline-block;
    font-size: 11px;
    font-weight: 300;
    line-height: 1;
}
.WhoToFollow-users .fullname {
    color: #292f33;
    font-size: 12px;
}
.ProfileHeaderCard-nameLink {
    font-size: 11px;
    font-weight: 700;
    line-height: 1;
    word-wrap: break-word;
}.ProfileHeaderCard-bio {
    font-size: 12px;
    font-weight: 400;
    line-height: 20px;
    margin-bottom: 10px;
    word-wrap: break-word;
}.ProfileHeaderCard {
    margin-top: 25px;
}.ProfileHeaderCard-joinDate {
    margin-top: -7px;
}.ProfileNav-value {
    font-size: 12px;
    padding-top: 8px;
    font-weight: 500;
    transition: color .15s ease-in-out;
}






.with-icn {
    font-size: 12px;
    color: #8899a6;
}

.new-tweets-bar {
    font-size: 12px;
    display: block;
    padding: 10px 1px;
    cursor: pointer;
    text-align: center;
    font-weight: normal;
    background-color: #f5f8fa;
    border-top: 1px solid #e1e8ed;
    border-bottom: 1px solid #e1e8ed;
    z-index: 2;
}

.stream-items>.stream-item.open:first-child {

    border-radius: 0 0 0px 0px;
}
.after-expanded:not(.separation-module), .after-expanded .tweet:hover, .after-expanded .tweet.focus {
    border-radius: 0px 0px 0 0;
}

.module ul:first-child li:first-child .list-link, .module .flex-module:first-child, .module .contrast-flex-module:first-child {
    border-top-left-radius: 0px;
    border-top-right-radius: 0px;
}
.module ul:last-child li:last-child .list-link, .module ul:last-child li.last-item .list-link, .module .list-link.last, .module .flex-module:last-child, .module .contrast-flex-module:last-child {
    border-bottom-right-radius: 0px;
    border-bottom-left-radius: 0px;
}.DashboardProfileCard {
    border: 1px solid #e1e8ed;
    background: #FFF;
    border-radius: 0px;
}.DashboardProfileCard-bg {
    background-size: 100%;
    border-bottom: 1px solid #e1e8ed;
    height: 95px;
    border-radius: 0px 0px 0 0;
    background-position: 0 50%;
    width: 100%;
    padding: 0 1px;
    margin-left: -1px;
    margin-top: -1px;
}.Trends {

    border: 1px solid #e1e8ed;
    border-radius: 0px;
    line-height: inherit;
    padding: 15px;
}

.home-tweet-box, .top-timeline-tweetbox .timeline-tweet-box {
    border-color: #E1E8ED !important;
}.home-tweet-box, .RetweetDialog-commentBox, .WebToast-box--altColor, .content-main .conversations-enabled .expansion-container .inline-reply-tweetbox {
    background-color: #FFFFFF;
}
.global-nav .search-input {
    display: block;
    -moz-box-sizing: border-box;
    box-sizing: border-box;
    width: 100%;
    height: 32px;
    padding: 5px 27px 6px 12px;
   
    line-height: 1;
    color: #292f33;
    background-color: #f5f8fa;
    border: 1px solid #e1e8ed;
    border-radius: 5px;
    transition: all .2s ease-in-out;
}
.ProfileHeaderCard-joinDate {
    margin-top: 7px;
}





















.ProfileAvatar-image, .ProfileAvatar-placeholderImage {

    border-radius: 8px;
    height: 100%;
    left: 0;
    position: absolute;
    top: 0;
    width: 100%;
    -webkit-border-radius: 100px;
}.ProfileAvatar {
    background: #fff;
    border: 5px solid #fff;
    -webkit-border-radius: 100px;
    box-shadow: 0 1px 1px rgba(136,153,166,0.15);
    height: 200px;
    position: relative;
    width: 200px;
}.stream-item-header .avatar, .permalink-header .avatar {
    float: left;
    margin-top: -3px;
    margin-left: -58px;
    -webkit-border-radius: 100px;
}.stream-item .content, .permalink-tweet .content {
    margin-left: 58px;
    margin-top: 13px;
}.stream-item-header .avatar, .permalink-header .avatar {
    float: left;
    margin-top: -6px;
    margin-left: -58px;
    -webkit-border-radius: 100px;
}.tweet>.context .with-icn {
    margin-left: -40px;
}.DashboardProfileCard-avatarLink {
    background-color: #fff;
    border-radius: 6px;
    margin: -30px 0 0 8px;
    padding: 1px;
    vertical-align: bottom;
    -webkit-border-radius: 100px;
}.DashboardProfileCard-avatarImage {
    border-radius: 7px;
    height: 72px;
    width: 72px;
    border: 2px solid #fff;
    -moz-box-sizing: border-box;
    box-sizing: border-box;
    color: #fff;
    -webkit-border-radius: 100px;
}.size32 {
    width: 34px;
    height: 34px;
    border-radius: 100px;
    margin-top: -2px;
}.nav .session .dropdown-toggle {
    width: 32px;
    height: 32px;
    border: 0;
    opacity: 1;
    -ms-filter: "alpha(opacity=100)";
    -webkit-border-radius: 100px;
}.QuoteTweet-fullname {
    font-size: 12px;
    font-weight: bold;
    color: #292f33;
}.QuoteTweet-screenname {
    color: #8899a6;
      font-size: 12px;
}.icon-btn .text {
    font-size: 11px;
    font-weight: normal;
    margin-left: 3px;
    position: relative;
    top: -6px;
}.top-timeline-tweetbox .timeline-tweet-box .tweet-form .tweet-box {
    max-height: none;
    font-size: 12px;
    padding: 8px 10px;
}


