    background-color: white;
    margin-top: 15px;
    border: 1px solid #e1e8ed;
}
ol#stream-items-id {
    border-top: 1px solid #f5f8fa;
    margin-top: -2px;

}





.AdaptiveSearchTimeline .stream .stream-item:first-child {
