css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("origo.hu") {
/* Arial helyett Trebuchet MS mindenhol */
body {
  font-family: "Trebuchet MS", sans serif !important;
}
/* reklámok off */
div#magnum, div.banner250, div#szoveges, div#sales-ajanlat, ul#mti-hirek, iframe, div.wsz_rotal_2, div.banner300, div#sportmoved, div.jcarousel-skin-origoAjanlat, div#allasdiv, div#szoveges-hird, div.shop-item, div#apro {
  display: none !important;
}
/* cikkek címe kicsit lejjebb */
div#cikk {
  margin-top: 12px !important;
}
/* friss képek doboz off */
div#akarmi{
  display: none !important;
}
/* valutaváltó box off */
h3#rate-title, div#rate-box, div#rate-bottom {
  display: none !important;
}

/* főcímek */
div.news-lead h2 {
  margin-top: 12px !important;
}
div.news-lead h2 a {
  color: #24468A !important;
}

/* címoldali kiemelések */
div#redalert {
  margin-bottom: 25px !important;
  background-color: #FFF !important;
  border-top: 2px solid #EC1D00 !important;
  border-bottom: 2px solid #EC1D00 !important;
}
div#redalert-in a {
  font-variant: small-caps !important;
  font-weight: bold !important;
  color: #EC1D00 !important;
  font-size: 19pt !important;
  line-height: 20pt !important;
  text-align: center !important;
}
h3.seeded-blue-big a, h3.seeded-green-big a, h3.seeded-blue-big a, h3.seeded-black-big a, h3.seeded-gray-big a {
  font-variant: small-caps !important;
  font-weight: bold !important;
  font-size: 19pt !important;
  line-height: 20pt !important;
}
h3.seeded-blue a, h3.seeded-green a, h3.seeded-blue a, h3.seeded-black a, h3.seeded-gray a {
  font-variant: small-caps !important;
  font-weight: bold !important;
  font-size: 15pt !important;
  line-height: 17pt !important;
}

/* box headers */
h3.box-title {
  margin-top: 20px !important;
}
h3.box-title a, h3.box-title span {
  font-size: 14pt !important;
}
}
