css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("hattrick.org"),
domain("hattrick.ws"),
domain("hattrick.interia.pl"),
domain("hattrick.uol.com.br") {

body, html { 
	background: url("http://www.hattrick.org/App_Themes/Standard/bg_dark.png") !important; }

div[class*="ad_"],
.bannerBackgroundHeader,
.bannerBackgroundSidebar160,
.bannerBackgroundFooter {
	display: none !important}

.hattrickNoSupporter {
	width: 980px !important}

#teamLinks, #time, #online {
	margin-top: 15px !important}
#time, #online, .ongoingEvents,
.startpage_signup, .startpage_text_big, .startPageHeader, div.clear,
.appBadgeText,
#ctl00_CPContent_pnlFacebookSignup h1,
#ticker a /* Suporters */{
	color: #CCC !important}
#top {
	display: none !important}

#header {
	border-radius: 8px  8px 0 0 !important;
	background: url("chrome://browser/skin/newtab/noise.png"), 
				linear-gradient(rgba(255,255,255,.15), rgba(255,255,255,.04)) 
				hsl(209,9%,8%) padding-box !important;
	border: 1px solid hsla(210,8%,5%,.4) !important;
	border-bottom: 0 !important;
	box-shadow: 0 1px 0 hsla(209,29%,72%,.25) inset !important;
	margin-bottom: -1px !important}
#content {
	background: url("chrome://browser/skin/newtab/noise.png"),
				linear-gradient(rgba(255,255,255,.04), rgba(255,255,255,.03)) 
				hsl(209,9%,8%) padding-box !important;
	border: 1px solid hsla(210,8%,5%,.4) !important;
	border-top: 0 !important;
	border-bottom: 0 !important;
	padding-left: 13px !important;
	padding-right: 13px !important}
#bottom {
	border-radius: 0 0 8px 8px !important;
	background: url("chrome://browser/skin/newtab/noise.png"), 
				linear-gradient(rgba(255,255,255,.03), rgba(255,255,255,.04)) 
				hsl(209,9%,8%) padding-box !important;
	border: 1px solid hsla(210,8%,5%,.4) !important;
	border-top: 0 !important}


#menu, .newLoginBox {
	border-radius: 5px !important;
	background: hsl(100,80%,14%) !important;
	background-image: linear-gradient(rgba(255,255,255,.15), rgba(255,255,255,.05)) !important;
	background-clip: padding-box !important;
	margin-left: -2px !important;
	border: 1px solid hsla(210,8%,5%,.3) !important;
	box-shadow: 0 1px 0 hsla(209,29%,72%,.25) inset !important}
.newLoginBox {
	margin-top: 15px !important}
#menu {
	position: relative !important;
	margin-top: 4px !important;
	margin-left: -18px !important}
#menu a:hover {
	background: hsla(210,8%,5%,.3) padding-box !important}
#menu, #menu a {
	height: 34px !important}

.boxLeft {
	background: Transparent !important}
.boxHead {
	border-radius: 5.5px 5.5px 0 0 !important;
	background: hsl(111,16%,28%) !important;
	background-image: linear-gradient(rgba(255,255,255,.1), rgba(255,255,255,.05)) !important;
	background-clip: padding-box !important;
	border: 1px solid hsla(210,8%,5%,.4) !important;
	border-bottom: none !important;
	box-shadow: 0 1px 0 hsla(209,29%,72%,.25) inset !important}
.boxBody {
	background: linear-gradient(White, #FAFAFA)
				padding-box !important;
	border: 1px solid hsla(210,8%,5%,.4) !important;
	border-top: 0 !important;
	border-bottom: 0 !important}
.boxFooter {
	border-radius: 0 0 5.5px 5.5px !important;
	background: #FAFAFA padding-box !important;
	border: 1px solid hsla(210,8%,5%,.4) !important;
	border-top: 0 !important}

.IRLTeamListItem, h2, h3,
div.borderSeparator {
	border-bottom-color: hsla(210,8%,5%,.25) !important}

a[href*="#skill"], a[href*="&ll="] {
	font-weight: bold !important}

a:hover[href*="#skill"], a:hover[href*="&ll="] {
	color: Black !important;
	font-weight: bold !important}

a[href*="skill&ll=0#skill"] {color: #C0C0C0 !important}
a[href*="skill&ll=1#skill"] {color: #CfCfCf !important}
a[href*="skill&ll=2#skill"] {color: #FF9966 !important}
a[href*="skill&ll=3#skill"] {color: Green !important}
a[href*="skill&ll=4#skill"] {color: Green !important}
a[href*="skill&ll=5#skill"] {color: Green !important}
a[href*="skill&ll=6#skill"] {color: Blue !important}
a[href*="skill&ll=7#skill"] {color: Blue !important}
a[href*="skill&ll=8#skill"] {color: Red !important}
a[href*="skill&ll=9#skill"] {color: Red !important}
a[href*="skill&ll=10#skill"] {color: Red !important}
a[href*="skill&ll=11#skill"] {color: #0099FF !important}
a[href*="skill&ll=12#skill"] {color: #0099FF !important}
a[href*="skill&ll=13#skill"] {color: #0099FF !important}
a[href*="skill&ll=14#skill"] {color: #990000 !important}
a[href*="skill&ll=15#skill"] {color: #990000 !important}
a[href*="skill&ll=16#skill"] {color: #990000 !important}
a[href*="skill&ll=17#skill"] {color: #b03060 !important}
a[href*="skill&ll=18#skill"] {color: #b03060 !important}
a[href*="skill&ll=19#skill"] {color: #b03060 !important}
a[href*="skill&ll=20#skill"] {color: #000000 !important}
a[href*="skillshort&ll=0#skillshort"] {color: #ff1493 !important}
a[href*="skillshort&ll=1#skillshort"] {color: #ff1493 !important}
a[href*="skillshort&ll=2#skillshort"] {color: Red !important}
a[href*="skillshort&ll=3#skillshort"] {color: Red !important}
a[href*="skillshort&ll=4#skillshort"] {color: #d2691e !important}
a[href*="skillshort&ll=5#skillshort"] {color: #ffa500 !important}
a[href*="skillshort&ll=6#skillshort"] {color: Green !important}
a[href*="skillshort&ll=7#skillshort"] {color: #0000ff !important}
a[href*="skillshort&ll=8#skillshort"] {color: #000099 !important}
a[href*="skillshort&ll=9#skillshort"] {color: #000033 !important}
a[href*="#skillshort"] {text-decoration: underline ! important}
a[href*="lt=gentleness"],
a[href*="lt=aggressiveness"],
a[href*="lt=honesty"] {
	padding: 1px 3px !important}
}
