@namespace url(http://www.mozilla.org/keymaster/gatekeeper/there.is.only.xul);
/* ==UserStyle==
// @name         Throbber image for Mozilla Beta
// @author       Roblesolido
// @description	 Changes the  throbber image with the icon of Mozilla Beta.
// ==/UserStyle==*/

#navigator-throbber {
background-color: #000 !important;
border-left: 1px ThreeDShadow solid !important;
border-right: 1px ThreeDHighlight solid !important;
background-image: none !important;
width: 43px !important;
list-style-image: url("data:image/png;base64,") !important;
}

#navigator-throbber[busy="true"] {
background-image: url("data:image/gif;base64,") !important;
background-position: center center !important;
background-repeat: no-repeat !important;
}
