css:
@namespace url(http://www.w3.org/1999/xhtml);















@-moz-document url-prefix('http://www.tumblr.com/'), url-prefix('https://www.tumblr.com/')   {




} body {

  background: url("http://oi51.tinypic.com/2cp6jr6.jpg") !important; 
  background-size: 100% !important; 
}



.login.sign_up,
A[href="/forgot_password"],
#login_form SPAN,
.footer_links {
  display: none !important; 
}





#logo {
  width: 0px !important;
  height: 0px !important;
  padding-left: 196px !important; 
  padding-top: 44px !important; 
  background-image: url("https://secure.assets.tumblr.com/images/logo/logo.png") !important; 
  background-repeat: no-repeat !important;
}


img[src*="http://assets.tumblr.com/images/new_post_icons_no_labels.png"]{
opacity:0.00 !important;  
}
#new_post{
  background-image: url(http://assets.tumblr.com/images/new_post_icons_no_labels.png) !important; 
  background-repeat: no-repeat !important; 
}

.post.is_mine.with_avatar.new_post {
  background: rgba(255,255,255,0.50) !important; 
}


.new_post_label{
  color: rgba(0,0,0, 1.0) !important; 
}

#left_column, #right_column {background-color: transparent !important;}

div.l-content.l-content.l-content {
  background: rgba(44,71,98, 0.50) !important; 
}


.selection_nipple.white, .selection_nipple:not([class="selection_nipple white"]) {
background: none !important;
width: 0px !important;
height: 0px !important;
margin-top: 0px !important;
border-bottom: solid 11px rgba(44,71,98, 0.50) !important;
border-right: 11px solid transparent !important;
border-left: 11px solid transparent !important;
}
.selection_nipple:not([class="selection_nipple white"]) {
border-bottom: solid 11px rgba(44,71,98, 0.50)  !important;
}




.mceContentBody {
  background: url("http://imgsrc.hubblesite.org/hu/db/images/hs-1994-02-c-web.jpg") !important; 
  background-size: 100% !important; 
  color: rgba(255,255,255, 1.0) !important; 
}


#content_top {
  width: 0px !important;
  height: 0px !important;




}


#content_bottom {
  width: 0px !important;
  height: 0px !important;




}


#left_column, #right_column {background-color: transparent !important;}

div.l-content.l-content.l-content {
  padding-top: 25px !important; 
  padding-bottom: 25px !important; 
   -moz-border-radius: 20px !important; 
}


LABEL {
  color: rgba(0,0,0, 1.0) !important; 
}



.radar {
  display: none !important; 
}
#footer {
  display: none !important; 
}
.selected .following,
.selected .followers,
.selected .settings,
.selected .posts,
.selected .messages,
.selected .drafts,
.selected .queue,
.selected .likes  {
  color: rgba(255,255,255, 0.50) !important; 
  background: rgba(44,71,98, 1.0) !important; 
}


#new_post .arrow {
  opacity: 0.50 !important;
  margin-left: -1px !important; 
}
