css:
@-moz-document domain("www.reddit.com") {


#siteTable, .commentarea {
    width: 640px ;
    margin-top: 0;
    margin-right: auto;
    margin-bottom: 0;
    margin-left: auto;
}

#siteTable {
    font-family: georgia;
}

.commentarea .usertext-body .md {
    font-family: lucida grande;
    color: #555;
}

.commentarea .author {
    color: #455085;
}

.commentarea .midcol, .commentarea a.expand, .commentarea .buttons .first, .commentarea .buttons li:nth-of-type(3) {
    display: none;
}

#siteTable .link {
    margin: 18px 0 24px;
}

#siteTable .link .title a {
    font-size: 21px;
    line-height: 28px;
    color: #666;
}

#siteTable .thing .midcol {
    display: none;
}

#siteTable .thing .rank {
    display: none;
}

#siteTable .link .tagline {
    margin-bottom: 4px;
}

#siteTable .link .title .domain,
#siteTable .link .title .domain a,
#siteTable .link .tagline,
#siteTable .link .tagline a,
#siteTable .link ul.buttons a {
    font-size: 13px !important;
    font-family: Helvetica !important;
    color: #aaa  !important;
    font-weight: normal  !important;
}

#siteTable .link ul.buttons .share, #siteTable .link ul.buttons li:last-child {
    display: none;
}

.last-clicked {
    border: 0;
}

#header {
    border-bottom: 1px solid #eee;
    padding: 0 0 12px;
    background-color: #fafafa;
}

#header .tabmenu {
    display: none;
}

#header #sr-header-area {
    border: 0;
    background-color: #f3f3f3;
    margin-bottom: 8px;
}

#header-img-a {
    position: relative;
    left: 12px;
}

#header-bottom-right {
    background-color: transparent;
}

#header-bottom-right a, #header-bottom-right {
    color: #777;
}

.footer-parent {
    display: none;
}

.side {
    display: none;
}




}
