css:
@namespace url(http://www.w3.org/1999/xhtml);
@-moz-document url-prefix(http://www.tvguide.com/Listings/Default.aspx),
url(http://www.tvguide.com/listings/default.aspx),
url(http://www.tvguide.com/listings/),
url(http://www.tvguide.com/Listings/),
url(http://www.tvguide.com/Listings/default.aspx) {

html {
	padding-bottom: 0 !important;
}

body {
	margin-top: -50px !important;
	overflow-x: hidden !important;
	background: #ddddd3 url(http://i50.photobucket.com/albums/f329/Webbpa/TV_bg.gif) fixed repeat;
}

.timebarRow > .timebarCell:first-child {
	padding-right: 15px !important;
}

.timebarCell + .timebarCell {
	border-right-width: 1px !important;
	border-right-color: white !important;
	border-right-style: solid !important;
}

.timebarCell img {
	margin-top: -2px !important;
}

.timebarCell {
	text-align: center !important;
}

.timebarCell:first-child + td {
	padding-left: 4% !important;
}

#body,
#l-w,
#l-l-w,
#l-l-c,
.listings-w,
.listings-w > div,
.listings-w > div > table,
#TvGridDiv,
#gridTable,
.gridtabs,
.timebarTable,
.ch > table {
	width: 100% !important;
	background-image: none !important;
}

#gridTable {
	border-top-width: 0 !important;
	margin-top: -2px !important;
}

#l-l-w {
	margin-left: -10px !important;
}

.gridLegend table {
	margin-left: auto !important;
	margin-right: auto !important;
}

.pc2height,
.nch,
.nch B,
.timebarCell {
	font-size: 13px !important;
	font-weight: bold !important;
}

.par {
	font-size: 13px !important;
	font-weight: lighter !important;
}

.pc2height:hover,
.nch:hover {
	text-decoration: none !important;
}

.newSelectStyle,
#ID_foLayerCell,
#ID_foLayerCell * {
	font-size: 14px !important;
}

img[src$="/flyover_close.gif"] {
	height: 15px !important;
	width: 15px !important
}

#l-l-c > div:first-child + div + div > div > table > tbody > tr:last-child > td:first-child {
	position: absolute !important;
	left: 600px !important;
	top: 10px !important;
}

.gridDiv {
	height: auto !important;
	overflow: hidden !important;
}

.nch b {
	padding-right: 5px !important;
}

#gridLegendDiv {
	margin-left: auto !important;
	margin-right: auto !important;
}

#divWelcome,
#ad-728x90-top0,
body > a,
.nch br,
body > #body > div:not(#l-w),
#ID_foLayerCell > font:last-child,
#l-w > div:not(#l-l-w),
.ph-w,
#amg_target_e5eb,
.listings-bottom-ad-w,
.gridAdRow,
.cc,
#printlocalrow,
#searchboxrow,
#facebox {
	display: none !important;
}

/* Special workaround for Meebo */
#meebo.meebo-00 {
	visibility: hidden;
}

.listings-w {
	margin-top: 18px !important;
}

#timebarDiv {
	position: fixed;
	top: -1px;
	width: 100%;
}

/* Day/time selectors */
table.listings-table-h tbody tr {
	position: absolute !important;
	left: 1px;
	top: 12px;
}

/* Search box stuff */
#searchboxrow, body tr#searchboxrow {
	display: inline !important;
	position: absolute !important;
	top: 22px !important;
	left: 840px !important;
}

#ListingsSearchResultsDiv,
#tdSearchResultsList > table {
	width:100% !important;
}
#tdSearchResultsList > table * {
	font-size:14px !important;
}
SPAN[style="color: rgb(204, 0, 0); font-size: 12px; font-weight: bold;"] {
	display:none !important
}
#txtListingsSearch {
	width:100px !important
}

/* Favs */
table.listings-table-h > tbody > tr:nth-child(7) > td:nth-child(2) > * {
	position: relative;
	top: 22px;
	left: 1125px;
}

/* Grid prefs */
table.listings-table-h > tbody > tr:nth-child(7) > td:nth-child(2) > img:nth-child(4),
table.listings-table-h > tbody > tr:nth-child(7) > td:nth-child(2) > a:nth-child(5) {
	left: 1100px;
}

/* HD Only */
#HdOnlyChk {
	position: absolute;
	top: 33px;
	left: 1398px;
}
#HdOnlyTextString {
	position: absolute;
	top: 35px;
	left: 1418px;
	font-size: 11px !important;
	font-weight: normal !important;
}

/* Hide ad rows */
#gr0, td[style*="background-image"] {
	display: none;
}

/* Restore left column */
.gridRow td[style*="background-image"] {
	display: table-cell;
	background-color: #EDEEDE !important;
	background-image: none !important;
}

/* Hide infomercial */
.infomercial {
	display: none !important;
}

}
