css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("facebook.com") {

#globalContainer { position : relative !important; margin : 0 auto !important; width: 1080px !important }
#blueBar{ position : fixed !important; top : 0px !important; float : center !important; z-index: 13 !important ; }
#pageHead{ z-index : 14 !important ; position : relative !important; left : auto !important; width : 980px !important;  float : center !important; }
#pageLogo, #jewelCase { position : fixed !important; top : 10 !important; left : auto !important; }
#jewelCase {margin-left : 97px !important; height : 31px !important; }
#headNav { position : fixed !important; top : 10px !important; width : 898px !important; }
#content{ margin-top : 31px !important }
#contentArea { width: 600px !important }
.uiUfi { width: 500px !important } /* comments width */
#rightCol { padding-right: 8px !important }
#rightCol > .home_right_column { position: fixed !important; padding-top: 0 !important; width: 242px !important; }
#mainContainer { z-index: -1 !important }

}


@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("facebook.com") {

/** Set Background color for page **/
.fbIndex { background-color: #3B5998 !important }

/** Hide Crap **/
.fbIndex #globalContainer #dropmenu_container,
.fbIndex #globalContainer #content,
.fbIndex #globalContainer #pageFooter { display: none !important }

/** Center Login Bar **/
.fbIndex .loggedout_menubar_container {
  position: fixed !important;
  width: 420px !important;
  height: 82px !important;
  min-width: 0 !important;
  top: 50% !important;
  left: 50% !important;
  margin-top: -17px !important;
  margin-left: -210px !important;
  z-index: -1 !important;
}

/** Center Login form **/
.fbIndex .loggedout_menubar { width: auto !important }
.fbIndex .loggedout_menubar_container .lfloat,
.fbIndex .loggedout_menubar_container .rfloat { float: none !important }
.fbIndex .loggedout_menubar_container .lfloat img,
.fbIndex .loggedout_menubar_container .rfloat #login_form table { display: block !important; margin: 0 auto !important }

.fbIndex .loggedout_menubar_container .lfloat img { display: block; margin: -70px auto 20px !important; }


/** Hide Homepage Box **/
#SetAsHomepage_Callout {
  display: none;
}

}
