@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain('adictv.com')  {

body {
background-color: #000000!important;
margin: 0px auto;
padding: 12px 30px 4px 30px;
width: 90%;
}

.catbg, .catbg2, .catbg3 {
border-bottom: solid 1px #222!important;
background-image:none!important;
}

.catbg, tr.catbg td, .catbg3, tr.catbg3 td {
background-color: black!important;
background-image: none!important;
color: #CEDBE5!important;
padding-left: 10px;
padding-right: 10px;
font-family: lucida sans, arial!important;}

.nav, .nav:link, .nav:visited {
color: #FFC7D5!important;
text-decoration: none;
font-family: lucida sans, arial!important;
}

a.nav:hover
{
	color: #FFFFFF!important;
	text-decoration: none!Important;
}

.titlebg, tr.titlebg th, tr.titlebg td, .titlebg2, tr.titlebg2 th, tr.titlebg2 td {
color: #FFC7D5!important;
background-image:none!important;
font-style: normal!important;
background-color: #111!important;
border-bottom: solid 1px #222!important;
border-top: dashed 1px #222!important;
padding-left: 10px;
padding-right: 10px; }

.tborder {
padding: 1px;
border: 0!important;
background-color: #222!important;}

.maintab_back a:link, .maintab_back a:visited, .maintab_active_back a:link, .maintab_active_back a:visited {
color: #FFC7D5!important;
text-decoration: none;
}

body, td, th, tr {
color: white;
font-size: 11px!important;
font-family: lucida sans, arial, sans-serif!important;
}

.bordercolor {
background-color: #111!important;
padding: 0px;
}

.windowbg {
color: white;
background-color: black!important;
}

.windowbg2 {
    color: #FFFFFF;
background-color: #070707!important;
}

.windowbg3 {
color: white;
background-color: #111!important;
}

a:link, a:visited {
color: #FFC7D5;
font-weight: normal!important;
text-decoration: none!important;
}

.windowbg3 a, .windowbg a, .mirrortab_back a:link  {
color: #FFC7D5!important;
font-weight: normal!important;
text-decoration: none!important;
}

.quote {
    background-color: #111111!important;
    border: 1px dashed #444444!important;
    color: #CCCCCC;
    margin: 1px;
    padding: 2px!important;
font-style: italic!important;
font-size: 11px!important;
}

.maintab_back {
    background-color: #111111!important;
    background-image: none!important;
}

.maintab_first {
    background-color: #111111!important;
    background-image: none!important;
    width: 10px;
}

.maintab_last {
    background-color: #111111!important;
    background-image: none!important;
    background-repeat: no-repeat;
    width: 10px;
}

.maintab_active_back {
    background-color: #222222!important;
    background-image: none!important;
    background-repeat: repeat-x;
}

.maintab_active_last {
    background-color: #222222!important;
    background-image: none !important;
    background-repeat: no-repeat;
    width: 8px;
}

.maintab_active_first {
    background-color: #222222!important;
    background-image: none !important;
    background-repeat: no-repeat;
    width: 8px;
}

.mirrortab_back {
background-image: none!important;
background-color:#111!important;
}

.mirrortab_first {
background-image: none!important;
background-color:#111!important;
width: 10px;
}

.mirrortab_last {
background-image: none!important;
background-color:#111!important;
width: 10px;
}

hr, .hrcolor {
    background-color: #222222!important;
    border: 0 none!important;
    color: #FFFFFF;
    height: 1px;}

.catbg2, tr.catbg2 td {
background-image: none!important;
background-color: black!important;
color: #666!important;
padding-left: 10px;
padding-right: 10px;
}

.catbg a:link, .catbg a:visited, .catbg2 a:link, .catbg2 a:visited {
color: white!important;
text-decoration: none;
}

.catbg3, tr.catbg3 td, .catbg3 a:link, .catbg3 a:visited {
font-size: 95%;
color: #BBB!important;
text-decoration: none;
}

.catbg, tr.catbg td, .catbg3, tr.catbg3 td {
background-color: black!important;
background-image: none!important;
color: #666;
padding-left: 10px;
padding-right: 10px;
font-family: lucida sans, arial!important;
}


