css:
@namespace url(http://www.w3.org/1999/xhtml);


@-moz-document domain("www.youtube.com") {

/* MAD DEV  */

#logo {background-image: url("http://techuff.webs.com/images/youtube-logo.png") !important; background-position: -35px -117px !important}

body {background: url("http://techuff.webs.com/images/bg.png") repeat fixed left center !important;}

a {color: #116099 !important;}

.video-list-item .title {color: #117099 !important;}

#homepage-whats-new .module-title, .homepage-side-block .module-title {color: #FF4500 !important}

#page.watch-branded #watch-main-container {background: url("http://techuff.webs.com/images/bg.png") repeat fixed left center !important;}

html {color: #fff !important}

#page.watch-branded #watch-sidebar {background: url("http://techuff.webs.com/images/bg.png") repeat fixed left center !important;}

#watch-description-extras h4 {color: #FF4500 !important}

#watch-uploader-info, #watch-description-extra-info li {color: #FF4500 !important}

.comments-section h4 {color: #ff9900 !important;}

#comments-post-form-alert {background: url("http://techuff.webs.com/images/bg.png") repeat fixed left center !important; border-bottom-color: transparent !important;  border-left-color: transparent !important;  border-top-color: transparent !important;  border-right-color: transparent !important; color: #fff !important}

.comment .comment-container {background-image: -moz-linear-gradient(center top , #131313, #131313) !important; border: 2px solid #131313 !important;
      -moz-border-radius: 10px !important;
      -webkit-border-radius: 15px !important; border-right-color: #ff4500 !important}

.comments-section .video-list-item-link {background: #131313 !important; border: 0px solid #131313 !important;
      -moz-border-radius: 15px !important;
      -webkit-border-radius: 15px !important;}

.comment {background-color: transparent !important} 

a.video-list-item-link:hover, .hover a.video-list-item-link {background: #131313 !important; border: 0px solid #131313 !important;
      -moz-border-radius: 15px !important;
      -webkit-border-radius: 15px !important;}

.watch-view-count strong {color: #FF4500 !important}

#default-language-box {background: transparent !important; border: 1px solid #131313 !important;
      -moz-border-radius: 15px !important;
      -webkit-border-radius: 15px !important;}

.video-list-item .stat {color: #FF4500 !important}

.shmoovies-list-container h2 {color: #FF4500 !important}

#masthead-subnav {background: transparent !important; border-top-color: transparent !important; border-bottom-color: transparent !important; border-right-color: transparent !important; border-left-color: transparent !important}

#masthead-container {border-bottom-color: #131313 !important}

.feedmodule-anchor {background: transparent !important}

.yt-alert-promo {background: transparent !important; border: 2px solid #131313 !important;
      -moz-border-radius: 15px !important;
      -webkit-border-radius: 15px !important;}

#footer-container {border-top-color: #131313 !important}

#shmoovies-category-heading .yt-uix-expander-head {color: #FF4500 !important; border: 2px solid #131313 !important;
      -moz-border-radius: 15px !important;
      -webkit-border-radius: 15px !important;}

#shmoovies-category-heading .yt-uix-expander-head:hover {background: #131313 !important; border: 2px solid #131313 !important;
      -moz-border-radius: 15px !important;
      -webkit-border-radius: 15px !important;}

#shmoovies-category-menu-container {background: #131313 !important; border: 2px solid #131313 !important;
      -moz-border-radius: 15px !important;
      -webkit-border-radius: 15px !important;}

#watch-owner-container, .yt-alert-warn {background: #303030 !important; border: 0px solid #131313 !important;
      -moz-border-radius: 25px !important;
      -webkit-border-radius: 25px !important;}

#search-header h2 {color:  #117099 !important}

#search-header .num-results {color: #ff9900 !important}

.yt-uix-button-text {color: #FF4500 !important}

.yt-uix-button-menu .yt-uix-button-menu-item {background: #131313 !important; border: 2px solid #131313 !important;
      -moz-border-radius: 15px !important;
      -webkit-border-radius: 15px !important; color: #ff9900 !important}

ul.yt-uix-button-menu {background-color: #131313 !important; border: 1px solid #131313 !important;
      -moz-border-radius: 15px !important;
      -webkit-border-radius: 15px !important; box-shadow: none !important}

a.yt-badge-std {background-color: #131313 !important; border-top-color: #131313 !important; border-bottom-color: #131313 !important; border-right-color: #ff4500 !important; border-left-color: #ff4500 !important}


.viewcount, .snippet-metadata li.views {color:  #117099 !important}

.channel-subscriber-count, .video-count, .username-prepend, .date-added {color: #ff4500 !important}

.description, .sort-by-title {color: #fff !important}


#watch-video-container {background-color: transparent !important}

#watch-headline-container {background-color: transparent !important}

#watch-sidebar .watch-module-body {background-color: transparent !important}

#watch-headline h1 .long-title {color: #fff !important}

#eow-title {color: #117099 !important}

#ppv-container {background-color: #131313 !important}

.first {color: #ff4500 !important}

.featured-label {color: #ff9900 !important}

.video-thumb {background-color: #131313 !important; border-left-color: transparent !important; ; border-bottom-color: transparent !important; border-top-color: transparent !important;border:1px solid #131313 !important;
      -moz-border-radius: 10px !important;
      -webkit-border-radius: 15px !important;border-right-color: #ff4500 !important}

#watch-player {background-color: #131313 !important; border-left-color: transparent !important; border-bottom-color: transparent !important; border-top-color: transparent !important;border:3px solid #131313 !important;
      -moz-border-radius: 10px !important;
      -webkit-border-radius: 10px !important; border-right-color: #ff4500 !important}

.yt-uix-button {background-image: -moz-linear-gradient(center top , #303030, #131313) !important; color: #117099 !important;  border-bottom-color: #131313 !important; border-top-color: #303030 !important; border-right-color: #ff4500 !important; border-left-color: transparent !important; background-color: #131313 !important; border-right-style: solid !important} 

.yt-uix-button:hover, .yt-uix-button:focus {background-image: -moz-linear-gradient(center top , #131313, #303030) !important; color: #117099 !important;  border-bottom-color: #131313 !important; border-top-color: #303030 !important; border-right-color: #ff4500 !important; border-left-color: transparent !important; background-color: #131313 !important; border-right-style: solid !important}

#masthead-expanded {background-image: -moz-linear-gradient(center top, #303030, #131313) !important; border-color: transparent !important}

.yt-uix-slider-shade-left, .yt-uix-slider-shade-right, #masthead-expanded-menu-shade {background-image: none!important; border: none !important}

.masthead-expanded-menu-item {background: transparent !important}

#masthead-expanded-menu-acct-sw-container {background-color: transparent !important}

.collapse, .expand  {background: url("http://techuff.webs.com/images/bg.png") repeat fixed left center !important;}

#watch-description-fadeout {display: none !important}

/* User Login */ 

#user-navbar {background-color: transparent !important;}

#user-navbar-sections .current a, #user-navbar-sections .current a:hover {background-image: -moz-linear-gradient(center top, #303030, #131313) !important; box-shadow: none !important; color: #ff4500 !important}

#user-navbar-sections li a {background-image: -moz-linear-gradient(center top, #303030, #131313) !important; box-shadow: none !important; color: #ff4500 !important}

.feedmodule-subnull-txt-header {color: #ff4500 !important}

#user-navbar-account a {color: #fff !important}

#masthead-expanded a {background-color: #f6f6f6 !important; color: #ff4500 !important}

#yt-admin h1, #yt-admin-content h2 {background-color: transparent !important; color: #ff4500 !important; font-size: 18px !important}

#masthead-user-expander .yt-uix-expander-head {background-color: #131313 !important; color: #ff4500 !important; border: 2px solid !important; border-top-color: transparent !important; border-left-color: transparent !important; border-right-color: #ff4500 !important; border-bottom-color: transparent !important}

#comments-post-form textarea, .comments-reply-form textarea {background-color: #303030 !important; color: #fff !important; border-right-color: #ff4500 !important; border-left-color: transparent !important; border-bottom-color: transparent !important; border-top-color: transparent !important}

.comments-post-count input {background-color: transparent !important; color: #ff4500 !important}

.edit_controls_outer {background-color: transparent !important;}

.channel_settings_tab {background-image: -moz-linear-gradient(center top , #303030, #131313) !important; color: #ff4500 !important;  border-bottom-color: #131313 !important; border-top-color: #303030 !important; border-right-color: #ff4500 !important; border-left-color: transparent !important; background-color: #131313 !important; border-right-style: solid !important}

#channel_settings {background-color: #131313 !important;}

.channel_tab_content, .display-list-container {background-color: transparent !important; border:3px solid #131313 !important;
      -moz-border-radius: 10px !important;
      -webkit-border-radius: 10px !important; border-right-color: #ff4500 !important}

.edit_controls_bottom_border, 
.edit_controls_top_border  {background-image: none !important;} 

.settings_label {color: #ff4500 !important}

#display_uploads, label {color: #ff4500 !important}

.compose_header {color: #ff4300 !important}

#masthead-expanded-menu-list a {background: transparent !important}
}
