/*----- DEFAULT TEXT, BORDER & BACKGROUND COLORS -----*/
* {color: #999 !important; text-shadow: none !important; background-color: transparent !important; -moz-border-top-colors: #444 !important; -moz-border-bottom-colors: #444 !important; -moz-border-left-colors: #444 !important; -moz-border-right-colors: #444 !important}

table {background-color: rgba(40,30,30,.6) !important; -moz-border-radius: 6px !important}
div[style] > form {background-color: rgba(10,20,20,.8) !important; -moz-border-radius: 6px !important}

a, a * {color: #409B9B !important; text-decoration: none !important}
a:hover, a:visited:hover, a:hover *,span[onclick]:hover, div[onclick]:hover, [role="link"]:hover, [role="link"]:hover *, [role="button"]:hover, [role="button"]:hover *, [role="menuitem"]:hover, [role="menuitem"]:hover *, .gac_b:hover *, .google-ac-b *, .yt-suggest-selected *, .yt-uix-button-menu-item:hover, .acl div.sug .sel *, .acl div.sug .sel, .ac_active, .ac_active *, .yui-ac-bd li:hover {
  color: #F0F0F0 !important; text-shadow: 0 0 2px #FFF !important}
a:visited {color: #607069 !important}
.selected, .selected *, .gtab-selected, .gtabi-mc, [href="#"] {color: #DDD !important; font-weight: bold !important}

h1, h2, h3, h4, h1 *, h2 * {color: #DDD !important}
[class*="error"], [class*="alert"], code, span[onclick], div[onclick] {color: #900 !important}

::-moz-selection {background-color: #377 !important; color: #000 !important}
:focus {outline: none !important}

/*----- MENU & CO BACKGROUND-COLORS -----*/
iframe, embed, label [onclick], div[style*="position:"][style*="left:"][style*="visible"], div[style*="z-index:"][style*="left:"][style*="visible"], div[style*="-moz-user-select"], div[role="menu"], div[role="dialog"], span[class*="script"] div, [class*="dropdown"], [class="title"], ul[style*="display:"], #vspb, #search_controls, .menu, .tooltip, .olp-mn, .gh-iovr, #categoriesdropdown, #user_link_menucontent, .overlay-stream, .dd, .ac_res, .yui-ac-bd, #overhead-collect #lub, .bluestip, .oh-menu, .popup2, .bt-mid, .gac_m, .google-ac-m, .gbm, .yt-suggest-table, .yt-uix-button-menu, .acl div.sug, .yt-uix-tooltip, .yt-uix-tooltip-tip > div > div, #currentStore {
  background-color: rgba(5,5,5,.8) !important;
  -moz-border-radius: 5px}

/*----- OTHER COLOR CHANGES -----*/
.modbox *:empty, div[style*="border: 1px"], div[style="display: block;"] {background-color: rgba(0,0,0,.5) !important}
i.gr2, i.gr3, .gr-top {background-color: #252020 !important}
.goog-imageless-button-top-shadow {border-color: transparent !important}
.cc-in, .gr-body, .smbutton > span, #watch-info, #watch-next-list, #watch-channel-discoverbox {background: -moz-linear-gradient(top, rgba(200,200,200,.1), rgba(0,0,0,.3)) !important}

/*----- CHANGE CURSORS -----*/
* {cursor: url("http://reloaded.site88.net/Data/aero_arrow.cur"), default !important}
a, a * {cursor: url("http://reloaded.site88.net/Data/aero_link.cur") ,pointer !important}

/*----- DEFAULT BUTTONS, SEARCHBOXES & CO -----*/
input, select, button:not(.ws):not(.wsa):not(.vspib), a[href="javascript:;"]:not([t]):not([onclick]) {
  -moz-appearance: none !important;
  background: #000 !important;
  border: 2px solid !important;
  -moz-border-radius: 4px !important}
input:hover, input[type="button"]:hover, select:hover, button:hover {
  -moz-border-top-colors: #555 !important;
  -moz-border-bottom-colors: #555 !important;
  -moz-border-left-colors: #555 !important;
  -moz-border-right-colors: #555 !important}
input:focus, select:focus {
  -moz-box-shadow: 0 0 5px #077 !important}
input[type="checkbox"], input[type="checkbox"]:focus {
  border: 2px solid #222 !important;
  background-color: #111 !important;
  color: #BBB !important;
  -moz-border-radius: 4px !important}
input[type="radio"], input[type="radio"]:focus {
  border: 2px solid #222 !important;
  background-color: #111 !important;
  color: #BBB !important}
select input[type="button"] {border: none !important}

button:active, input[type="submit"]:active {
  background: #444 !important}

input *, input, select {color: #F0F0F0  !important}
input *:hover * {color: #F0F0F0 !important; text-shadow: 0 0 2px #FFF !important}

textarea {-moz-appearance: none !important; background: rgba(0,0,0,.3) !important; -moz-border-radius: 3px !important; border: 1px solid #000 !important; -moz-box-shadow: inset 0 0 8px #000 !important}
textarea:focus {background: rgba(0,0,0,.5) !important}
textarea, textarea * {color: #C8C8C8 !important; text-shadow: 0 0 1px gray !important}
textarea:focus, textarea:focus > * {text-shadow: none !important; -moz-box-shadow: none !important}

option {
	-moz-appearance: none !important; background: none !important; color: #555 !important}
option:not([disabled]):hover, option:focus, option:checked {
	background: -moz-linear-gradient(center top, #AAA, #555) !important; color: #000 !important}

/*----- SCROLLBAR CHANGES -----*/
scrollbarbutton {
  -moz-appearance: none !important;
  background-color: transparent;
  border: none}
thumb {
  -moz-appearance: none !important;
  border: none !important;
  background-color: #999 !important;
  -moz-border-radius: 10px;
  -moz-box-shadow: inset 0 0 3px #000}
scrollbar {
  -moz-appearance: none !important;
  background: transparent !important;
  -moz-box-shadow: inset #000 0 0 8px}
scrollbarbutton:hover, thumb:active, thumb:hover {
  opacity: 0.5 !important}

scrollbar[orient="vertical"] thumb {min-width: 7px !important; max-width: 7px !important; min-height: 14px !important}
scrollbar[orient="horizontal"] thumb {min-height: 7px !important; max-height: 7px !important}
scrollbar[orient="vertical"] {padding-left: 3px !important; min-width: 12px !important; max-width: 12px !important}
scrollbar[orient="horizontal"] {padding-top: 3px !important; min-height: 12px !important; max-height: 12px !important}


/*----- IMAGE CHANGES -----*/
*:not(:empty):not(html):not(span):not(option):not([style="display: block;"]):not([onclick*="open"]):not([onclick*="s_objectID"]):not(#globalsearch):not(IMG):not([class*="stars"]):not([id*="stars"]):not([id="rating"]):not([class="rating"]):not([class*="SPRITE"]):not([id*="SPRITE"]):not([class*="item"]):not([id*="item"]):not([class*="thumb"]):not([id*="lbImage"]):not([class*="cc-in"]):not([class*="gr-body"]):not([id*="watch"]):not(.sp),
span[style*="width"], .r3_hm, .gmbutton2 b, .gtab-i, .ph, .bstab-iLft, .csb, #pagination div, [style*="sprite2.png"] {
  background-image: none !important}
.olp-mn * {background: none !important; border: none !important}

img {opacity: .7 !important}
img:hover, a:hover img {opacity: 1 !important}

/*----- GOOGLE & YOUTUBE LOGOS -----*/
#lga #logo, #hp-logo img, img[src*="video_logo_lg.gif"], div[title="Firefox Start"] {
  margin-top: 4% !important;
  width: 0 !important;
  height: 103px !important;
  padding: 0 281px 0 0 !important;
  background: url("http://s2.subirimagenes.com/privadas/previo/thump_1383022classicus.png") !important}
h1 a[title*="Google"] img, a img[src="/images/logo_sm.gif"] {
  width: 0 !important;
  padding: 0 150px 0 0 !important;
  top: 0 !important;
  opacity: 1 !important;
  background: url("http://h-2.abload.de/img/indexsmall5z5t.png") !important}
h1 a[title*="Google"] {color: transparent !important}

.a9 {background: url("http://i46.tinypic.com/30n9aq8.jpg") no-repeat !important}

img[alt^="YouTube"] {
  width: 0 !important;
  padding-right: 100px !important;
  opacity: 1 !important;
  background: url("http://i46.tinypic.com/eg5r4i.jpg") no-repeat !important}
}

/*----- ABOUT PAGES BACKGROUND -----*/
@-moz-document url-prefix(about) {
* {background-color: transparent}
body > * {background-color: rgba(0,0,0,.5) !important} }

/*----- SHOW INSTALLS ON USERSTYLES.ORG -----*/
@-moz-document domain(userstyles.org) {
li[total-install-count]:after {
  font-size: .70em !important;
  content: " (Installs: " attr(total-install-count) ", " attr(weekly-install-count) " wk)"} }

/*----- RIGHT-CLICK MENUS & TOOLTIP STYLE -----*/
#contentAreaContextMenu menuitem[disabled="true"], 
#contentAreaContextMenu menuitem[disabled="true"] menuseparator {
  display: none !important}

popup {
  -moz-appearance: -moz-win-glass !important;
  background: rgba(0,0,0,.8) !important;
  border: 1px solid !important;
  -moz-border-left-colors: #555 !important;
  -moz-border-right-colors: #555 !important;
  -moz-border-top-colors: #555 !important;
  -moz-border-bottom-colors: #555 !important;
  -moz-border-radius: 8px !important;
  -moz-box-shadow: inset 0 0 10px !important;
  padding: 2px 0 !important}
popup * {
  color: #CCC !important;
  background: none !important}
popup menuitem:hover * {
  color: #F0F0F0 !important;
  text-shadow: 0 0 2px #FFF !important;}
popup menuitem:last-child:hover * {
  color: #FFF !important;
  text-shadow: none !important;}
popup menuseparator {
  -moz-appearance: none !important;
  margin: 2px 0 2px -3px !important;
  padding: 0 !important;
  border-top: none !important;
  border-color: transparent !important;
  -moz-box-shadow: 0 0 3px #666 !important}
popup menuitem {
  -moz-appearance: -moz-win-glass !important;
  font-size: 13px !important;
  font-weight: bold !important}

tooltip {
  -moz-appearance: none !important;
  color: #CCC !important;
  background-color: rgba(0,0,0,.8) !important;
  border: 1px solid #555 !important;
  -moz-border-radius: 3px !important}
