css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("bt.se") {

body{
width: 810px !important;
margin: 0 auto !important;
font-family: sans-serif !important;
background: white !important;
}

#head{
width: 790px !important;
}

.topsection, .topsection-search{
background: none !important;
}

.head-top{
border: none !important;
margin-top: 6px !important;
}

div.menuMainSub{
background: #E7E7E7 !important;
border-bottom: none !important;
}

a.menuMainSub{
padding: 0 0.46em !important;
}

.menuMain{
background: silver !important;
}

.menuMain_ON{
background: #E7E7E7 !important;
}

a.menuMain{
padding: 5px 8px 0 !important;
}

#index-page, #section-page, #article-page, #forum-page{
margin-top: 20px !important;
}

#article-column p{
font-size: 100% !important;
}

#rightcol .h1ColorBar, #bottomsection-rightcol .h1ColorBar, #bottomsection-leftcol .h1ColorBar, #bottomsection-middlecol .h1ColorBar{
padding: 4px 2px 2px 5px !important;
}

#leftcol h1.h1ColorBar, #rightcol h1.h1ColorBar, #bottomsection-rightcol .h1ColorBar, #bottomsection-leftcol .h1ColorBar, #bottomsection-middlecol .h1ColorBar{
background: #E7E7E7 !important;
color: black !important;
}

h1, h2, h3, .special-headlineh3, .special-headlineh3-withvignette, a.menuMain, a.menuMainSub, #rightcol p, #bottomsection-rightcol p, ul.relatedmedia li a, .teaser-links li a, .article-toolbar li, #leftcol p, #leftcol-search p, #leftcol-search-classifieds p{
font-family: sans-serif !important;
}

.head-contact, .head-top .floatRight, .ad-head, #ad-column, ad-container, #leftcol .ad-container, #rightcol .ad-container, ul.social-toolbar, .article-twingly, ul.article-toolbar{
display: none !important;
}
}
