/*
skin	: heise nerdified (dark theme)
author	: banabeal
updated	: 20th February 2008

Additional notes: Thanks go out to thespook whose "dA5pook" skin for www.deviantart.com insipred me to make this skin right here.
*/

@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document url("http://www.heise.de/"), url-prefix("http://www.heise.de/newsticker/meldung") {

/* basic resize + recolor */
body {
width:80%;margin:0 auto !important; background-color:#222222 !important;
}
td[bgcolor~="#eeeeee"], td[bgcolor~="#999999"] {
background-color:#222 !important;
}

/*  link defaults */
a:link {color: #FFFFFF !important; background: #333 !important;}
a:visited {color: #656565!important; background: #333 !important;}
a:hover {color: #fcd116 !important;}

/* define text in general */
div, p { color:#656565 !important;}

/* topMenu tweaks + new header image*/
.druck td a:link {
background: #222 !important;
}
.druck td a:visited {
color: #656565!important;
background: #222 !important;
}
.druck td a:hover {
color: #fcd116 !important;
}
.druck td  b {
color: #FFFFFF !important;
background: #222 !important;
}
img[src~="/icons/ho/news_logo.gif"] {
width: 0 !important;
height: 30px !important;
padding-left: 126px !important;
background-image: url(data:image/png;base64,);
background-position: center center;
background-repeat: no-repeat;
}

/* news items */
.full_content {
background: #333 !important; padding:15px !important;
}

.full_content .anriss a:link {
color: #ff8e3e !important; background: #333 !important;
}
.full_content .anriss a:hover {
color: #fcd116 !important;
}
.anriss span {
color: #fcd116 !important;
}

/* news item detail */
H2 {
color: #ff8e3e !important;
}
.full_content TD, .meldung_wrapper ul {
color: #FFFFFF !important;
}
img[src="/icons/ho/heise_min.gif"] {
width: 0 !important;
height: 30px !important;
padding-left: 30px !important;
background-image: url(data:image/png;base64,);
background-position: center center;
background-repeat: no-repeat;
}

/* bottom  menu*/
#navi_bottom {
background: #333 !important;
color: #FFFFFF !important;
}

/* remove unnecessary stuff */
.leaderboard, 
.sales, 
.adbottom_itmarkt,
.heiseadvert, 
.skyscraper, 
.adbottom, 
.adbottom_jobs, 
.bottom, 
.kasten_markt,
.contentbanner, 
.full_content .druck TABLE,
.full_content .druck SPAN,
.meldung_wrapper table script,
.meldung_wrapper table object,
.meldung_wrapper DIV,
table[width="150"], 
table[width="200"], 
td.druck, 
tr.druck, 
td[width="137"], 
a[href^="http://matrix.mediavantage.de/"],
a[href^="http://oas.wwwheise.de/"] {
display:none !important;
}
}
