/*Colors*/
#storyPageContainer {
background-color: rgb(20,24,24)!important;
}

.voteStatisticsPercentage, .voteStatisticsPercentageBig {
color:white!important;
background-color:black!important;
}

#SubNav,.SubNavLink {
color: white!important;
}

#ctl00_Header1_Search1_SearchText {
background-color: black!important;
color:white!important;
}

#InterrupterMain {
background-color: black!important;
}

.gridBackground,#storyGrid,#SubNav,#APopularSubNavComments,
#APopularSubNavLikes {
background-color: rgb(20,24,24)!important;
color: white !important;
}

#GridMoreBarBack,#GridMoreBar {
background-color: rgb(20,24,24)!important;
color:black!important;
}

.storyTopMain {
background-color: black!important;
color: white;
}

voteDiv.clear.clearfix {
color:white !important;
}

#bordersDiv {
background-color: black !important; 
}

h1,#TodaysMostPopular {
color: #FFFFFF!important;
}

#CommentsShort {
background-color: black !important;
color: #000000!important;
}

#HeadlineDeckByline {
color: #FFFFFF !important;
}

.mediaCaption {
color: #000000!important;
}

.MainCol {
color: #000000 !important;
}

.bounds {
color: #FFFFFF !important;
background-color: rgb(20,24,24) !important;
}

.clearfix {
color: #000000 !important;
}

#bordersDiv {
background-image: none!important;
}

/*Hiding*/
#UserErrorReport,#PartnerLinks,#FooterPartnerData,#footer,
.regBar.bb,#headerAdSection,.SideCol,#ToolsShareStory,
#grid_728x90_grid1,#ctl00_ImageHouseAd,#apheadlines,#grid_728x90_grid0,
#ctl00_ContentPlaceHolder1_ctl00_StoryContainer1_ImageHouseAd,
#grid_728x90_grid2,#InterrupterTitle,#InterrupterRight,.headerPromos {
display:none!important;
}

/*Width*/
.summory.more,.threeCol {
}
#storyPageContainer .MainCol {
padding-left: 0px!important;
padding-right: 0px!important;
}

#storyBackground {
padding-left: 160px!important;
}

#storyPageContainer .storySectionCarousel {
left: -160px!important;
}
style {
border-left-width:0px !important;
border-left-color: black !important;
}

}
