css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("www.pandora.com") 

{
/* Main Player */
div[id="mainContainer"]{background:#000000!important;}
div[id="trackInfo"]{background:#D9E0EA!important;color:#000000!important;}
.detail{background:#cccccc!important;color:#000000!important;}

/* Old Site Player - for compatability until the new site is permanent */
div[class="tuner"]{background:#000000!important;color:#000000!important;}
div[id="tuner"]{width:640px!important;background:#cccccc!important;color:#000000!important;padding:15px!important;-moz-border-radius:9px!important;-webkit-border-radius:9px!important;}


/* BIO Page */
div[id="mainContentContainer"],div[class="artist_bio_inner"],div[class="similar_artists"]{background:#cccccc!important;color:#000000!important;padding:15px!important;}

div[class="discography"], div [class="profile_rCol music_feed_rCol clearfix"],
div [class="profile_rCol clearfix"]{background:#cccccc!important;color:#000000!important;}

/* footer stuff */
div[id="footer"]{display:none!important;}
#promotional_gallery, #advertisement, #ad_controls, #advertisement_bottom, #google_adwords{display:none!important;}
}
