css:
/* AGENT_SHEET */
@namespace url("http://www.mozilla.org/keymaster/gatekeeper/there.is.only.xul");
/*

          Title  |  Terminalation
    Description  |  Minimal Aero Theme for Dark Visual Styles
         Author  |  ksaad
   Date Created  |  16.09.2011
   Last Updated  |  20.09.2011
        Version  |  1.0

*/
/* Glass Options, Download & Dialog Boxes */
#downloadView { background: transparent !important; color: white !important; } #downloadView > richlistitem[selected=true] { background: RGBa(235,244,255,0.6) !important; color: black !important; } #clearListButton[disabled="true"] { display: none !important; } #searchbox { display:none !important; } .tab-throbber[progress] { list-style-image: url("chrome://browser/skin/places/searching_16.png") !important; }

/* Minimal Tabbar */
#main-window { margin-top: 0px !important; } #main-window[sizemode="normal"] #navigator-toolbox[tabsontop="true"] #TabsToolbar, #main window[sizemode="normal"] #appmenu-button-container { padding-left: 3px !important; } #TabsToolbar[tabsontop="true"] { background: none !important; margin-top: -1px !important; margin-bottom: 0 !important; height: 23px !important; } #navigator-toolbox::after { height: 0 !important; } #main-window[sizemode="maximized"] { margin-top: -2px !important; height: 23px !important; } #navigator-toolbox::after { height: 0 !important; } #main-window[sizemode="fullscreen"] { margin-top: 0 !important; } #main-window[tabsontop="true"][sizemode="normal"] #TabsToolbar { margin-right: 110px !important; } #main-window[tabsontop="true"][sizemode="normal"] #toolbar-menubar { margin-left: 4px !important; margin-top: -20px !important; } #main-window[tabsontop="true"][sizemode="normal"] #appmenu-button-container { position: relative !important; z-index: 2 !important; } .tabbrowser-tab .tab-icon-image {display:none!important;} .tab-text { text-align: center !important; } tab[selected="true"] *{ font-weight: bold !important; color: white !important; } tab *{ color: white !important; } #appmenu-button-container { position: fixed !important; display: none !important; } .tabs-newtab-button {display: none} .tabs-closebutton-box { display: none !important; }

/* Minimal Navbar */
#urlbar{ font-family: Arial !important; font-size: 10pt !important; color: white !important; } .search-go-button { display: none !important; } .searchbar-engine-image { display: none !important; } .urlbar-history-dropmarker { display: none !important; } #urlbar-icons {visibility: hidden !important;} #back-button[disabled="true"] { display: none; } #forward-button[disabled="true"] { display: none; } #reload-button[disabled="true"] { display: none; } #feed-button[disabled="true"] { display: none; } toolbar { height: 24px !important; } #urlbar > toolbarbutton { border-left: none !important; border-radius: 0 !important; padding-left: 2px !important; } #urlbar-go-button, #urlbar-go-button:hover, #urlbar-reload-button, #urlbar-reload-button:hover, #urlbar-stop-button, #urlbar-stop-button:hover { background-image: none !important; box-shadow: none !important; }

/* Minor Tweaks */
.toolbarbutton-text { color: white !important; padding: 5px !important; } toolbarbutton.bookmark-item > .toolbarbutton-icon { display: none !important; } a:active { outline: none; } a:focus { -moz-outline-style: none; }

/* Transparency and naked UI */
#nav-bar { background-image: -moz-linear-gradient(rgba(255,255,255,.5), rgba(255,255,255,.1)) !important; background-color: transparent !important; border: none !important; margin-bottom: 0 !important; padding: 0 !important; } #urlbar { background: none !important; border: none !important; border-radius: 3.5px 0 0 0 !important; margin: 0 !important; box-shadow: none !important; } #searchbar > *, #search-box { background: none !important; border: none !important; border-radius: 3.5px 0 0 0 !important; margin: 0 !important; padding-left: 4px !important; box-shadow: none !important; height: 24px !important; } #PersonalToolbar { background-color: rgba(255,255,255,.1) !important; border: none !important; margin-top: 0 !important; margin-bottom: 0 !important; padding: 0 !important; } .tabbrowser-tab:not([selected="true"]) { background-image: -moz-linear-gradient(rgba(0,0,0,.1), rgba(0,0,0,.4)) !important; color: #dddddd !important; text-shadow: none !important; } .tabbrowser-tab:not([selected="true"]):hover { background-image: -moz-linear-gradient(rgba(0,0,0,.2), rgba(0,0,0,.5)) !important; } .tabbrowser-tab{ margin-top: -1px !important;} .tabbrowser-tab[selected="true"] { background-image: -moz-linear-gradient(rgba(255,255,255,.1), rgba(255,255,255,.5)) !important; text-shadow: none !important; } .tabbrowser-tab[pinned] { height: 23px !important; } .tabs-newtab-button { background: none !important; border: none !important; } #alltabs-button,#tabview-button{ margin-right: -10px !important; } .tabs-newtab-button:hover, #new-tab-button:hover, .scrollbutton-down:hover, .scrollbutton-up:hover, #alltabs-button, #tabview-button:hover, #TabsToolbar #feed-button { -moz-appearance: none !important; background: none !important; box-shadow: none !important; border: none !important; } .toolbarbutton-1 > .toolbarbutton-menubutton-button, .toolbarbutton-1 > .toolbarbutton-menubutton-dropmarker, .toolbarbutton-1 { background: none !important; box-shadow: none !important; border-color: transparent !important; } #identity-box:not(.verifiedIdentity):not(.verifiedDomain) { background: none !important; box-shadow: none !important; border-color: transparent !important; }

/* Addon Bar */
#browser-bottombox { border: none !important; background: transparent !important; } #addon-bar { border: none !important; background-color: transparent !important; background-image: -moz-linear-gradient(rgba(255,255,255,.1), rgba(255,255,255,.5)) !important; } #downbarHolder,#FindToolbar{ border: none !important; background-color: transparent !important; background-image: -moz-linear-gradient(rgba(255,255,255,.1), rgba(255,255,255,.1)) !important; }

/* Menu Button Fixes */ 
#appmenu-button { border: 0.5px solid rgba(90,30,0,0.8)!important; background: rgba(255,255,255,.1) !important; border-top:none!important; padding: 2px 5px 1px 5px !important; height: 23px !important; min-width: 43px !important; width: 43px !important; -moz-box-shadow: 0 0 3px rgba(255,255,255,.5) inset, 0 0 1px rgba(255,250,200,0.8) inset, 0 0 1px rgba(255,250,200,0.8) inset, 0 0 1px rgba(255,250,200,0.8) inset, 0 0 1px rgba(255,250,240,0.5) !important; } #appmenu-button:hover, #appmenu-button[open] { background-image: -moz-linear-gradient(rgba(2,1,10,.2), rgba(2,1,10,.2) 100%, transparent 100%), -moz-radial-gradient(14px, circle cover, rgba(255,255,255,.1) 100%, rgba(255,255,255,.1) 100%, rgba(255,255,255,.1) 100%) !important; } #appmenu-button .button-text { color: transparent !important; text-shadow: none !important; margin-left: -27px !important; margin-right: -33px !important; } #appmenu-button dropmarker { margin: 0 1px 0 0 !important; } #appmenu-button-container { position: fixed !important; top: 0 !important; } #main-window[sizemode="maximized"][tabsontop="true"] #appmenu-button-container { position: fixed !important; margin: 6px 0px 4px 2px !important; }

/* Sidebar */
.sidebar-placesTreechildren { color: white !important;} /* <-Siderbar text color */ #bookmarksPanel,#history-panel, .sidebar-placesTree,#browser-border-start,#browser-border-end { background-color: transparent !important; -moz-appearance: -moz-win-glass !important; border: none !important; margin-left: -1px !important; } #xfirexo,#vertical-toolbox,#sidebar { background-image: -moz-linear-gradient(rgba(255,255,255,.1), rgba(255,255,255,.1)) !important; background-color: transparent !important; -moz-appearance: -moz-win-glass !important; border: none !important; } #browser *{ -moz-appearance: none !important; } #sidebar-splitter { background-color: transparent !important; border: none !important; } sidebarheader { display: none !important; }

/* Sidebar Search */
#bookmarksPanel, #history-panel { -moz-box-direction: reverse; } label[value='Search:'] { margin-left: 1px !important; } #bookmarksPanel { margin: 0px 0px 0px -15px !important; } #search-box { background: transparent !important; -moz-appearance: -moz-win-glass !important; color: white !important; margin: 0px 0px 0px 11px !important; } #viewButton { margin-left: -4px !important; margin-right: 0px !important; } #bookmarksPanel label[control="search-box"], #history-panel label[control="search-box"], #viewButton > hbox > hbox > label { display: none !important; }

/* iMacro Sidebar */
#im-tabs,.tabs-right,#Recorder,#treeview,#boxlistbox *,#replaystat,#im-tabpanels,#im-play-button decoration,#im-record-button-decoration { background-color: transparent !important; color: white /*<--custom color*/!important; text-shadow: 0px 0px 6px white, 0px 0px 6px white, 0px 0px 6px white !important; -moz-appearance: -moz-win-glass !important; }

/* About:Blank:Aero - opacity: 0.1 = aero*/
#content, #appcontent, #content > tabbox > tabpanels { background-color: transparent !important; } #main-window[inFullscreen="true"]{ -moz-appearance: -moz-win-borderless-glass !important; background: none !important; } #main-window[stylish-url="about:blank"]:not([onclose^="PrintUtils"]):not([title^="View Source Chart"]) #content browser:not([src]){ opacity: 0.1 !important; }

/* Scrollbars */
scrollbar, scrollbar thumb { -moz-appearance: none !important; border: none !important; } scrollbar, scrollcorner { background: transparent !important; } scrollbar thumb { background-image: -moz-linear-gradient(rgba(255,255,255,.2), rgba(255,255,255,.3)) !important; background-color: black !important; } scrollbar[orient="vertical"] { min-width: 8px !important; } scrollbar[orient="horizontal"] { min-height: 8px !important; } scrollbar scrollbarbutton { visibility: collapse !important; }
