css:
@namespace url(http://www.w3.org/1999/xhtml);
@-moz-document domain("userscripts.org") {

#top, #root {
	background: #000 !important;
}

#section, #footer, .stats {
	background: #222 !important;
	border-color: #222 !important
}

#mainmenu > .active {
	background: #222 !important;
}

#mainmenu > .active > a {
	color: tomato !important
}

.pagination {
	text-align: center !important;
}

.pagination a {
	background: none !important;
	border-color: transparent !important;
	color: steelblue !important
}

.pagination .current {
	background: none !important;
	border-color: transparent  !important;
	font-weight: bold !important;
	color: tomato !important
}

.pagination a:visited:not([class]), .entry-title:visited, .group a:visited, .help {
	color: #555 !important;
}

p.desc, H1, .inv, H2, .c2, .stats, td.body.entry-content, #content p, .summary, #content > ul, #details {
	color: #aaa !important;
}

#right {
	display: none !important;
}

#content {
	width: 100% !important;
}

#content > .wide {
	border: 1px solid #444 !important
}

.script-meat, #header, .c2, .vat.c1, .wide > tbody > tr > td:not(.inv) {
	border: 0 !important;
}

.inv, .ca.inv {
	background: transparent !important;
	border-color: #444 !important;
	border-bottom-color: transparent !important
}

.wide > tbody > tr:last-child .inv, .wide > tbody > tr:last-child .ra.c2 {
	border-bottom-color: #444 !important
}

.wide > tbody > tr:nth-child(2n) {
	background: #222 !important
}

.wide > tbody > tr:nth-child(2n+1) {
	background: #111 !important
}

.wide > tbody > tr > th, #content > TABLE > TBODY > TR >th {
	border-color: #444 !important;
}

.la > a[href^="/scripts"] {
	color: tomato !important;
	text-decoration: none !important
}

td.author.vcard {
	background: #121212 !important
}

td.author.vcard a, #content > P:first-child > A {
	color: steelblue !important
}

blockquote, .notice {
	background: #333 !important;
	border: 1px solid black !important;
	color: #aaa !important
}

pre, code {
	background: #000 !important;
	color: #aaa !important;
	border: 1px solid #444 !important;
}

.title:visited, li.menu a:visited {
	color: #555 !important
}

#script-nav .menu.current {
	background: #000 !important;
	color: tomato !important;
	border-color: #000 !important;
	font-weight: bold !important;
}

#script-nav li {
	background: #333 !important;
}

#script-nav li a:visited, h2.title a, td.inv.lp > a {
	color: steelblue !important
}

#review-list {
	padding: 10px !important;
}

.review {
	color: #aaa !important;
	border: 0 !important;
	padding: 10px !important
}

.review:nth-child(2n+1), li.menu a {
	background: #111 !important;
}

.review:nth-child(2n), li.menu.current, #screenshots, #uso {
	background: #222 !important;
}

.clear {
	border-color: #222 !important
}

.body.entry-content, #uso div {
	background: #111 !important
}

#content > TABLE > TBODY > TR > TD, h3 {
	border-color: #444 !important;
	color: #aaa !important
}

textarea {
	-moz-appearance: none !important;
	background: #000 !important;
	border-color: #444 !important;
	color: #aaa !important;
}

h4, #full_description, li.menu > a > span, th.la>a, .container, #main label {
	color: #aaa !important;
}

#full_description > TABLE > TBODY > TR > TD {
	border-color: #444 !important;
}

.postactions {
	border: 0 !important
}

.postactions a {
	color: steelblue !important
}

#options .menu.current {
	border-color: #444 !important;
	color: steelblue !important;
}

#options, .subnav > .menu, #screenshots {
	border-color: #444 !important
}

input[type="password"], input[value="Update"], select[id^="user_"], input[id^="user_"], #monitor_submit, .input, #issues_submit {
	-moz-appearance: none !important;
	color: #aaa !important;
	background-color: rgba(0,0,0,.5) !important;
	box-shadow: 0 0 4px rgba(161,161,161,.9) inset !important;
	border-radius: 4px !important;
	border: 1px solid transparent !important;
}
}
