css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("neon.de") {

/* Style for neon.de , a German community. */
/* Last updated: 09-Feb-2009 */

/* Hintergrund */
body {
 background:none !important;
 background-color:#f5f4f2 !important;
}
#wrapper { background:none !important; }
#footer { background:none !important; }

/* ### Links ### */
a { color:#000; text-decoration:none; }
a:hover { color:#1791E6 !important; text-decoration:underline; }

/* Links zu Usern */
a.user, span.user, a[style="color: red;"], a[style="color: red; text-decoration: underline;"] { color:#1791E6 !important; font-weight:bold;}

/* Userpage-Texte oder Links z.B. bei "Wo ist das denn" */
.rot, a.rot, #main .rot strong { color:#1791E6 !important; }

/* Aktuelle Seite, z.B. im Gaestebuch */
.pager a.current { color:#1791E6 !important; }

/* Neon taeglich */
#sidebar div.teaser table.Standard a>span { color:#1791E6 !important; }
#sidebar div.teaser a.user>span { color:#1791E6 !important; }

/* Neon-Texte */
/* #main .admin_advice { color:#1791E6; } */
/* #main .magazin_advice { color:#1791E6; } */



/* Schatten abschalten */
body>div#wrapper>div>div>img { display:none; }
#logo-shadow,
#logo-shadow-edge,
#pagehead-shadow,
#pagehead-shadow-edge,
#pageheadrandomuser-shadow,
#pageheadrandomuser-shadow-edge,
#page-shadow,
#page-shadow-edge,
#page-shadow-bottom { display:none; }

/* Werbe-Anzeige ganz oben ausblenden*/
#leaderboard { display:none; }

/* Header anpassen */
#header { border:none !important; margin-left:1px !important; }
#logo {
 border:none !important;
 margin-bottom:3px;
 background-color:transparent !important;
}
#logo ul { background-color:#aaaaff !important; }
#logo li a { color:#000 !important; }

/* Seitenheader links vom Random User */
#pagehead { border:none !important; background:none !important;}


/* Menue (Artikel, Bilder, Musik, Webticker, Reise, Forum) */
#blogreiter {}

#blognav li img,
#blognav li.active img { display:none !important; }

ul#blognav li {
 background-color:#fff;
 border: 1px solid rgb(102, 102, 102);
}
#blognav li.active { background-color:#aaaaff !important; }

/* Pop-Up-Menue Artikel */
#subnavi_artikel {}
#subnavi_artikel ul li { border:none !important; }

/* Auch gerade online (Pop-Up und Bild) */
#random_user_online {}
#random_user_userpage {
 background:none !important;
}
#random_user_userpage img {
 border-width:1px !important;
}

/* 3dbox */
#homestage div { border:none !important; }
#homestage>div>div>img { display:none; }
#homestage>div>div>div>div>div {
 width:848px !important;
 border: 1px solid rgb(102, 102, 102) !important;
}

/* ### Hauptteil ### */

/* Artikel Kategorie-Bild */
.teaserImg { display:inline !important; height:80px !important; }
/* (nur verkleinert... wenn man es ganz weglaesst, dann
    funktioniert das Artikel-Popupmenue nicht mehr, weil
    es vom nachfolgenden Text ueberdeckt wird) */

/* Werbe-Anzeige */
#ad_content { display:none; }

/* Herumfliegende Werbung */
#ad_sky { display:none; }


/* Kommentare */
/*
.posting_top { background:none !important;}
.posting_middle { background:none !important; }
.posting_bottom { background:none !important; }
*/

/* ### Sidebar ### */
#sidebar {
border-left:1px dashed rgb(102, 102, 102);
padding-left:5px;
border-bottom:1px dashed rgb(102, 102, 102);
padding-bottom:5px;
}

/* kleine Texte */
#sidebar .subline {}

/* Das Magazin (Sidebar) */
#sidebar .teaser>a>img,
#sidebar .teaser ul { display:none; }

/* Werbung */
#halfpromo { display:none; }
#promoTmp { display:none; }
#sidebar .textlink { display:none; }

/* Wegweiser */
#wegweiser_artikel {}
#wegweiser_artikel .option {}
#wegweiser_artikel .abstand {}

/* Empfehlungen */
#sidebar .teaser>p>a {}

/* Texte zu diesem Thema */
#sidebar>.subline>p {}

/* Empfehlungen die zweite */
#sidebar .subline .teaser { display:none; }

/* Texte die zweite */
#sidebar .subline .subline>p { display:none; }

/* Unnuetzes Wissen */
#sidebar .erkenntnisse {}



/* Neu dabei und Gerade angemeldet */
#footer_logins { display:none; }
#updatemain { display:none; }
.dojoHtmlFisheyeListBar { display:none !important; }


/* Andere Webseiten der G+J AG & Co KG */
#footer_sites { display:none !important; }



}
